/* ==UserStyle==
@name        The Colorado Sun dark theme
@namespace   https://github.com/obscuredetour/
@version     1.3.2
@license     CC-BY-4.0
@author      obscure detour (https://www.jeffreysummers.me)
@homepageURL https://github.com/obscuredetour/dark-co-sun
@updateURL   https://github.com/obscuredetour/dark-co-sun/raw/master/co-sun-dark.user.css
==/UserStyle== */

@-moz-document domain("coloradosun.com") {
  :root {
    --raven: #202326;
    --night-sky: #232629;
    --ash: #303438;
    --dusk: #444;
    --dusky-fog: #42484e;
    --charcoal: #555;
    --soot: #aeaeae;
    --rimrock: #bcb8b6;
    --smoke: #c4c2c0;
    --stone: #c9c9c9;

    --cloud-front: rgba(255,255,255,.85);
    --cloud-clear: #f5f4f5;

    --sun-rays: #fcd232;
    --flower-blue: #2b56ff;
    --stem-green: #30e8bd;

    /* backgrounds */
    --bkg-main: var(--raven);
    --bkg-button: var(--sun-rays);
    --bkg-link-nav-hover: var(--dusky-fog);

    /* text */
    --txt-body: var(--cloud-clear);
    --txt-heading: var(--cloud-front);
    --txt-grey: var(--rimrock);
    --txt-link: var(--stem-green);
  }
  
  html,
  .large-feature__content___jQPqQ,
  .article-header__wrapper___1UKb2,
  .civil-footer__wrapper___3xZwz,
  .article-header__date___3TskR::after {
    background-color: var(--bkg-main);
    color: var(--txt-body);
  }

  .search-form__input___2VR7a {
    background-color: var(--dusk);
    color: var(--rimrock);
  }

  .has-very-light-gray-background-color,
  .sub-menu a:focus,
  .sub-menu a:hover,
  .body-content__sidebar___3Bplp div[style*="background-color:#fcd232"] {
    background-color: var(--dusky-fog) !important;
  }

  .content-item__eyebrow___25bqF a,
  .river__title___krYaD a,
  .rich-text ol,
  .rich-text p,
  .rich-text ul,
  .rich-text h1,
  .rich-text h2,
  .rich-text h3,
  .rich-text h4,
  .rich-text h5,
  .rich-text h6,
  .article-footer__social-shares .sd-title,
  .article-bylines__expandable___1gcWr .article-bylines__bio-name___30oIS,
  .article-bylines__static___1jfy3 .article-bylines__bio-name___30oIS {
    color: var(--txt-body);
  }

  .content-item__eyebrow___25bqF span,
  .newsroom-header__subscribe-wrapper .subscribe-button__button,
  p.has-luminous-vivid-amber-background-color,
  p.has-cyan-bluish-gray-background-color,
  p.has-background[style*="background-color:#fcd232"],
  .newsroom-header__subscribe-wrapper button.cta-button__button,
  .newsroom-footer__subscribe-wrapper button.cta-button__button {
    color: var(--ash);
  }

  .rich-text p.has-luminous-vivid-amber-background-color a,
  .rich-text p.has-luminous-vivid-amber-background-color a:visited,
  p.has-background[style*="background-color:#fcd232"] a,
  p.has-background[style*="background-color:#fcd232"] a:visited {
    color: #000;
  }

  .rich-text p.has-luminous-vivid-amber-background-color a:hover,
  .rich-text p.has-luminous-vivid-amber-background-color a:focus,
  p.has-background[style*="background-color:#fcd232"] a:hover,
  p.has-background[style*="background-color:#fcd232"] a:focus {
    color:  var(--ash);
    border-bottom: 2px solid  var(--ash);
  }

  .rich-text .alignfull img {
    box-shadow: none;
  }

  .card__wrapper___35V-0,
  .large-feature__content___jQPqQ {
    border-bottom-color:  var(--flower-blue);
  }

  iframe[src*="//datawrapper"] {
    background-color: rgba(255, 255, 255, 0.7);
    padding-left: 0.25rem;
    padding-right: 0.25rem;
    margin-bottom: 1rem;
  }

  .river__title___krYaD {
    color:  var(--txt-body);
    line-height: 1.2;
  }

  .page-header__title,
  .article-header__title,
  .large-feature__title___1lPYS a,
  .article-header__wrapper___1UKb2 {
    color: var(--txt-heading);
  }

  .credibility-indicators__heading___1JRFb,
  .credibility-indicators__heading___1JRFb,
  button [data-_extension-text-contrast="fg"],
  [data-_extension-text-contrast="default"]:root {
    color: var(--txt-heading) !important;
  }

  .featured-articles__list-headline___scXlr,
  .credibility-indicators__intro___1IYKw {
    border-bottom-color: var(--txt-body);
  }

  .newsroom-header__runs-on-civil svg,
  .newsroom-header__menu-trigger path,
  svg:not(:root),
  .credibility-indicators__icon svg path,
  .credibility-indicators__icon svg g,
  .credibility-indicators__icon svg g path {
    fill: var(--txt-body);
  }

  .credibility-indicators__icon svg g {
    stroke: var(--txt-body);
  }

  .article-taxonomies__link___3_SRi {
    background-color: var(--charcoal);
    color: var(--txt-body);
  }

  .article-taxonomies__link___3_SRi:hover {
    background-color: var(--dusk);
  }

  .card__dek___2LnA5 {
    color: var(--soot);
  }

  .content-item__date___2Adqk {
    color: var(--stone);
  }

  .article-bylines__byline-expand___1Cti0[aria-expanded]::after {
    color: var(--cloud-clear);
  }

  .card__avatar___1YAw7,
  .article-bylines__expandable___1gcWr .article-bylines__bio-content___18yWF,
  .article-bylines__static___1jfy3 .article-bylines__bio-content___18yWF,
  .river__byline___1YWD4,
  .rich-text .wp-block-image figcaption {
    color: var(--rimrock);
  }

  .rich-text p a {
    color: var(--stem-green);
    border-bottom: 2px solid var(--flower-blue);
  }

  .rich-text p a:hover,
  .rich-text p a:focus {
    color: var(--smoke);
  }

  .article-bylines__twitter-handle___26sL7,
  .article-bylines__bio-more___3XaZl {
    color: var(--stem-green);
  }

  .article-bylines__twitter-handle___26sL7,
  .article-bylines__bio-more___3XaZl,
  .credibility-indicators__learn-more___3BCYI,
  .article-bylines__bio-content a,
  .sharing-anchor.sd-button.share-more {
    color: var(--sun-rays);
  }

  .button-secondary-light {
    color: var(--sun-rays);
    border-color: var(--sun-rays);
  }

  .button-secondary-light:hover,
  .button-secondary-light:focus {
    color: var(--night-sky);
    background-color: var(--sun-rays);
  }

  @media screen and (max-width: 1024px) {

    .newsroom-header__nav___2hRfj a:focus,
    .newsroom-header__nav___2hRfj a:hover {
      background-color: var(--smoke);
    }
  }
}
