style.css:
@-moz-document domain("thingiverse.com") {

/* Fixed header */
div.site-header {
    position: fixed !important;
    z-index: 99 !important;
}

#search-page-form, #thing-page, #main {
    margin-top: 72px !important;
}

.modal-content-wrapper {
    max-width: 90%;
}

/* Show full filename */
.truncate {
    white-space: normal;
    text-overflow: unset;
    overflow: visible;
}

/* Improve gallery fullview */
/* Remove commenting once supported: https://caniuse.com/#feat=css3-attr
.gallery-photo .thing-img {
    background-image: attr(data-img);
} 
also remove the css below. */

.modal-fullsize .gallery-photo img {
    width: 100%;
}
}
