style.css:
*[data-column-index="0"] { background-color: #FFFFFF; }
  *[data-column-index="1"] { background-color: #FFFFFF; }
  *[data-column-index="2"] { background-color: #FFFFFF; }
  *[data-column-index="3"] { background-color: #FFFFFF; }
  *[data-column-index="4"] { background-color: #FFFFFF; }
  *[data-column-index="5"] { background-color: #EEFFFF; }
  *[data-column-index="6"] { background-color: #EEFFFF; }
  *[role="grid"] *[role="columnheader"]:nth-child(1) { background-color: #FFFFFF; }
  *[role="grid"] *[role="columnheader"]:nth-child(2) { background-color: #FFFFFF; }
  *[role="grid"] *[role="columnheader"]:nth-child(3) { background-color: #FFFFFF; }
  *[role="grid"] *[role="columnheader"]:nth-child(4) { background-color: #FFFFFF; }
  *[role="grid"] *[role="columnheader"]:nth-child(5) { background-color: #FFFFFF; }
  *[role="grid"] *[role="columnheader"]:nth-child(6) { background-color: #EEFFFF; }
  *[role="grid"] *[role="columnheader"]:nth-child(7) { background-color: #EEFFFF; }
  *[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(1) { background-color: #FFFFFF; }
  *[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(2) { background-color: #FFFFFF; }
  *[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(3) { background-color: #FFFFFF; }
  *[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(4) { background-color: #FFFFFF; }
  *[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(5) { background-color: #FFFFFF; }
  *[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(6) { background-color: #EEFFFF; }
  *[role="grid"] *[role="rowgroup"] > *[role="row"] > *:nth-child(7) { background-color: #EEFFFF; }
  *[role="grid"] *[role="presentation"] > *[role="row"] > *:nth-child(1) > *:nth-child(1) { background-color: #FFFFFF; }
  *[role="grid"] *[role="presentation"] > *[role="row"] > *:nth-child(1) > *:nth-child(2) { background-color: #FFFFFF; }
  *[role="grid"] *[role="presentation"] > *[role="row"] > *:nth-child(1) > *:nth-child(3) { background-color: #FFFFFF; }
  *[role="grid"] *[role="presentation"] > *[role="row"] > *:nth-child(1) > *:nth-child(4) { background-color: #FFFFFF; }
  *[role="grid"] *[role="presentation"] > *[role="row"] > *:nth-child(1) > *:nth-child(5) { background-color: #FFFFFF; }
  *[role="grid"] *[role="presentation"] > *[role="row"] > *:nth-child(1) > *:nth-child(6) { background-color: #EEFFFF; }
  *[role="grid"] *[role="presentation"] > *[role="row"] > *:nth-child(1) > *:nth-child(7) { background-color: #EEFFFF; }
  *[role="grid"] *[role="presentation"] > *[role="row"] > *:nth-child(2) > *:nth-child(6) { background-color: #EEFFFF; }
  *[role="grid"] *[role="presentation"] > *[role="row"] > *:nth-child(2) > *:nth-child(7) { background-color: #EEFFFF; }
