/* ==UserStyle==
@name           Readable Font - More Pretty
@namespace      github.com/openstyles/stylus
@version        1.0.1
@description    This improves font readability and makes it beautiful!!
@author         black7375
@homepage      https://userstyles.org/styles/164757
 License        CC BY-SA - Creative Commons Attribution-ShareAlike
==/UserStyle== */

@-moz-document url-prefix(http://),
url-prefix(https://)
{

  body, input, .navigator-toolbox, .toolbarbutton-text, .sidebar-title
  {
    -webkit-text-stroke-width:0.02px !important;
    -webkit-font-smoothing: antialiased !important;
     text-shadow: 0px 0px 0.001px #ACACAC !important;
  }
}
