style.css:
@-moz-document domain("wiidatabase.de") {
#content {
    background-image: none;
    background-repeat: repeat;
    background-color: #2d2d2d;
    padding: 20px;
    color: white;
}
.entry-title a, .site-header .home-link {
    color: white;
}

.download_wrapper {
	background-color: #2d2d2d;
}

.format-link, .format-aside, .paging-navigation, .post-navigation {
    background-color: #2d2d2d;
}

.entry-meta, .entry-meta a, .entry-content a, .entry-summary a {
    color: #4b9dbc;
}
.site-footer, .widget {
    background-color: rgba(0,0,0,0.3);
}
body {
    color: white;
    background-color: #2d2d2d;
}
.download_title {
    background-color: #2d2d2d;
}
pre {
    background-color: rgba(0,0,0,0.3);
}
div.spoiler-head {
    color: white;
}
div.spoiler-wrap {
    background-color: #2d2d2d;
}
div.spoiler-body {
    background: #2d2d2d;
}
.widget a, .site-footer .widget a {
    color: #4b9dbc;
}
}
