style.css:
@-moz-document domain("superuser.com"), domain("stackoverflow.com"), domain("stackexchange.com"), domain("askubuntu.com"), domain("mathoverflow.net"), domain("serverfault.com"), domain("stackapps.com") {
/* don't let useless top bar always taking up your reading space. */
.top-bar {
    position: absolute !important;
}


/* hide unnecessary element while reading */
#left-sidebar,
#sidebar {
    opacity: 0 !important;
    transition: 1500ms !important;
}
#left-sidebar:hover,
#sidebar:hover {
    opacity: 1 !important;
    transition: 200ms !important;
}


/* not related to your work */
.module.community-bulletin,

/* never mind what's up on Meta */
#hot-network-questions,

/* no wasting your time in a semi-related chatroom */
#chat-feature,

/* seriously, stop lookin gfor ways to waste your time */
#footer-sites {
    display: none;
}


/* Ads to join the site. */
#herobox,
.s-hero {
    display: none;
}


/* Ads for products. */
.ad,
.ad-container {
    display: none;
}
}
