style.css:
@-moz-document url("https://www.rocketbeans.tv/superchad/"), url-prefix("https://rocketbeans.tv/superchad/") {
:root {
--b0: /*[[b0]]*/;
--b1: /*[[b1]]*/;
--b2: /*[[b2]]*/;
--b3: /*[[b3]]*/;
--o1: /*[[o1]]*/;
}
.chat-container .chatSelector h1,.chat-container li .message .inlineMessage{color:var(--o1)}.chat-container .chatSelector{background-color:var(--b3)}.chat-container li:nth-child(odd){background-color:var(--b1)}.chat-container{background-color:var(--b2)}.chat-container ::-webkit-scrollbar-thumb{background-color:var(--o1);border:2px solid var(--o1)}.chat-container ::-webkit-scrollbar-track{background-color:var(--b2)}.chat-container li:hover{background-color:var(--o1)!important}.chat-container li:hover .message .username[data-username]{color:var(--b0)}.chat-container li:hover .message .inlineMessage{color:var(--b1)}.btn:focus,.btn:hover{background-color:var(--o1);color:var(--b0)}.btn{background-color:var(--b3)}
}
