/* ==UserStyle==
@name           infoc.eet.bme.hu - 10/12/2018, 1:02:09 AM
@namespace      github.com/openstyles/stylus
@version        0.0.7
@description    Infoc dark theme
@author         AndrisBorbas
==/UserStyle== */
@-moz-document domain("infoc.eet.bme.hu") {
    body {                              /*háttér*/
        background-color: #222;
        color: #FFF;
    }
    .slide {                            /*általános szöveg*/
        color: #edb21b;
    }
    h1 {                                /*header szöveg*/
        color: #bb3d4e;
    }
    h2 {                                /*header szöveg*/
        color: #bb3d4e;
    }
    a {                                 /*linkek*/
        color: #be731d;
    }
    button {                            /*gombok*/
        color: #EEE;
        background-color: #333;
        box-shadow: 1px 1px 0 #111 inset;
        border: 1px solid #111;
    }
    table {                             /*táblázat*/
        background: #333;
        border: 1px solid #777;
    }
    tbody tr:nth-child(even) {          /*minden második táblázat sor*/
        background: linear-gradient(to top, rgba(255, 255, 255, 0.10), rgba(255, 255, 255, 0.10))
    }
    #popupfillscreen {                  /*a feladatbeadós ablak*/
        background: #222;
        color: #DDD;
    }
    #popupwindow .gombok {              /*a feladatbeadós ablak alján a gombok*/
        background: #333
    }
    .tovabb h4 a {                      /*beküldött megoldás és további megoldások szöveg*/
        color: #edb21b;
    }
    .komment {                          /*tiltott szavak meg ilyesmik feladatok alján*/
        color: #888;
    }
    pre {                               /*kód és üzenet komment blokkok*/
        color: #FFE;
        background: #4a4438;
    }
    #popupfillscreen pre {              /*kód és üzenet komment blokkok*/
        color: #FFE;
        background: #4a4438;
    }
    blockquote {                        /*kód és üzenet komment blokkok*/
        color: #FFE;
        background: #4a4438;
    }
    #popupfillscreen blockquote {       /*kód és üzenet komment blokkok*/
        color: #FFE;
        background: #4a4438;
    }
    img.kozep {                         /*képek háttere*/
        background: #4a4438;
        border: 1px solid #d1c2a6;
        border-radius: 3px;
        padding: 5px;
    }
    .kozep {                            /*képek háttere*/
        background: #4a4438;
        border: 1px solid #d1c2a6;
        border-radius: 3px;
        padding: 5px;
    }
    em {                                /*kiemelt szöveg*/
        color: #be731d;
        font-weight: bold;
    }
    form.kitolt {                       /*kérdőív*/
        color: #edb21b;
        background: #4a4438;
        border: 1px solid #d1c2a6;
    }
    label.radio input:checked + span {  /*kérdőív kiválasztott eleme*/
        color: #be731d;
    }
    #tartalom .fontos {                 /*tartalomjegyzék fontos elemei*/
        color: #bb3d4e; 
    }
    input, select, form textarea{       /*üzenetküldö felugró ablak*/
        color: #DDD;
        background: #4a4438;
        border: 1px solid #d1c2a6;
    }
}
