/* ==UserStyle==
@name        translit-dark
@description Dark Mode for Translit.net
@version     2020.11.19
@namespace   dym-sh
@author      dym-sh
@homepageURL https://dym.sh/translit-dark
@updateURL   https://dym.sh/translit-dark/style.user.css
@license     MIT
==/UserStyle== */

@-moz-document domain("translit.net")
{

* { background : transparent !important
  ; color : #bbb !important
  }

body
, .bcharset
  { background: #222 !important }

ul#tTabCell li:nth-of-type(2n)
  { background: #333 !important }

input, button, select, textarea
  { background: #333 !important
  ; border: 1px solid #222 !important
  }

.tTextArea120, .tWzgBlock120
  { padding-right : 0 !important }

p
, .tBottomContainer
, .tLogoContainer
, .tCookiesButtonA
, .tAdvContainer
, .tBoxAdv
, .tBannerRight120
, #placeholder
  { display: none !important }

}
