style.css:
@-moz-document regexp("http[s*]://forum.toribash.com.*") {
body
{
    background-color: #111;
    border-color: #111;
}

select,
input,
.fjdpth0,
.fjsel,
.button
{
    color: #888;
    background-color: #111 !important;
    border-color: #333;
}

.imagebutton,
.imagebutton:hover
{
    background: #333 !important;
}

.vbmenu_popup,
.vbmenu_option
{
    background: #444;
    border-color: #111;
}

.thead, 
.tcat,
.vbmenu_control,
.postTopLeft,
.postBotLeft,
.vBulletin_editor,
.panel
{
    color: #ccc;
    background: #333;
    border-color: #111 !important;
}

.panelsurround
{
    background: #222;
}

.postTopRight,
.postMidRight,
.postBotRight
{
    background-color: #222;
    border-color: #111 !important;
}

.smallfont,
font[color="black"],
div,
legend,
span .pageTitle
{
    color: #999;
}

.alt1Active, .alt1, .alt2
{
    color: #999;
    background-color: #222;
}

.tborder
{
    background-color: #111;
}

a:link,
font[color="#000"]
{
    color: #cc9 !important;
}

a:visited,
font[color="#000"]:visited
{
    color: #aa7 !important;
}

.warning
{
	color: #880;
    border-color: #666600;
	background-color: #333300;
	background-color: rgba(100, 100, 0, 0.4);
}

img[src^="https://cache.toribash.com/forum/images/ca_morpheus_gray/"],
img[src="https://i.imgur.com/gImvKiM.png"],
img[src^="https://cache.toribash.com/forum/images/editor"]
{
    filter: hue-rotate(180deg) invert(100%) brightness(80%);
}

textarea
{
    color: #888888;
	background-color: #111111;
    border-color: #333;
}

.spoiler_bbc
{
    background-color: #333;
    border-color: #111;
}

.spoiler_hr_bbc
{
    display: none !important;
}

.spoiler_header_bbc
{
    background-color: #444;
    border: none;
}

.spoiler_title_bbc
{
    color: #888;
}

.spoiler_button_bbc
{
    color: #888;
    background-color: #444;
    border-color: #222;
}

.spoiler_content_bbc
{
    background-color: #333;
}

.leftMenu
{
    background-color: #555;
}

.clan_top
{
    background: #333 !important;
    border-color: #111 !important;
}

.clan_top_below
{
    box-shadow: inset 0 -4px 0 #111 !important;
}

.clan_contents
{
    background: #333 !important;
    border-color: #111 !important;
    box-shadow: inset 0 -4px 0 #111 !important;
}

#clan_foot,
.content,
.sale_wrapper
{
    background: #222 !important;
    border-color: #111 !important;
}

.box .section,
#footer,
.header
{
    background: #333 !important;
    border-color: #111 !important;
}

td .clan_top
{
    white-space: nowrap;
}

div[id^="vB_Editor_001_size"]
{
    width: 45px !important;
}

.osize,
.osmilie,
.ofont
{
    background: #333 !important;
    border-color: #111 !important;
    color: #999 !important;
}
}
