style.css:
@-moz-document url-prefix("https://www.eldarya.fr/"), regexp("https://(fa1|fa2)\\.eldarya\\.fr/.*") {

/*____Boutons____*/
	.flavr-button {
    background-color: #DFA2EE !important;
    color: #FFF9A8 !important;
    border-color: #FFF9A8 !important;
    font-weight: bold;}
	.flavr-button:hover {
    background-color: #FFF9A8 !important;
    color: #DFA2EE !important;
    border-color: #DFA2EE !important;
    font-weight: bold;}
	.flavr-button, .button.button.button {
    background-color: #DFA2EE !important;
    color: #FFF9A8 !important;
    border-color: #FFF9A8 !important;
    font-weight: bold;}
	.flavr-button, .button.button.button:hover {
    background-color: #FFF9A8 !important;
    color: #DFA2EE !important;
    border-color: #DFA2EE !important;
    font-weight: bold;}
/*Boutons validation*/
	.flavr-button.default {
    background-color: #DFA2EE !important;
    color: #FFF9A8 !important;
    border-color: #FFF9A8 !important;
    font-weight: bold;}
	.flavr-button.default:hover {
    background-color: #FFF9A8 !important;
    color: #DFA2EE !important;
    border-color: #DFA2EE !important;
    font-weight: bold;}
	.flavr-button.gray {
    background-color: #DFA2EE !important;
    color: #FFF9A8 !important;
    border-color: #FFF9A8 !important;
    font-weight: bold;}
	.flavr-button.gray:hover {
    background-color: #FFF9A8 !important;
    color: #DFA2EE !important;
    border-color: #DFA2EE !important;
    font-weight: bold;}
/*Bouton pour fermer Pop Up Classement*/
#preview-popup-container .ranking-popup-close {background-color: #FFF9A8 !important;
    border-color: #DFA2EE !important;
    color: #DFA2EE !important;
    background: none #FFF9A8 !important;}
#preview-popup-container .ranking-popup-close:before {background-color: #FFF9A8 !important;
    border-color: #DFA2EE !important;
    color: #DFA2EE !important;
    background: none #FFF9A8 !important;}
/*Bouton pour fermer Pop Up nourrir son familier*/
.flavr-container .flavr-fixer .flavr-outer .flavr-toolbar .flavr-button.close, .flavr-container .flavr-fixer .flavr-outer .flavr-toolbar.inline .flavr-button.close {background-color: #FFF9A8 !important;
    border-color: #DFA2EE !important;
    color: #DFA2EE !important;
    background: none #FFF9A8 !important;}
.flavr-container .flavr-fixer .flavr-outer .flavr-toolbar .flavr-button.close, .flavr-container .flavr-fixer .flavr-outer .flavr-toolbar.inline .flavr-button.close:before {background-color: #FFF9A8 !important;
    border-color: #DFA2EE !important;
    color: #DFA2EE !important;
    background: none #FFF9A8 !important;}
/*Bouton pour fermer Pop Up de description de la bibliothèque*/
    .fancybox-wrap .fancybox-close {border-color: #DFA2EE !important;
    color: #DFA2EE !important;
    background: none #FFF9A8 !important;}
    .fancybox-wrap .fancybox-close:before {border-color: white !important;
    color: #DFA2EE !important;
    background: none #FFF9A8 !important;}

/*____Fond(s)____*/
    body {
    background: url("https://www.world-lolo.com/images/uploads/image.num1537637435.of.world-lolo.com.jpg") fixed center;
    background-size: cover ;
    overflow-y: scroll;}
	body.night {
    background: url("https://www.world-lolo.com/images/uploads/image.num1537638046.of.world-lolo.com.jpg") fixed bottom center;
    background-size: cover;
    overflow-y: scroll;}

/*____Fond Transparent Principal____*/
    #container .page-main-container {
    background-color: rgba(0, 156, 191, 0.5);
    border-radius: 10px;
    border: 3px ridge white;
    padding: 20px 17px;
    box-shadow: inset 1px 1px 5px 1px #dfa2ee;}

/*____logo____*/
img[src="/i18n/static/fr/logo.png"] {
	width: 100%;
	height: 100%;
	background: url("https://www.world-lolo.com/images/uploads/image.num1536855541.of.world-lolo.com.png");
    padding-left: 386px;
    padding-top: 166px;
	position: absolute;
	top: 15px;}

/*____Barres menu haut et bas____*/
/*Bas*/
    #footer {
    background: linear-gradient(0deg, rgba(0, 156, 191, 0.5) 0%, rgba(0, 156, 191, 0.3) 10%, rgba(255, 255, 255, 0) 90%);
    font-family: Banda Bold, sans-serif;
    position: absolute;
    height: 42px;
    width: 100%;
    bottom: 0;
    left: 0;}
	#footer #footer-links a {
    color: white;
    font-size: 14px;
    text-shadow: 1px 1px 1px #4c4c4c, 2px 1px 1px #00e4ff, 2px 1px 1px #00e4ff, 2px 1px 1px #00e4ff, 2px 1px 1px #00e4ff, 2px 1px 1px #00e4ff;
    font-family: Merriweather, serif;
    border-radius: 5px;
    padding: 5px 5px;
    background-color: rgba(0, 93, 114, 0.5);
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536846899.of.world-lolo.com.png");}
    #footer #footer-links a:hover {
    color: white;
    text-shadow: 1px 1px 1px #4c4c4c, 2px 1px 1px #DFA2EE, 2px 1px 1px #DFA2EE, 2px 1px 1px #DFA2EE;
    font-family: Merriweather, serif;}
	#footer #footer-in {
    color: white;
    text-shadow: 1px 1px 1px #4c4c4c, 2px 1px 1px #DFA2EE, 2px 1px 1px #DFA2EE;
    font-family: Merriweather, serif;}
/*Haut*/
    #header #header-top {
    background-color: rgba(255, 255, 255, 0);
    position: absolute;
    height: 39px;
    width: 100%;
    top: 0;}
	#header-menu a {font-family: Merriweather, serif;
    color: white;
    font-size: 15px;
    text-shadow: 1px 1px 1px #4c4c4c, 2px 1px 1px #00e4ff, 2px 1px 1px #00e4ff, 2px 1px 1px #00e4ff, 2px 1px 1px #00e4ff, 2px 1px 1px #00e4ff;
    border-radius: 5px;
    padding: 5px 10px;
    background-color: #009cbf;
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536846899.of.world-lolo.com.png");}
	#header-menu a:hover {font-family: Merriweather, serif;
    color:white;
    font-size: 15px;
    text-shadow: 1px 1px 1px #4c4c4c, 2px 1px 2px #DFA2EE, 2px 1px 2px #DFA2EE, 2px 1px 2px #DFA2EE, 2px 1px 2px #DFA2EE, 2px 1px 2px #DFA2EE;
    border-radius: 5px;
    padding: 5px 10px;}

/*____Page d'accueil déconnecté____*/
/*Page d'accueil - bouton de connexion*/
	#header-login-form input {
    color: #d077e6;
    margin: 0px 3px;}
	#header-login-form {
    background-color: #009cbf;
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536846899.of.world-lolo.com.png");
    height: 35px;
    width: 65%;
    border-radius: 20px;
    margin: 5px;
    font-weight: bold;}
/*Bordure Image Accueil*/
	#index-banner {
    height: 408px;
    border: solid 2px #FFF9A8;
    border-radius: 10px;
    text-align: center;
    position: relative;}
/*Changement de la bannière*/
	#contentPane > div {
	text-align: center !important;}
	#index-banner {
    height: 408px;
    border: solid 2px #FFF9A8;
    border-radius: 10px;
    text-align: center;
    position: relative;}
/*Découvre le monde d'Eldarya Titre*/
	#punchline h2 {
    color: #d077e6;
    text-shadow: 1px 1px 2px white, 1px 0px 2px white, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff, 0px 0px 8px #dfa2ee, 0px 0px 8px #dfa2ee, 0px 0px 12px white, 0px 0px 12px white;
    font-size: 24px !important;
    font-family: "Times New Roman" !important;
    font-weight: bold !important;
    padding-top: 20px;}
/*Texte D'intro*/
	#intro {
    font-family: "Banda regular", serif, bold;
    color: white;
    text-shadow: 1px 1px 1px rgba(223, 162, 238, 0.8), 1px 1px 1px #4c4c4c, 1px 1px 1px #4c4c4c, 0px 0px 2px #00e4ff, 0px 0px 2px #00e4ff, 0px 0px 2px #00e4ff, 0px 0px 2px #00e4ff;
    background-image: radial-gradient(rgba(223, 162, 238, 0.5), rgba(255, 255, 255, 0.3));
    font-size: 17px;
    text-align: center;}

/*____Page d'accueil connecté____*/
/*Boutons d'accueil déconnexion*/
	#play-free.button.button-purple {
    font-size: 16px;
	margin-top: 45px;}
    #index-banner #fast-registration {
	font-size: 16px;}
/*Personnage sur la page d'accueil*/
	#index-player {border-radius: 250px;
    border: solid 2px #FFF9A8;}

/*____Menu____*/
/*Enlever le fond et cadre du menu*/
	#main-menu ul {
    text-align: center;
    white-space: nowrap;
    background-color: rgba(92, 157, 248, 0);
    box-shadow: inset 1px 1px 3px 1px  rgba(92, 157, 248, 0);}
	#main-menu ul:after {
    background-size: 4px 0%, 4px 0%, 0% 4px, 0% 4px;
    box-shadow: 1px 2px 3px 0 rgba(47, 62, 113, 0);}
/*Icone Episode*/
    #main-menu .main-menu-episode {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536859512.of.world-lolo.com.png");}
	#main-menu .main-menu-episode:hover, #main-menu .main-menu-episode.selected {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536860450.of.world-lolo.com.png");}
/*Icone Mini-Jeu*/
    #main-menu .main-menu-minigames {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536859526.of.world-lolo.com.png");}
	#main-menu .main-menu-minigames:hover, #main-menu .main-menu-minigames.selected {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536860432.of.world-lolo.com.png");}
/*Icone Boutique*/
    #main-menu .main-menu-mall {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536859516.of.world-lolo.com.png");}
	#main-menu .main-menu-mall:hover, #main-menu .main-menu-mall.selected {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536860444.of.world-lolo.com.png");}
/*Icone Marché*/
    #main-menu .main-menu-marketplace {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536859517.of.world-lolo.com.png");}
	#main-menu .main-menu-marketplace:hover, #main-menu .main-menu-marketplace.selected {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536860434.of.world-lolo.com.png");}
/*Icone Bibliothèque*/
    #main-menu .main-menu-library {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536859529.of.world-lolo.com.png");}
    #main-menu .main-menu-library:hover, #main-menu .main-menu-library.selected {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536860440.of.world-lolo.com.png");}
/*Icone Messagerie*/
    #main-menu .main-menu-messaging {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536859518.of.world-lolo.com.png");}
	#main-menu .main-menu-messaging:hover, #main-menu .main-menu-messaging.selected {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536860442.of.world-lolo.com.png");}
/*Icone Banque*/
    #main-menu .main-menu-bank {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536859530.of.world-lolo.com.png");}
	#main-menu .main-menu-bank:hover, #main-menu .main-menu-bank.selected {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1536860142.of.world-lolo.com.png");}
/*Indication au survol du menu*/
    #main-menu .menu-label span {
    background-image: radial-gradient(#FFF9A8, #DFA2EE);
	color: #d077e6;
	box-shadow: 1px 1px 1px white;}


/*____Carroussel - Annonces défilantes____*/
/*Flèches Menu Déroulant*/
.index-carousel-prev {
    background: url("https://www.world-lolo.com/images/uploads/image.num1543148346.of.world-lolo.com.png");
    left: 4px;
    width: 33px !important;
	height: 33px !important;
    top: 100px !important;}
.index-carousel-next {
    background: url("https://www.world-lolo.com/images/uploads/image.num1543148336.of.world-lolo.com.png");
    right: 5px;
    width: 33px !important;
	height: 33px !important;
    top: 100px !important;}
/*Titre menu déroulant*/
    .index-carousel-title {
    font-family: "Times New Roman" !important;
    font-size: 24px;
    line-height: 30px;
    font-weight: bold;
    color: white;
    text-align: center;
    margin-left: 15%;
    text-shadow: 1px 1px 1px #4c4c4c, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff;}
/*Sous-titre menu déroulant*/
.index-carousel-subtitle {
    border-top: 1px solid #FFFFFF;
    font-family: "Banda Regular", sans-serif;
    text-transform: uppercase;
    font-size: 18px;
    font-weight: bold;
    line-height: 18px;
    color: #DFA2EE;
    padding: 5px 0;
    text-align: center;
    margin-left: 15%;
    text-shadow: 1px 1px 1px black, 1px 1px 1px #4c4c4c, 0px 0px 3px white, 0px 0px 3px white, 0px 0px 3px white, 0px 0px 3px white, 0px 0px 3px white;}
/*Bords et Couleur Menu déroulant*/
    .index-carousel {
    position: relative;
    height: 231px;
    width: 522px;
    border-radius: 10px;
    border: 2px solid #FFF9A8;
    background-color: rgba(102, 195, 216, 0.7);
    overflow: hidden;}
/*Intérieur du menu déroulant*/
	.index-carousel-description {
    text-shadow: 0px 0px 0px #4c4c4c, 0px 0px 3px white, 0px 0px 3px white, 0px 0px 3px white, 0px 0px 3px white, 0px 0px 3px white !important;
    font-family: "Banda regular", serif, bold;
    text-align: justify;
    color: #d077e6 !important;
    font-weight: bold;
    margin-top: -1px;}
/*Bouton Menu Déroulant Accueil*/
	.flavr-button.purple, .button.button.button-purple {
    color: #FFF9A8;
    background-color: #DFA2EE;
    border-color: #FFF9A8;}
	.flavr-button.purple:hover, .button.button.button-purple:hover {
    background-color: #FFF9A8;
    color: #DFA2EE;
    border-color: #DFA2EE;}

/*____Annonces actualité accueil____*/
/*Tableau Titre Annonces Actualités*/
    #index-news {
    display: inline-block;
    vertical-align: top;
    height: 344px;
    width: 450px;
    margin: 30px 30px 10px;
    background-image: radial-gradient(#d077e6, #dfa2ee);
    border-radius: 10px;
    border: 2px solid #FFF9A8;
    overflow: hidden;}
/*Titres des actualités*/
    #index-news .tile-title {text-shadow: 1px 1px 1px black, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff, 0px 0px 6px #00e4ff;
    font-family: "Times New Roman" !important;
    font-size: 20px;
    color: white;
    font-weight: bold;}
/*Fond Tableau Annonces Accueil*/
    #news-list .news {
    opacity: 1;
    position: relative;
    border-top: 2px solid #dfa2ee !important;
    height: 50px;
    box-shadow: inset 0px 0px 2px #fff9a8, 0px 2px 5px #fff9a8 !important;
    background: linear-gradient(0deg, #fffbca 0%, #ebc7f4 76%, #fffbca 96%);}
/*Texte des annonces*/
    #index-news .text a {
    color: #009cbf;
    font-size: 15px;
    font-family: "Banda Regular", sans-serif;
    font-weight: bold !important;
    text-shadow: 0px 0px 2px white, 0px 0px 2px white;}
/*Dates Tableau Annonces Accueil*/
    #index-news .date {
    float: left;
    background-color: #d077e6;
    width: 55px;
    height: 25px;
    color: #fff;
    border-radius: 10px;
    font-size: 16px;
    font-family: "Banda Regular", sans-serif;
    text-align: center;
    line-height: 25px;
    margin: 12px 5px;}


/*____Information de la joueuse en haut____*/
/*Encadré Nom*/
    #player-infos .player-name {
    color: white;
    font-weight: 600;
    font-family: "Banda Regular", sans-serif;
    text-shadow: 1px 1px 1px black, 1px 1px 1px #00e4ff;
    position: relative;
    height: 26px;
    line-height: 26px;
    display: inline-block;
    border: ridge 3px #FFF9A8;
    background-image: radial-gradient(#d077e6, #dfa2ee);
    -webkit-transform: skew(-12deg);
    -moz-transform: skew(-12deg);
    -o-transform: skew(-12deg);
    -webkit-backface-visibility: hidden;}
/*Encadré Argent*/
    #player-infos .player-currencies {
    color: #d077e6;
    background-image: radial-gradient(#d077e6, #dfa2ee);
    border-radius: 20px;
    border: solid 2px #FFF9A8;
    height: 25px;
    line-height: 25px;
    font-family: Banda Semi Bold, sans-serif;
    font-weight: 600;
    padding: 0 10px;
    margin-top: 8px;
    text-shadow: 1px 1px 1px black, 1px 1px 1px #00e4ff;}
/*Rond Niveau (tête gardienne)*/
    #player-info-level {color: #d077e6;
    text-shadow: 1px 1px 1px black, 1px 1px 1px black, 0px 0px 3px white, 0px 0px 3px white;
    text-align: center;
    font-size: 18px;
    font-family: Magnel, serif;
    background-image: radial-gradient(white, #009cbf);
    box-shadow: 0 0 5px 0 rgb(35, 6, 6);
    border: 2px ridge white;
    width: 33px;
    height: 33px;}
/*Rond visage gardienne*/
	#player-headerPortrait {background-image: url("https://www.world-lolo.com/images/uploads/image.num1537724227.of.world-lolo.com.png");}
    #playerPortraitDisplay, #playerPortraitDisplay canvas {
    width: 90%;
    height: 90%;
    border-radius: 50%;
    position: absolute;
    top: 6.5px;
    left: 6.5px;}
    #player-headerPortrait a {
    width: 105px;
    height: 105px;
    position: absolute;
    top: 0px;
    left: 0px;}
/*Rond familier*/
    #pet-headerPortrait {
    border: solid 2px rgba(208, 119, 230, 0.5);
    top: 39px;
    left: 14px;}
/*Entête joueuse et familier (Début-Milieu-Fin)*/
	#header-portraits {
    width: 197px;
    height: 132px;
    background: url("https://www.world-lolo.com/images/uploads/image.num1527784032.of.world-lolo.com.png") no-repeat right;}
	#player-infos {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1527784023.of.world-lolo.com.png");
    height: 125px;
    padding: 0 10px 0 10px;
    text-align: center;}
	#player-infos:after {
    content: '';
    position: absolute;
    left: 100%;
    top: 0;
    width: 36px;
    height: 125px;
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1527784007.of.world-lolo.com.png");
    z-index: -1;}
/*Entête joueuse et familier (Début-Milieu-Fin)*/
	#header-portraits {
    width: 197px;
    height: 132px;
    background: url("https://www.world-lolo.com/images/uploads/image.num1537635465.of.world-lolo.com.png") no-repeat right;}
	#player-infos {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1537635570.of.world-lolo.com.png");
    height: 125px;
    padding: 0 10px 0 10px;
    text-align: center;}
	#player-infos:after {
    content: '';
    position: absolute;
    left: 100%;
    top: 0;
    width: 36px;
    height: 125px;
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1537635639.of.world-lolo.com.png");
    z-index: -1;}
/*Etincelles de progression de niveau*/
	#player-info-dots .player-info-dot.lit {
    background-color: transparent;
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1537635774.of.world-lolo.com.png");
    width: 17px;
    height: 17px;}


/*____Titres des pages____*/
    #container h1 {
    position: relative;
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1537719223.of.world-lolo.com.png");
    height: 45px;
    line-height: 45px;
    display: inline-block;
    color: #00e4ff;
    text-shadow: 1px 1px 1px black, 1px 1px 1px #00e4ff;
    font-family: Merriweather, serif;
    font-size: 16px;
    text-transform: uppercase;
    padding: 0 15px 0 25px;
    margin-left: -10px;}
    #container h1 .title-end {
    background-image: url("https://www.world-lolo.com/images/uploads/image.num1537719232.of.world-lolo.com.png");
    position: absolute;
    right: -25px;
    top: 0;
    width: 25px;
    height: 45px;}


/*____Autres____*/
/*Indications au Survol (inventaire)*/
    .tooltip-content {
    font-family: "Banda Light";
    font-size: 12px;
    text-shadow: 1px 2px 3px rgba(0,0,0, 0.3);
    color: #FFF9A8;
    background-image: radial-gradient(#d077e6, #DFA2EE);
    border: 2px ridge #FFF9A8;}
/*Texte Pop Up (retour familier et autres)*/
	.flavr-container .flavr-fixer .flavr-outer .flavr-content .flavr-message p {text-align: center;
    border-bottom: none;
    color: #d077e6;
    text-shadow: 0px 0px 2px white, 0px 0px 2px white, 0px 0px 3px white, 0px 0px 3px white, 0px 0px 4px white, 0px 0px 4px white;
    font-weight: bold;}
/*Titre Pop Up*/
	.flavr-container .flavr-fixer .flavr-outer .flavr-content h1 {color: white;
    text-shadow: 1px 1px 2px black, 2px 2px 2px #00e4ff;
