/*Bouton citer*/
	.pun p:last-child a {color: #009cbf;}
	.pun p:last-child a:hover {color: #009cbf;
    text-shadow: 0px 0px 5px #dfa2ee, 0px 0px 5px #dfa2ee;}
/*Contenu des post*/
	.pun .post .content {font-family: "Times New Roman" !important;
	font-size: 17px !important;}
/*Pseudos*/
	#forum .pun.box > .inbox .author .author_name .summary, #forum .pun.box > .inbox .author .author_name strong {
    text-align: center;
    font-family: Merriweather, serif;
    color: white;
    font-size: 15px;
    font-weight: bold;
    text-shadow: 1px 1px 1px black, 1px 1px 1px #00e4ff, 0px 0px 10px #00e4ff, 0px 0px 15px #00e4ff;}
/*Couleur du texte des indications de catégories*/
	.pun#index td.col1 {color: #009cbf;
	font-weight: bold;}
/*Fond des indications de vues et réponses*/
	.pun.pun_topic_page td.altcol {background-color: #71e0c6;
    color: white;
    text-shadow: 1px 1px 1px black;}
/*Fond des indications de vues et réponses pour les topics classés en haut*/
	.pun_topic_page td.col2.sticked_col, .pun_topic_page td.col3.sticked_col {background: #66c3d8;}
/*Titre des topics classés en haut*/
    .pun_topic_page td.col1.sticked_col a.topic_link {font-size: 12px;
    color: #ff95d9;
    font-family: Merriweather, serif;
    text-shadow: 1px 1px 1px black, 1px 1px 1px #4c4c4c, 0px 0px 5px white, 0px 0px 5px white, 0px 0px 5px white, 0px 0px 5px white;}
/*Bordure après les topics classés en haut*/   
    #forum tbody > tr.pun_sticked_separator td {border: 1px solid #ebc7f4;
    background: #dfa2ee;}
/*Bordures du forum entier*/
	.pun .inbox {border: solid 2px #d077e6;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    margin-top: -3px;}
/*Bordure gauche-droite des catégories*/
    table {
    border-collapse: collapse;
    border-spacing: 0;
    background-color: #b2e1eb;}


/*____Page aide____*/
/*Questions Fréquentes Block*/
	#support-menu {
    width: 180px;
	background-image: radial-gradient(#ebc7f4,#66c3d8);
    border: 3px ridge #fff9a8;
    position: relative;
    float: left;
    clear: both;
    padding: 8px;
    border-radius: 10px;}
/*Question Fréquentes non Séléctionnées*/
	#support-menu li {
    font-size: 13px;
    font-weight: bold;
    color: #009cbf;
    text-align: center;
    padding: 5px;
    margin: 7px 0px;}
/*Survol Texte Questions Fréquentes*/
	#support-menu li:hover {color: #fff;
    text-shadow: 1px 1px 3px #00E4FF, 1px 1px 3px #00E4FF, 1px 1px 3px #00E4FF;}
/*Question Fréquente sélectionnée*/
	#support-menu li.on {
    color: white;
    font-weight: bold;
    text-shadow: 1px 1px 3px #d077e6, 1px 1px 3px #d077e6, 1px 1px 3px #d077e6;}
/*Bouton recherche*/
	#support-search input {
    background-color: #fff9a8;
    border: 2px solid #d077e6;
    text-align: center;
    color: #d077e6;}
/*Fond Block Droite*/
	#support-category-content {
    float: left;
    width: 635px;
	background-color: rgba(102, 195, 216, 0.5);
    border-radius: 10px;
    border: 2px ridge white;
    margin: 0px 15px;
    padding: 15px;}
/*Texte Question Droite*/
	.support-main-container .question > a {
    color: #009cbf;
    font-weight: bold;
    font-family: 'Trebuchet MS', Tahoma, Verdana, Arial, sans-serif;
    padding: 10px 10px 10px 30px;
    border-radius: 4px;
    border: 1px solid #fff9a8;}
/*Titre Question Au clic*/
	.support-main-container .question.active > a {color: #d077e6;}
/*Block Question au click*/
	.support-main-container .question {background: radial-gradient(#fffbca, #ebc7f4);
    border: solid 1px #fff9a8;}
/*Bloc question au click sélectionné*/
	.support-main-container .question.active {color: #d077e6;
    border: 2px solid #fff9a8;}
/*Ligne de Séparation Titre Question au Click*/
	.support-main-container .question .answer {border-top: 0px solid #ebc7f4;}
/*Question Droite au Survol*/
	.support-main-container .question > a:hover,
	.support-main-container .question.active > a {
    background-image: radial-gradient(#fff9a8, #66c3d8);
    color: white;
    text-shadow: 1px 1px 1px #d077e6, 1px 1px 3px #d077e6, 1px 1px 3px #d077e6, 1px 1px 3px #d077e6;
    border: 1px dotted #009cbf;}


/*____Page compte____*/
/*Bloc*/
	.account-personal-bloc {color: #009cbf;
    border: solid 3px #fff9a8;
	background: linear-gradient(to top, #ebc7f4 0%, #fffbca 40%, rgba(223, 162, 238, 0.2) 100%);}
	.account-social-bloc {color: #4F3F8C;
    border: solid 3px #fff9a8;
	background: linear-gradient(to top, #ebc7f4 0%, #fffbca 40%, rgba(223, 162, 238, 0.2) 100%);}
	.account-misc-bloc {color: #4F3F8C;
    border: solid 3px #fff9a8;
	background: linear-gradient(to top, #ebc7f4 0%, #fffbca 40%, rgba(223, 162, 238, 0.2) 100%);}
/*Titres des bloc*/
	.account-main-container h2, .account-social-bloc h2, .account-misc-bloc h2 {color: #009cbf;
	text-shadow: 0px 0px 3px white, 0px 0px 3px white, 0px 0px 3px white;}
/*Texte des bloc*/
	.account-main-container .bloc {color: #d077e6;}
/*Boutons homme et femme*/
	.account-main-container .custom-checkbox {background-color: #3bc3ea;
    color: white;}
    #account-gender-f:checked + .custom-checkbox {background-color: rgb(249, 138, 185);
    color: rgb(255, 255, 255);}


/*____Page CGU____*/
/*Bloc principal*/
	#container .cgu-main-container > div {background-color: #fff9a8;
    border: 2px solid #dfa2ee;
    border-radius: 8px;
    padding: 10px;
    color: #d077e6;
    text-shadow: 0px 0px 3px white, 0px 0px 3px white, 0px 0px 3px white, 0px 0px 3px white;
    font-weight: bold;}
/*Titres*/
.cgu-main-container h3 {
    color: #d077e6;
    text-shadow: 1px 1px 8px white, 1px 1px 8px white, 1px 1px 8px white, 1px 1px 8px white, 1px 1px 10px #00e4ff, 1px 1px 10px #00e4ff, 1px 1px 10px #00e4ff;}


/*____Page de contact____*/
/*Bloc*/
	#contact-player-container, #contact-partner-container {background: #fff9a8;
    border: 2px solid #dfa2ee;}
/*Titres*/
	.contact-main-container h3 {color: #d077e6;
    text-shadow: 1px 1px 4px #00e4ff, 1px 1px 4px #00e4ff;
    text-align: center;
    border-bottom: 1px dotted #dfa2ee;}
/*Texte*/
#contact-player-container li a, #contact-player-container ul, #contact-partner-container p {color: #009cbf;}


