/*____Page épisode en cours____*/
/*Barre Blabla Episodes*/
	.choiceText {
    font-size: 15px;
	background-image: radial-gradient(#71e0c6,#66c3d8);
	border: dotted 1px #4F3F8C;
    text-shadow: 1px 1px 5px white, 1px 1px 5px white;}
	.choice, .choice span {color: #d077e6 !important;}
/*Objectif et Historique des Actions*/
	#episode-toolbox > div {background: linear-gradient(to top, #009cbf 0%, #66c3d8 40%, rgba(113, 224, 198, 0.5) 100%);
	border: 1px ridge #009cbf;
    width: 383px;
    height: 140px;
    overflow: hidden;
    padding: 5px;
    border-radius: 4px;}
/*Grand Titre Historique et Objectif*/
	#episode-toolbox h2 {text-shadow: 1px 1px 1px black, 1px 1px 1px #00E4FF, 1px 1px 3px #00E4FF, 1px 1px 3px #00E4FF;
    text-align: center;
    color: white;
    border-bottom: 1px dotted white;
    font-family: "Times New Roman" !important;
    text-transform: uppercase;
    font-weight: bold;}
/*Texte de l'historique et objectifs*/
	#notifications li, #objective-list li, #objective-list li.objective-completed {
    color: white;
    text-shadow: 1px 1px 1px black, 1px 1px 1px black, 1px 1px 3px #00E4FF, 1px 1px 3px #00E4FF, 1px 1px 3px #00E4FF;
    font-size: 13px;
    font-family: "Banda regular", serif, bold;}
/*Texte Narration Episode*/
	.bubbleText > div {color: #009cbf;
	text-shadow: 1px 1px 1px white, 1px 1px 1px white, 0px 0px 5px white, 0px 0px 5px white, 0px 0px 5px white, 0px 0px 5px white, 0px 0px 10px white, 0px 0px 10px white, 0px 0px 10px white, 0px 0px 10px white;}
/*Fond RPG*/
	.choiceTextRpg {background-image: radial-gradient(#71e0c6,#66c3d8);
    text-shadow: 0px 0px 5px white, 0px 0px 5px white, 0px 0px 5px white, 0px 0px 5px white;}
	.choice:hover {background: transparent;
    text-shadow: 0px 0px 5px #fff9a8, 0px 0px 5px #fff9a8, 0px 0px 5px #fff9a8, 0px 0px 5px #fff9a8;}
/*Boite de narration en haut*/
    #episode-container .text > div {color: #4c52c1;
    text-shadow: 1px 1px 1px white, 1px 1px 1px #00E4FF;
    padding: 19px;
    box-sizing: border-box;}
    #episode-container .text {background-image: linear-gradient(rgba(235, 199, 244, 0.9) 20%, rgba(255, 255, 255, 0.7) 70%, rgba(0, 156, 191, 0.5) 100%)}
}
