style.css:
@-moz-document domain("creaturescaves.com") {
body {
    background-color: #444;
}

body > table {
    width: 50%;
}

form > a > img {
    position: relative;
    vertical-align: middle;
    top: -1px;
}

tr {
    line-height: 2em;
}

#main > a {
    font-size: 1.1em;
}

#note > a {
    font-size: 1.1em;
}

#note > a > img {
    height: 28px;
    width: 28px;
}

.tmenu {
    font-size: 1.6em;
}

#t1menu {
    font-size: 1.2em;
}

.headline {
    font-size: 1.2em;
}

.main,
.note,
.bmenu {
    font-family: "Bitstream Vera Serif", "DejaVu Serif", "Liberation Serif", "Verdana";
    line-height: 1.5;
    font-size: 0.9em;
}
}
