style.css:
@-moz-document url-prefix("http://c-faq.com/") {
html {
    font-family: Abel, Georgia, Arial;
    background-color: ivory;
}

body {
    background-color: ivory;
    padding: 1em 2em;
    margin: 1em 5em;
}

a {
    color: dimgray;
    font-style: oblique;
}

font {
    font-family: Abel, Georgia, Arial;
    font-size: 1.5em;
    font-style: oblique;
    color: deeppink;
    padding: 5px;
}

h1 {
    font-style: oblique;
}

pre,
tt {
    color: deeppink;
    font-family: "Courier New", Courier, monospace;
    font-size: 0.8em;
}
}
