style.css:
@-moz-document domain("aws.amazon.com") {
html,
iframe {
    background: #FFF;
    -webkit-filter: invert(100%) !important;
}

body,
.aws-page-header {
    background: #000;
}

/* Un-invert */
img,
.aws-signin-logo-header-box,
#nav-menubar,
#aws-nav,
#footer,
#console-nav-footer,
#aws-nav-cta-button,
#right-content-wrapper,
.chartgraph,
.awsui-util-pt-m,
.awsui-breadcrumb-group,
.service-icon-container,
.solution-title,
.solution-icon,
.recent-service-icon,
/* .awsui .awsui-icon.awsui-icon-variant-normal, */
.cwdb-chart,
.recent-service-label,
.gwt-Button,
.gwt-Anchor,
.GK20X4JBMLF,
.awsc-header,
.m-nav-header,
.m-nav-panel-wrapper,
.lb-carousel-stage-outer,
.lb-bg-left-top-cover,
.lb-bg-center-center-cover,
.lb-bg-center-top-cover,
.lb-carousel-item,
.lb-bg-left-top-cover-no-repeat,
.lb-bg-center-center-cover-no-repeat,
.cwui-colorpicker-item.standalone,
.cwui-colorpicker-popup.cwui-popup .cwui-colorpicker,
.lb-tiny-12,
.lb-mid-18,
.sfn-workflow-legend-success,
.sfn-workflow-legend-failed,
.sfn-workflow-legend-cancelled,
.sfn-workflow-legend-in-progress,
.sfn-workflow-graph,
.awsui-util-status-positive,
.awsui-util-status-negative,
.btn,
.aws-nav-logo,
.breadcrumb,
.status,
.data-grid-cell-text,
div.data-grid-cell.color-positive,
a.awstoc,
a.pagetoc,
.service-name,
.GINSA1BCBUF,
.GINSA1BCCUF,
.GINSA1BCAUF,
/* .awsui .cloudwatch-basic-graph svg, */
.awsui-flash,
.event-layer,
.xterm-cursor-layer,
.xterm-link-layer,
.xterm-selection-layer,
.xterm-text-layer,
.xterm-helpers,
.xterm-viewport {
    -webkit-filter: invert(100%) !important;
}

/* Step functions */
.awsui-table-row a,
.sfn-dictionary-item {
    -webkit-filter: invert(100%) !important;
}
.sfn-dictionary-item {
    color: #777
}
.sfn-dictionary-item awsui-icon[name="status-positive"] {
    -webkit-filter: invert(100%) !important;
}
.sfn-dictionary-item span[translate="SUCCEEDED"] {
    -webkit-filter: invert(100%) !important;
}
.sfn-dictionary-item awsui-icon[name="status-negative"] {
    -webkit-filter: invert(100%) !important;
}
.sfn-dictionary-item span[translate="FAILED"] {
    -webkit-filter: invert(100%) !important;
}
.sfn-execution-status[status="$item.status"] awsui-icon[name="status-positive"] {
    -webkit-filter: invert(100%) !important;
}
span[class="awsui-util-status-positive"] {
    -webkit-filter: invert(100%) !important;
}
tr[class="awsui-table-row ng-scope"] span[class="awsui-icon awsui-icon-size-normal awsui-icon-variant-link"] {
    -webkit-filter: invert(100%) !important;
}

/* General UI elements */ 
.awsui-table-row awsui-radio-button,
#consoleNavHeader {
    -webkit-filter: invert(100%) !important;
}
.awsui-radio-button-styled-circle  {
    fill: #44444440 !important;
    stroke: #444 !important;
}
.m-nav-mobile,
.image {
    -webkit-filter: invert(100%) !important;
}

/* Cloudwatch */
.GIYU-ANBKEF,
.GIYU-ANBLFF,
.cwdb-copilot-breadcrumb-current span {
    -webkit-filter: invert(100%) !important;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.75);
}

.btn, .btn:visited {
    text-shadow: 0 1px 1px rgba(255, 255, 255, 0);
}

.btn.disabled, .btn:disabled, .btn[disabled] {
    background-image: linear-gradient(to bottom, #505050, #232323);
}

/* RDS */
.GK15FVNCDRF a {
color:white;
}

.GK15FVNCDRF a:visited {
color:darkgray;
}

.GK15FVNCDRF a:hover {
color:#6a686a;
}

.datapoint-leaderboard-row-color {
    -webkit-filter: invert(100%) !important;
}

/* Docs */
.awsdocs-banner,
.awsdocs-local-nav-header,
.awsdocs-header {
    -webkit-filter: invert(100%) !important;   
}

.awsdocs-header {
    background: #2b2b2b;
}

/* Notices */
.awsui .awsui-alert-type-info {
    border-color: #ff8c44bf;
}

/* Cloudwatch Charts*/

.cwdb-chart svg {
    background-color: #000;
}

.cwdb-status-insufficient .cwdb-chart-container-background {
    border: 2px solid #e07700b8;
}

.header-background {
    fill: #000;
}

.axis.dimmable .tick text {
    fill: #9d9d9d;
}   

.legend text {
    fill: #9d9d9d;
}

body .btn, body .btn:hover, body .btn:visited, body .Select-control, body .awsui .awsui-button.awsui-button-variant-primary, body .awsui .awsui-button.awsui-button-variant-primary:hover {
    background: #000;
    color: #ABA49B;
    border-color: #ABA49B;
    border-radius: 2px;
    cursor: pointer;
    text-decoration: none;
}

.cwui .cwui-datepicker .cwui-datepicker-toolbar .cwui-datepicker-quickpick-selected a {
    color: #EE9944;
    font-weight: bold;
}

.cwdb-page a {
    color: #EE9944;
    text-decoration: none;
}

.alarm-text-background {
    fill: #000000;
    opacity: 0.8;
}

/* SQS */

.awsui .awsui-util-container .awsui-util-label  {
    color: #868686;
}

.awsui .awsui-util-container awsui-column-layout  {
    color: #fff;
}

#cwdb-container .cwdb-page {
    background-color: #fff;
}

#h {
        background: #dcd0c1;   
}

/* Cloudwatch dashboard */

/* Headers */
.cwdb-copilot-alarms-summary-table th:nth-child(2) {
    color: #33ffffc7;
}
.cwdb-copilot-alarms-summary-table th:nth-child(3) {
    color: #1f88ffc4;
}
.cwdb-copilot-alarms-summary-table th:nth-child(4) {
    color: #ff66ccc9;
}

/* Sidebar */
.GIYU-ANBLEF a {
    color: #33ffffc4;
}
.cwdb-dashboard {
    background: #FFF;
}
#b {
    background-color: #FFF;
}
.gwt-SplitLayoutPanel {
    background-color: #FFF;
}
.GIYU-ANBNEF {
    background-color: #33ffffc4;
}
.GIYU-ANBKFF a {
    color: #1f88ffc9;
}
.GIYU-ANBOEF {
    background-color: #1f88ffc9;
}
.GIYU-ANBPFF a {
    color: #f6cc;
}
.GIYU-ANBPEF {
    background-color: #f6cc;
}
.cwdb-copilot-alarms-summary-table td:nth-child(1) a {
    color: #ee9944;
}
.cwdb-copilot .cwdb-dashboard-title .Select-control {
    background-color: #FFFFFF;
}

/* Direct Connect */
.awsui .awsui-util-container awsui-column-layout {
    color: #818080;
}

/* RDS */
[class^="G"]  a {
color:#818181;
}

[class^="G"] a:visited {
color:darkgray;
}

[class^="G"]  a:hover {
color:#6a686a;
}


/* Logged out page */


/* Home page boxes */
.toggle-content h4 {
    -webkit-filter: invert(100%) !important;   
}

.sideBarCard-section-with-icon .link-external {
    -webkit-filter: invert(100%) !important;   
}

.sidebar-section-item .link-external {
    -webkit-filter: invert(100%) !important;   
}

.awsui-util-container-header .link-external,
.awsui-util-container-footer .link-external {
    -webkit-filter: invert(100%) !important;   
}

a.awstoc:visited,
a.pagetoc:visited {
    color: gray
}

a.awstoc.selected,
a.pagetoc.selected {
    color: white
}

.sfn-workflow-graph {
    background: #1122338f
}

#right-content-wrapper {
    background: #FFF
}

.path, .shape {
   stroke: #abc !important;
   stroke-width: 2px !important;
}
}
