:root {
/*[[link-color]]*/
/*[[quote-color]]*/
/*[[thm-color]]*/
/*[[thm-color-level]]*/
/*[[thm-scheme]]*/
}

html[data-theme=nightmode]:root {
/*[[ng-link-color]]*/
/*[[ng-quote-color]]*/
/*[[ng-thm-color]]*/
/*[[ng-thm-color-level]]*/
/*[[ng-thm-scheme]]*/
}

::-webkit-scrollbar {background: var(--theme_scrollbg); height: 14px; width: 14px;}
::-webkit-scrollbar-track {background-clip: padding-box; border: solid transparent; border-width: 0;}
::-webkit-scrollbar-button {width: 0px; height: 0px;}
::-webkit-scrollbar-thumb {background-color: var(--theme_scroll); background-clip: padding-box; border: 2px solid transparent; border-radius: 4px;}
::-webkit-scrollbar-thumb:hover {background-color: var(--theme_scrollhover);}
::-webkit-scrollbar-thumb:active {background-color: var(--theme_scrollactive);}
::-webkit-scrollbar-corner {background: var(--theme_scrollbg);}

div[style="width: 100%; height: 100%; position: fixed; top: 0px; left: 0px; z-index: 2000000000; background-color: rgb(255, 255, 255); opacity: 0.5;"] {background-color: var(--theme_default_bg) !important;}

/*[[width-thread]]*/

*::selection {background: var(--theme_default_link); color: var(--theme_default_contrasttext);}
*::-moz-selection {background: var(--theme_default_link); color: var(--theme_default_contrasttext);}
*::-webkit-selection {background: var(--theme_default_link); color: var(--theme_default_contrasttext);}

.rmenu, #plashque, #mobile-plashque, .logo img, .logo br, .mmm {display: none;}

a {color: var(--theme_default_link); text-decoration: none; transition: color .1s; outline: none;}
a:hover {color: var(--theme_default_hover);}
a:link {text-decoration: none;}
a[href*="youtube.com"] {border-bottom: none;}
a[href$="sage"], a[href$="Sage"], a[href$="SAGE"] {color: var(--theme_sage);}
a[savefrom_lm_is_link="1"] {background: none !important;}
a[savefrom_lm_is_link="1"]::before {content: '\f019'; font: 16px/1 FontAwesome;}
.hashlink {color: var(--theme_default_altlink);}
.hashlink:hover {color: var(--theme_default_althover);}

/* ===== Main Page ===== */
.st0, .st1, use[fill="url(#gradientaaCw2bI38)"] {fill: var(--theme_default_link);}
.welcomefriend {color: var(--theme_default_link);}

.wraper.mainpage {margin: 0 10px;}
.mcontent, .board-data {max-width: 1000px; width: auto;}
.hdescription, .hdescription-stats {font-family: inherit;}
.postscontainer {border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_shadow);}
font[color="red"], font[color="#ff0000"], span[style="font-size:10px; color:red;"], span[style="color: #ff0000;"], .closed-board, .archive-thread, .thanks-abu {color: var(--theme_red) !important;}

.mainpage .pager {box-shadow: none;}
.hnews .news-posts-count {color: var(--theme_default_alttext2);}

/* Stats */
.ui-widget, .ui-widget input, .ui-widget select, .ui-widget textarea, .ui-widget button {font-family: inherit;}

.ui-state-default a, .ui-state-default a:link, .ui-state-default a:visited, a.ui-button, a:link.ui-button, a:visited.ui-button, .ui-button {color: var(--theme_default_alttext);}
.ui-state-hover a, .ui-state-hover a:hover, .ui-state-hover a:link, .ui-state-hover a:visited, .ui-state-focus a, .ui-state-focus a:hover, .ui-state-focus a:link, .ui-state-focus a:visited, a.ui-button:hover, a.ui-button:focus {color: var(--theme_default_text);}

#tabbed li {border: none; border-bottom: 2px solid transparent; border-radius: 0; font-weight: bold;}
#tabbed li.ui-tabs-active {background: none; border-bottom-color: var(--theme_default_link); color: var(--theme_default_link); margin-bottom: unset; padding-bottom: unset;}

.ui-widget-content {background: var(--theme_default_postbg);}
.ui-widget.ui-widget-content {box-shadow: 0 0 0 1px var(--theme_default_border);}
.ui-widget-header {background: var(--theme_default_postbg); padding: 4px;}
.tracker-tab, .board-data {border: none;}
.cloudheader .cloudoption {color: var(--theme_default_alttext);}
.cloudheader .cloudoption:hover {color: var(--theme_default_text);}

.table .thead .th {background: var(--theme_default_link); border: none; color: var(--theme_default_altbtntext);}
.table .thead .th a[style="color:black;"] {color: var(--theme_default_altbtntext) !important;}
.board-speed {color: var(--theme_default_alttext);}

hr {border-color: transparent;}
.hr {border-top-color: var(--theme_default_border);}
.box-data .data {border-color: var(--theme_default_border);}

/* FAQ */
a.logo {background: none; color: var(--theme_default_link) !important; padding: 0;}
a.logo::before {content: "\f0e7"; font: 60px/1 FontAwesome; padding-right: 10px;}

.box {background: var(--theme_default_postbg); border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_shadow);}
.box hr {border-top-color: var(--theme_default_border);}
.boardname {color: var(--theme_default_link);}

/* Donate Abu */
#adminbar ul.menu ul li a {color: var(--theme_default_link);}
#adminbar ul.menu ul li a:hover {color: var(--theme_default_hover);}
#adminbar ul.menu ul li a:visited {color: var(--theme_default_link);}
.logo {color: var(--theme_default_link);}

.reply {background: var(--theme_default_postbg); border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_shadow);}

/* Passcode kupi - Abu pomogi */
/*.header {color: var(--theme_default_link);}*/

#fixed {background-color: var(--theme_default_postbg); border-top: 1px solid var(--theme_default_border); border-bottom: 1px solid var(--theme_default_border); box-shadow: 0 1px 3px var(--theme_shadow);}
.amenu {font-family: inherit !important;}
.topmenu li a.mitem:link, .topmenu li a.mitem:visited {background: var(--theme_default_btnbg); border: 1px solid var(--theme_default_border); border-radius: 3px; color: var(--theme_default_text);}
.topmenu li a.mitem:hover {background: var(--theme_default_btnbghover);}
.addban, .floatforms {background-color: var(--theme_default_postbg); border: 1px solid var(--theme_default_border); box-shadow: 0 1px 3px var(--theme_shadow);}
.floatforms {font-family: inherit;}

/* Stickers */
.body-stickers .sticker {background-color: var(--theme_default_postbg); border-color: var(--theme_default_border); box-shadow: 0 1px 3px var(--theme_shadow); color: var(--theme_default_text);}
.body-stickers .sticker:hover {border-color: var(--theme_default_altborder);}

.sticker {scrollbar-color: var(--theme_scroll) transparent; scrollbar-width: thin;}

/* ===== Catalog ===== */
.logo {font-family: unset;}

.centered span[style="float:left"] a {color: var(--theme_default_text); font-size: 0;}
.centered span[style="float:left"] a:hover {color: var(--theme_default_link);}
.centered span[style="float:left"] a::before {content: "\f060"; font: 22px/30px FontAwesome; width: 40px; display: inline-block;}

.thread_wrap {border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_shadow); margin: 5px;}
.thread_wrap.expandble:hover .cthread {border-radius: 3px; box-shadow:  0 0 0 1px var(--theme_default_border);}
.thumb {border-radius: 3px; box-shadow: none; max-width: 228px; height: auto;}

/* ===== Page ===== */
.warningcover {background-color: var(--theme_default_glassbg); backdrop-filter: blur(10px); opacity: 1;}
.warningbox {border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 6px 16px var(--theme_shadow2); margin-top: -50px; overflow: auto;}
.warningboxbutton a {font-weight: bold; text-transform: uppercase; letter-spacing: .5px;}
.warningboxbutton a[id="ageboxallow"] {font-size: 0;}
.warningboxbutton a + br {display: none;}
.warningboxbutton a[id="ageboxallow"]::before {content: "–Ø —Å–æ–≥–ª–∞—Å–µ–Ω"; font-size: 18px; margin-right: 12px;}
.warningboxbutton a[id="ageboxdisallow"] {color: var(--theme_red);}

#fullscreen-container {animation: open .1s ease-out; box-shadow: 0 6px 16px var(--theme_shadow4); padding: 0; margin: 6px 4px 4px 4px;}
#fullscreen-container:hover:after {opacity: 1;}
#fullscreen-container:after {background: var(--theme_default_glassbg); border-radius: 3px; color: var(--theme_default_text); padding: 1px 5px; top: 2px; left: 2px; width: unset; opacity: 0; transition: .1s; white-space: unset;}
#fullscreen-container video {outline: none;}
.fullmedia__meta {background-color: var(--theme_default_postbg); border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_shadow2); color: var(--theme_default_text); line-height: 1.4; bottom: -26px; padding: 3px 8px;}

.na {background: var(--theme_default_postbg); border-radius: 0; display: block !important; height: 25%; width: 35px; padding: 0; left: 0; right: unset; text-align: center; z-index: 201;}
.na_type_up:hover, .na_type_up:active {background: linear-gradient(0deg, var(--theme_default_postbg) 0%, var(--theme_default_bg) 90%, var(--theme_default_postbg) 100%);}
.na_type_down:hover, .na_type_down:active {background: linear-gradient(0deg, var(--theme_default_postbg) 0%, var(--theme_default_bg) 10%, var(--theme_default_postbg) 100%);}
.na__icon {display: block; fill: var(--theme_default_alttext2); position: fixed;}
.na:hover .na__icon {fill: var(--theme_default_alttext);}
.na_type_up {top: 25%;}
.na_type_up .na__icon {padding: 10px 10px 0; width: 35px; height: 35px; top: 25%;}
.na_type_down {bottom: 25%; transform: none;}
.na_type_down .na__icon {padding: 10px 10px 0; width: 35px; height: 35px; transform: rotate(180deg); bottom: 25%;}

.alert {border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_shadow2);}

.media-thumbnail img {border-radius: 3px; box-shadow: 0 1px 6px 0 var(--theme_shadow3);}

/* Header */
.adminbar {color: var(--theme_default_alttext); text-align: center;}
.adminbar a {color: var(--theme_default_alttext); padding-right: 3px;}
.adminbar a:hover, .adminbar a:focus {color: var(--theme_default_link);}
.adminbar__menu {border-bottom: none; height: 32px;}
.adminbar__boards_sticky {border-bottom-color: transparent; box-shadow: 0 0 0 1px var(--theme_default_border); left: 0; transform: translateY(-36px); transition: transform .1s; z-index: 200;}
.adminbar__boards_sticky::before {content: ""; position: fixed; top: 35px; left: 0; right: 0; height: 12px;}
.adminbar__boards_sticky::after {background: var(--theme_default_altborder); border-radius: 3px; content: ""; position: fixed; top: 39px; left: 50%; padding: 2px 40px; transform: translateX(-50%);}
.adminbar__boards_sticky.adminbar__boards_hovered {box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_shadow2);}
.adminbar__boards_hovered {transform: none;}
.adminbar__boards_hovered::after {background: none;}
.adminbar__cat_type_myboards {color: var(--theme_default_alttext2);}
.adminbar__cat_type_myboards a {padding-right: 0;}

select {background: var(--theme_default_altbtnbg); background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="18" height="44"><defs><g><path id="select" d="M10.3 0L5.5 4.7.7 0 0 .7 5.5 6 11 .7z"/></g></defs><use href="%23select" x="0" fill="%23000"/><use href="%23select" x="0" y="22" fill="%23fff"/></svg>') !important; background-position: var(--theme_select) !important; background-repeat: no-repeat !important; border: 1px solid var(--theme_default_border); border-radius: 3px; color: var(--theme_default_text); padding: 2px 22px 2px 6px !important; outline: none; transition: border .1s, background .1s, color .1s; -webkit-appearance: none; -moz-appearance: none; appearance: none;}
select:focus {background: var(--theme_default_altbtnbghover); border-color: var(--theme_default_altborder);}
.style-switcher {height: 24px;}

/* Setting */
.settings__prefs {margin-left: 15px;}
.settings__header span {font-size: 0;}
.settings__header span:first-child::before {content: "\f078"; font: 12px/1 FontAwesome; margin-right: 4px;}
.settings__header span:last-child::before {content: "\f077"; font: 12px/1 FontAwesome; margin-right: 4px;}
.settings__row {padding: 1px 0;}
.settings__label select {margin-left: 4px;}
.settings__edit {background: none; color: var(--theme_default_text);}
.settings__edit:hover {color: var(--theme_default_link);}
.settings__edit::before {content: "\f044"; font: 14px/1 FontAwesome;}
#setting-editor-textarea-textarea {font-family: Consolas, monospace;}

.ah__header {background-color: var(--theme_default_postbg2);}
.ah__cell {border-color: var(--theme_default_postbg2);}
.error {border-color: var(--theme_red) !important;}
.ah__input:focus {border-color: var(--theme_default_altborder) !important; box-shadow: 0 1px 3px var(--theme_shadow); transition: width .4s ease;}

/* Create Post */
.newpost__label {font-size: 1.1em; line-height: unset;}
.desktop.newpost__label_top, .desktop.newpost__label_bot {background-color: var(--theme_default_link); border-radius: 3px; box-shadow: 0 2px 8px -4px var(--theme_default_link); color: var(--theme_default_altbtntext); display: inline-block; padding: 5px 14px; transition: .1s;}
.desktop.newpost__label_top:hover, .desktop.newpost__label_bot:hover {background: var(--theme_default_hover); box-shadow: 0 2px 8px -4px var(--theme_default_hover);}

.postform__raw {margin: 3px 0;}
textarea {min-height: 50px; max-height: 1000px; scrollbar-color: var(--theme_scroll) transparent; scrollbar-width: thin;}
input[type="text"], input[type="password"], textarea, .input {background: var(--theme_default_altbtnbg); border: 1px solid var(--theme_default_btnborder); border-radius: 3px; box-shadow: none; color: var(--theme_default_inputtext); padding: 4px 8px; transition: background .1s, border .1s; outline: none;}
input[type="text"]:focus, input[type="password"]:focus, textarea:focus, .input:focus {background: var(--theme_default_altbtnbghover); border-color: var(--theme_default_altborder);}
input[type="text"]::placeholder, input[type="password"]::placeholder, textarea::placeholder {color: var(--theme_default_alttext2);}
.postform__len {pointer-events: none;}
.postform__input_type_m {margin-right: 3px;}

button, .button, html input[type=button], input[type=reset], input[type=submit], .arch-searchform input[type="submit"] {background: var(--theme_default_btnbg); border: 1px solid var(--theme_default_btnborder); border-radius: 3px; box-shadow: none; color: var(--theme_default_inputtext); padding: 4px 8px; transition: .1s; outline: none;}
button:hover, .button:hover, html input[type=button]:hover, input[type=reset]:hover, input[type=submit]:hover {background: var(--theme_default_btnbghover); color: var(--theme_default_inputtext);}
button[disabled], html input[disabled] {background: var(--theme_default_btnbg) !important; color: var(--theme_default_alttext2) !important;}

.postform .rules {display: none;}

.postform__sticker-btn {bottom: -24px !important; left: 0 !important;}
.postform__sticker-prev {left: 0;}

#qr-CommentToolbar, #CommentToolbar {position: relative; margin-left: 22px;}
#qr-CommentToolbar span, #CommentToolbar span {display: inline-block; height: 18px; text-align: center;}
#qr-CommentToolbar span:before, #CommentToolbar span:before {position: absolute;}
.markup {padding: 1px 3px; opacity: 0;}
.m-bold:hover, .m-italic:hover, .m-underline:hover, .m-overline:hover, .m-strike:hover, .m-sup:hover, .m-sub:hover {color: var(--theme_default_link);}
.m-bold:before {content: "\f032"; font: 14px/18px FontAwesome; height: 18px; width: 22px;}
.m-italic:before {content: "\f033"; font: 14px/18px FontAwesome; height: 18px; width: 22px;}
.m-quote {color: var(--theme_default_postnum);}
.m-quote:hover {color: var(--theme_default_postnum2);}
.m-quote:before {content: "\f054"; font: 14px/18px FontAwesome; height: 18px; width: 22px;}
.m-underline:before {content: "\f0cd"; font: 14px/18px FontAwesome; height: 18px; width: 22px;}
.m-overline:before {content: "O"; font: bold 14px/20px serif; height: 18px; width: 22px; text-decoration: overline;}
.m-spoiler {color: var(--theme_default_alttext2); width: 31px;}
.m-spoiler:hover {color: var(--theme_default_text);}
.m-spoiler img {width: 31px; height: 18px;}
.m-spoiler:before {background: var(--theme_default_spoiler); content: "SP"; line-height: 14px; margin-top: 1px; height: 16px; width: 31px; transition: .1s;}
.m-strike:before {content: "\f0cc"; font: 14px/18px FontAwesome; height: 18px; width: 22px;}
.m-sup:before {content: "\f12b"; font: 14px/18px FontAwesome; height: 18px; width: 22px;}
.m-sub:before {content: "\f12c"; font: 14px/18px FontAwesome; height: 18px; width: 22px;}

/*[[report-checkbox]]*/
input[type="checkbox"] {cursor: pointer; height: 15px; width: 15px; -webkit-appearance: none; -moz-appearance: none; appearance: none; outline: none; margin-top: -1px; vertical-align: middle;}
input[type="checkbox"]::before {background: var(--theme_default_altbtnbg); border: 1px solid var(--theme_default_altborder); border-radius: 3px; box-sizing: border-box; content: ""; display: block; height: 15px; width: 15px; transition: .1s;}
input[type="checkbox"]:focus::before {background: var(--theme_default_altbtnbghover);}
input[type="checkbox"]::after {color: transparent; content: "\f00c"; display: block; font: 12px/15px FontAwesome; height: 15px; width: 15px; position: relative; bottom: 15px; text-align: center; transition: .1s;}
input[type="checkbox"]:checked:before {background: var(--theme_default_link); border-color: var(--theme_default_link);}
input[type="checkbox"]:checked::after {color: var(--theme_default_altbtntext);}

.filer__limits {color: var(--theme_default_alttext2); font-size: 12px; margin: 2px 0;}
.filer__drag-area {background-color: var(--theme_default_drag); border-color: transparent; border-radius: 3px; color: var(--theme_default_alttext2); transition: color .1s;}
.filer__drag-area:hover {color: var(--theme_default_alttext);}
.filer__drag-area_hover {color: var(--theme_default_link);}
.filer__thumb {border-radius: 3px; border-width: 2px; margin: 4px 6px 4px 0; overflow: hidden;}
.filer__meta {background: none; opacity: 1;}
.filer__size {background: var(--theme_default_glassbg); border-radius: 10px; box-shadow: 0 0 0 1px var(--theme_default_border); display: inline-block; padding: 1px 6px; margin: 0 3px 3px 3px;}
.filer__delete {background: var(--theme_default_glassbg); border-radius: 50%; box-shadow: 0 0 0 1px var(--theme_default_border); padding: 2px 4px 0; font-size: 13px; margin: 0 3px 3px 3px;}

.oekaki-width, .oekaki-height {width: 42px !important;}

.grecaptcha-badge {display: none;}

/* Drawing */
.literally .lc-picker, .literally .horz-toolbar, .literally .lc-options .lc-font-settings {background-color: var(--theme_default_postbg) !important;}
.literally .lc-picker, .literally.toolbar-at-top .lc-options {border-color: var(--theme_default_border) !important;}
.literally .lc-picker .toolbar-button:hover:not(.disabled) {background-color: var(--theme_default_postbg3) !important; border-color: var(--theme_default_postbg3) !important;}
