.modal a {font-size: 15px; padding: 6px 14px;}

.post__filezise {color: var(--theme_default_alttext) !important; margin-top: 0; visibility: visible !important;}
.post__image-link {float: none;}
.post__images_type_single + .post__message {margin: 0 1em 0.6em;}

/* Footer */
.bb {border-radius: 6px 6px 0 0; box-shadow: 0 0 0 1px var(--theme_default_border), 0 -2px 6px 0 var(--theme_shadow2); display: block !important; font-size: 1.1em; top: unset; left: 0; right: 0; bottom: -600px; transition: background .1s; z-index: 979;}
.bb:hover, .bb:focus, .bb:focus-within {bottom: 50px;}
.bb__panels {display: block !important;}
.bb__header, .bb-header-text {font-weight: bold; font-size: 16px; letter-spacing: .5px; text-transform: uppercase;}
.bb__update, .fav__fa_type_remove, .fav__fa_type_update {width: 16px; height: 16px;}
.bb__header::before {content: "\f005"; font: 18px/48px FontAwesome; position: fixed; left: 150px; bottom: 0; height: 50px; width: 50px; text-align: center; transition: .1s;}
.bb__header:hover::before {background-color: var(--theme_default_btnbghover);}
.bb__footer {font-size: 14px; text-align: left;}
.bb__footer label {display: block; margin: 4px 0;}
.bb__bstitle {max-width: 250px;}

.footer__info {display: none;}

/* ===== Dollchan ===== */
#de-panel {bottom: 51px;}

.de-parea-btn-thr, .de-parea-btn-close, .de-parea-btn-reply {border-radius: 50%; bottom: 10px; right: 20px; position: fixed; width: 46px; z-index: 981;}
.de-parea-btn-thr::after, .de-parea-btn-reply::after {content: "\f044"; font: 24px/36px FontAwesome;}
.de-parea-btn-close::after {content: "\f00d"; font: 24px/36px FontAwesome;}

#de-pform {background: var(--theme_default_postbg); left: 0; right: 0; top: 0; bottom: unset; position: fixed; z-index: 980;}
#de-pform > form {padding: 0 5px 125px;}

.replypage .de-thr-updater-link::before {color: var(--theme_default_text); font: 18px/48px FontAwesome; height: 50px; width: 50px; top: unset; bottom: 0; left: 200px; right: unset; z-index: 977; text-align: center;}
.replypage .de-thr-updater-link:hover::before {background: var(--theme_default_btnbghover); color: var(--theme_default_text);}

.de-mypost {margin-left: 0;}
.de-mypost::before {left: 0;}

body.de-runned .bb {bottom: -600px;}
body.de-runned .bb:hover, body.de-runned .bb:focus, body.de-runned .bb:focus-within {bottom: 50px;}
}
}

