html, body {font-family: 'Ubuntu', sans-serif !important;}

.cntnt {max-width: 1024px; margin: 0 auto; padding: 0 5px 0 42px;}
.catalog, .body-stickers {max-width: 1024px; margin: 0 auto; padding: 0 10px;}
@media only screen and (min-width: 1280px) and (orientation:portrait),  only screen and (min-width: 1280px) and (orientation:landscape){
.cntnt {max-width: 1024px; margin: 0 auto; padding: 0 5px 0 42px;}
.catalog, .body-stickers {max-width: 1024px; margin: 0 auto; padding: 0 10px;}
}
@media only screen and (min-width: 1480px) and (orientation:portrait),  only screen and (min-width: 1480px) and (orientation:landscape){
.cntnt {max-width: 1280px; margin: 0 auto; padding: 0 5px 0 42px;}
.catalog, .body-stickers {max-width: 1280px; margin: 0 auto; padding: 0 10px;}
}
@media only screen and (min-width: 1920px) and (orientation:portrait),  only screen and (min-width: 1920px) and (orientation:landscape){
.cntnt {max-width: 1480px; margin: 0 auto; padding: 0 5px 0 42px;}
.catalog, .body-stickers {max-width: 1480px; margin: 0 auto; padding: 0 10px;}
}

.cntnt__left {margin-top: 15px; padding: 0 5px 0 0; width: 335px; position: relative;}
.cntnt__left_sticky {scrollbar-color: var(--theme_scroll) transparent; scrollbar-width: thin;}
.cntnt__left:before {background: var(--theme_default_postbg); box-sizing: border-box; color: transparent; content: "\f0c9"; font: 20px/1 FontAwesome; position: fixed; top: 0; left: 0; bottom: 75%; padding: 9px; z-index: 201; width: 35px;}
.cntnt__left:after {background: linear-gradient(to bottom, var(--theme_default_bg) 0%,transparent 100%); content: ''; width: 100%; height: 20px; display: block; position: absolute; top: 0; z-index: 100; pointer-events: none;}

.fm {margin: 5px;}
.fm__header {background: none; border: 1px solid transparent; border-radius: 3px; font-weight: bold; font-size: 1.1em; color: var(--theme_default_text); display: inline-block; cursor: pointer; letter-spacing: .5px; padding: 4px 12px; user-select: none; text-transform: uppercase; transition: .1s;}
.fm__header:hover {background: var(--theme_default_postbg); border-color: var(--theme_default_border); box-shadow: 0 1px 3px 0 var(--theme_shadow);}
.fm__sub {padding-left: 5px;}
.fm__sub a {border: 1px solid transparent; color: var(--theme_default_text); display: block; padding: 3px 6px; border-radius: 3px;}
.fm__sub a:hover, .fm__sub a:focus {background: var(--theme_default_postbg); border-color: var(--theme_default_border); box-shadow: 0 1px 3px 0 var(--theme_shadow);}

#hideFm {display: none;}
.cntnt__left {background: var(--theme_default_postbg); box-shadow: 0 0 0 1px var(--theme_default_border), 1px 0 6px var(--theme_shadow2); display: block !important; padding: 0 5px 0 0; width: 335px; top: 0; left: -345px; position: fixed; z-index: 200; transition: .3s;}
.cntnt__left:hover {left: 35px;}
.cntnt__left_sticky {scrollbar-color: var(--theme_scroll) transparent; scrollbar-width: thin;}
.cntnt__left:before {background: var(--theme_default_postbg); box-sizing: border-box; color: var(--theme_default_alttext2); content: "\f0c9"; font: 20px/1 FontAwesome; position: fixed; top: 0; left: 0; bottom: 75%; padding: 9px; z-index: 201; width: 35px;}
.cntnt__left:hover::before {color: var(--theme_default_alttext);}

.fm {margin: 5px;}
.fm__header {background: none; border-radius: 3px; font-weight: bold; font-size: 1.1em; color: var(--theme_default_text); display: inline-block; cursor: pointer; letter-spacing: .5px; padding: 4px 12px; margin-left: 5px; user-select: none; text-transform: uppercase; transition: .05s;}
.fm__header:hover {background: var(--theme_default_postbg2);}
.fm__sub {padding-left: 5px;}
.fm__sub a {color: var(--theme_default_text); display: block; padding: 3px 9px; border-radius: 3px; transition: .05s;}
.fm__sub a:hover {background: var(--theme_default_postbg2);}

.post__file-attr a {opacity: 0; height: 15px; transition: .1s;}
.post__filezise {display: block; margin-top: -16px; transition: .1s;}

figure {position: relative;}
figure:hover > .post__file-attr a {opacity: 1;}
figure:hover > .post__file-attr .post__filezise {color: transparent; visibility: hidden; }
