style.css:
@-moz-document domain("minkara.carview.co.jp") {
/*======================================
  minkara carview compact mix
  ======================================*/
/*--- unwanteds ---*/
#insertitem, .insertitem,
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: inherit !important; font-weight: inherit !important;
 line-height: 1.4em !important;
 }

dummy#dummy.dummy {display: none;}
}

@-moz-document regexp("https?:\\/\\/minkara\\.carview\\.co\\.jp\\/userid\\/\\d+.*") {
/*==========
  マイページ
  ==========*/

#AdPrimeDisplay,
dummy1#dummy1.dummy1 {display: none !important;}


/*--- header ---*/
.mypage_header {width: auto;}
.mypage_header .head_contents {margin-bottom: 0;}
.head_contents {width: auto; margin: 0; font-family: initial; font-size: initial;}
.head_top {float: right; width: auto; margin: 0;}
.head_top .head_top_block {height: auto;}
.head_top .head_top_block .head_menu {height: auto;}
.head_bottom {float: left;}
.head_left_block {margin: 0;}
.head_search_block {width: auto; padding: 0;}


/*--- foooter ---*/


/*--- main ----*/
#container {clear: both; width: auto; margin: 0; padding: 0; border-top: 1px dotted gray;}

#container .colleft {width: 160px;}
#container .colright {width: 300px; padding: 0;}
#container .colcenter {width: calc(100% - 300px - 160px); margin: 0; padding: 0 0.5em 0 0.5em;}

/* 燃費一覧 (2列) */
#container .colcenter2 {width: calc(100% - 160px); margin: 0; padding: 0 0 0 0.5em;}
#container .colcenter2>* {width: auto; height: auto; margin: 0; padding: 0;}

/* 燃費投稿ページ */
div#NewMinkaraHeader div#MinkaraHeader {height: auto;} /* header */
div#HeaderBlock {width: auto;}
.head_top {height: auto; margin: 0;}
.head_bottom {width: auto; height: auto; margin-bottom: 0;}
.head_left_block {width: auto;}
.head_right_block {width: auto; height: auto;}
div#BodyBlock {width: auto;}
div#fuelEdit-page-editBlock .formTable .FuelAmount-area .textArea,
div#fuelEdit-page-editBlock .formTable .FuelPrice-area .textArea,
div#fuelEdit-page-editBlock .formTable .Mileage-area .textArea,
div#fuelEdit-page-editBlock .formTable .Odometer-area .textArea {width: auto;}
.formEditContainer .EditContentsArea-Contents .formTable tr .textArea {height: auto; padding: 0.1em;}

/* 燃費投稿ページ 2019-03-29 */
body #MinkaraBody #BodyBlock {width: auto; padding: 0;}
div.nolink_layout {height: auto;}
body #MinkaraBody #BodyBlock #Path #BlogChgView {margin-bottom: 0;}
body #MinkaraBody #BodyBlock .headImg {padding: 0; height: auto;}
body #MinkaraBody #BodyBlock #BodyBlockMain {width: calc( 100% - 300px); margin-left: 0; padding-bottom: 0;} /* added margin-left 0 2020年2月15日 */


/* ユーザートップページ */
.stream_block, .stream_block *:not(script) {width: auto; margin: 0; padding: 0;}
.stream_block .head_block {width: auto; margin-bottom: 0;}
.stream_block .head_block .text_block {width: auto; height: auto;}
.stream_block .content_block {width: auto;}
.stream_block .etc_block {width: auto; height: auto; padding: 0;}

/* 愛車紹介 愛車一覧 */
table#UserCarPartsCategory {width: auto; margin: 0;}
table#UserCarPartsCategory ul {margin: 0;}
div.contentsHeader {height: auto; background: none;}
.userCarListTitle {float: left; padding: 0 0 0 20px;}
div#MinkaraBody #BodyBlock #BodyBlockMain .contents .CarLikeList {margin-bottom: 0;}
div#MinkaraBody #BodyBlock .likeTitle .postLikeCount {height: auto;}
div#MinkaraBody #BodyBlock #BodyBlockMain .contents .userCarListPhoto {margin-bottom: 0;}
div.userCarListPhoto {clear: both; width: 150px; height: auto;}
div#MinkaraBody #BodyBlock #BodyBlockMain .contents .userCarListPhoto .carreviewlink {width: auto; margin-top: 0;}
div#MinkaraBody #BodyBlock #BodyBlockMain .contents .userCarListMemo {margin-bottom: 0;}
div.userCarListMemo {float: left; width: calc(100% - 150px); padding-left: 0.5em; text-indent: 1em;}
div.userCarListEntryDate {padding: 0;}


/* 愛車紹介 記事 (2列) */
div#BodyBlockHeader {float: none; width: auto; height: auto; margin: 0; background-size: cover;}
div#BodyBlockHeader .HeaderBlock {width: auto; margin: 0; padding: 0;}
div#BodyBlockMain {width: calc(100% - 300px); min-height: auto; margin-left: 0; border-top: 1px dotted gray;} /* added margin-left 0 2020年2月15日 */
div#BodyBlockSide {width: 300px; margin: 0; border-left: 1px dotted gray;}
div#BodyBlockMain>* {margin: 0; padding: 0 0.5em;}
div#BodyBlockMain [class^="contents"] {width: auto; margin: 0; padding: 0;}
div.contents {padding: 0; background-image: none;}
.txtLikeCountStyle {color: inherit; margin: 0;}
.likeCommentClipBlock {margin: 0; padding: 0;}
.likeCommentClipArea div {background-color: white;}

/* ヤフーショッピング ヤフオク カタログ */
#yshp_carousel, #yauc_carousel {width: calc(100% - 58px);}
#yauc_container {margin: 0;}
/* 20200905 */
body #MinkaraBody #BodyBlock #BodyBlockMain .contents #yshp_container .grid,
body #MinkaraBody #BodyBlock #BodyBlockMain .contents #yauc_container .grid,
#yshp_container.carousel-style #yshp_carousel
 {width: 100%;}

/* 関連記事 */
table.pickupRelation tbody * {width: auto; margin: 0; padding: 0;}

/* プロファイル トップ */
ul#MainNav {height: auto; margin: 0;}
h1.HandleName {margin: 0;}
div#BodyBlockProfileSide {padding-bottom: 0; margin-bottom: 0;}
div#BodyBlockProfileMain {width: calc(100% - 300px);}
div#DoCommand {margin-bottom: 0;}
.profileCategoryTitle {margin: 0;}
div.newDocumentAll {margin: 0;}

table.newDocument td.entryDate {width: 8em;}

/* blog text remove line break */
div.blogMemo br {display: none;}

/* tweet */
.mynani {width: auto;}
.now_nanishiteru_main {width: auto; margin: 0;}
.speech_balloon_box {width: auto; margin-bottom: 0;}
.sb_usercomment {width: auto; float: left; background: none;}
[class^="sb_usercomment_"] {width: auto !important; background: none !important; padding-top: 0 !important; padding-bottom: 0 !important;}
[class^="sb_usercomment_"] br {display: none !important;}


}

@-moz-document regexp("https?:\\/\\/minkara\\.carview\\.co\\.jp\\/search\\/.+") {
/*
  検索結果 2020年2月15日
*/

/* unwanted */
#adInboard,
dummy3#dummy3.dummy3 {display: none !important;}


.topsidebannercontainer {width: auto;}
#container {width: auto; padding: 0;}

/*--- header ---*/
.head_contents .head_top {padding: 0;}
.head_top {float: right; width: auto; margin: 0;}
.head_contents {width: auto; margin: 0;}
.head_bottom {float: left; width: auto;}


/*--- foooter ---*/


/*--- main ----*/
#content {padding: 0;}
#main2col {width: calc(100% - 300px);}
#sub {width: 300px; padding-top: 0;}

#main {width: auto; padding-top: 0;}
#main>* {width: auto; padding: 0 0.5em;}

.searchArea {margin-bottom: 0; padding: 0;}
.pageTitle {padding: 0;}
.searchResultArea .contentsHeader {padding: 0;}
.searchResultArea .itemlist .item li article {padding-top: 0; padding-bottom: 0;}
.searchResultArea .itemlist .item li.withfigure article .textarea {width: calc(100% - 220px);}
}

