style.css:
@-moz-document domain("mail.google.com") {
* {
    /* good bye roboto :-) */
    font-family: arial, sans-serif !important;
    letter-spacing: 0 !important;
}

.zA {
    /* inbox rows */
	padding-top: 8px;
	padding-bottom: 8px;
}

.zA * {
    /* inbox rows */
	font-size: 13px !important;
}

.xY .av {
    /* inbox labels */
	font-size: 11px !important;
}
}
