#qr-CommentToolbar span, #CommentToolbar span {height: 24px;}
.markup {padding: 4px 5px;}
.m-bold:before {font:  18px/24px FontAwesome; height: 24px; width: 26px;}
.m-italic:before {font: 18px/24px FontAwesome; height: 24px; width: 26px;}
.m-quote:before {font: 18px/24px FontAwesome; height: 24px; width: 26px;}
.m-underline:before {font: 18px/24px FontAwesome; height: 24px; width: 26px;}
.m-overline:before {font: bold 18px/24px serif; height: 24px; width: 26px;}
.m-spoiler {width: 35px;}
.m-spoiler img {height: 24px; width: 35px;}
.m-spoiler:before {font-size: 18px; line-height: 22px; height: 20px; width: 35px;}
.m-strike:before {font: 18px/24px FontAwesome; height: 24px; width: 26px;}
.m-sup:before {font: 18px/24px FontAwesome; height: 24px; width: 26px;}
.m-sub:before {font: 18px/24px FontAwesome; height: 24px; width: 26px;}
.postform__raw.options {display: block !important;}

input[type="checkbox"] {height: 20px; width: 20px;}
input[type="checkbox"]::before {height: 20px; width: 20px;}
input[type="checkbox"]::after {bottom: 20px; font: 15px/20px FontAwesome; height: 20px; width: 20px;}

.filer__drag-area {font-size: 18px; height: 46px;}

/* Quick Reply */
.qr {border: none; border-radius: 0; font-size: 1em; left: 0 !important; top: 0 !important; padding: 5px; width: 100%; height: calc(100vh); overflow: auto; z-index: 983;}
.qr__header {text-transform: uppercase; letter-spacing: 1px; font-size: 16px; padding: 6px 0;}
.qr__close {padding: 10px 15px; margin: -10px;}
.qr__close::before {font: 18px/1 FontAwesome;}

.qr__footer {font-size: 14px;}

/* Page */
.warningbox {top: 0; left: 0; bottom: 0; right: 0; width: unset; margin-top: 0; margin-left: 0; border: none; border-radius: 0;}

#fullscreen-container:after {font-size: .8em; opacity: 1;}

.na {height: 40px; width: 50px; padding: 10px; z-index: 970;}
.na__icon {fill: var(--theme_default_text) !important; position: unset;}
.na_type_up {display: block !important; top: unset; bottom: 0; left: 50px;}
.na_type_up:hover, .na_type_up:active, .na_type_down:hover, .na_type_down:active {background: var(--theme_default_btnbghover);}
.na_type_up .na__icon {padding: 4px 0 10px; height: 25px; width: 30px;}
.na_type_down {display: block !important; bottom: 0; left: 100px;}
.na_type_down .na__icon {padding: 10px 0 4px; height: 25px; width: 30px;}

.alert {left: 4px; right: unset;}

.cntnt {margin-top: 5px;}
.cntnt__right {padding: 0 0 100px 0; flex: 1 100%; -webkit-flex: 1 100%;}

.button-mob {border-bottom-width: 2px; border-radius: 4px; transition: .1s;}
.button-mob:hover {background-color: var(--theme_default_btnbghover);}

.thread-nav {display: block !important; font-size: 0;}
.search {background: var(--theme_default_postbg); border-bottom: 2px solid var(--theme_default_border); box-shadow: 0 1px 3px 0 var(--theme_default_shadow); display: block !important; margin-right: 0; padding: 5px; position: absolute; top: -40px; left: 0; width: 100%; transition: .1s; z-index: 100;}
.search:hover, .search:focus, .search:focus-within {top: 0;}
.search::after {color: var(--theme_default_alttext); content: "\f002"; font: 20px/36px FontAwesome; position: absolute; top: 40px; right: 0; width: 36px; text-align: center; transition: .1s;}
.search:hover::after, .search:focus::after, .search:focus-within::after {top: 0;}
input[name="find"] {font-size: 14px;}
.thread-nav__inner a {display: none;}

.thread-nav__stats {font-size: 15px; position: absolute; top: 5px; left: 10px;}
a[id="bottom"] + .thread-nav, .button-mob[href="#bottom"], .button-mob[href="#top"], .thread-nav-mob:nth-of-type(4) > a.button-mob:nth-of-type(4) {display: none !important;}

a[onclick*="updateThread(); return false;"] {background-color: var(--theme_default_postbg); border: none; border-radius: 0; box-shadow: none; color: var(--theme_default_text); font-size: 0; padding: 0px 20px !important; position: fixed; left: 200px; bottom: 0; z-index: 977; margin: 0; height: 40px; width: 80px; text-align: left !important;}
a[onclick*="updateThread(); return false;"]::before {display: none;}
a[onclick*="updateThread(); return false;"]::after {background: none !important; color: var(--theme_default_text) !important; content: "\f021"; font: 18px/38px FontAwesome; position: unset; padding: 0;}
.autorefresh-checkbox {position: fixed; left: 245px; bottom: 11px; z-index: 978;}
.autorefresh-checkbox[checked="checked"] + .autorefresh-countdown {background: var(--theme_default_postbg); border: 1px solid var(--theme_default_border); border-radius: 4px; box-shadow: 0 1px 6px 0 var(--theme_default_shadow); font-size: 13px; padding: 1px 5px; position: fixed; bottom: 45px; left: 200px; z-index: 978;}

/* Thread */
.post {padding: 2px;}
.post_type_oppost {border-top: 1px solid var(--theme_default_border); border-bottom: 2px solid var(--theme_default_border);}

.thread__post {margin: 6px 0; padding: 0 6px;}
.post_type_reply .post__details, .post .post-details {background: none; opacity: 1; padding: 2px;}
.post__ophui {padding: 0 2px;}
.post__btn.desktop {display: inline-block !important;}
.post__btn {float: left;}
.post__detailpart {padding-left: 2px;}
.post__number {margin-left: 8px;}

.modal {padding: 6px 0;}
.modal a {font-size: 15px; padding: 6px 14px;}

.post__filezise {margin-top: 0; visibility: visible !important;}
.post__image-link {float: none;}
.post__images_type_single + .post__message {margin: 0 1em 0.6em;}

.post__refmap {padding-left: 8px;}

/* Footer */
.bb {border-radius: 6px 6px 0 0; box-shadow: 0 0 0 1px var(--theme_default_bordershadow), 0 -2px 6px 0 var(--theme_default_shadow2); display: block !important; font-size: 1em; top: unset; left: 0; right: 0; bottom: -600px; transition: background .1s; z-index: 979;}
.bb::before {content: "\f005"; cursor: pointer; font: 18px/38px FontAwesome; padding: 0; position: fixed; left: 150px; bottom: 0; margin: 0; height: 39px; width: 50px; text-align: center; transition: .1s;}
.bb:hover::before {background-color: var(--theme_default_btnbghover);}
.bb:hover, .bb:focus, .bb:focus-within {bottom: 40px;}
.bb__panels {display: block !important;}
.bb__header, .bb-header-text {font-weight: bold; font-size: 16px; letter-spacing: 1px; text-transform: uppercase;}
.bb__update, .fav__fa_type_remove, .fav__fa_type_update {width: 16px; height: 16px;}
.bb__header::before {display: none;}
.bb__bstitle {max-width: 250px;}

.footer__info {display: none;}
}
}

