style.css:
@-moz-document domain("app.ninchanese.com") {
#top {
    background-color: #555;
}
#profile-avatar {
    background-color: #555;
}
#notif-icon {
    background-color: #555;
}

#navigation a.nav-main-entry {
    color: #ddd;
}

#ex-hdr, #ex-content {
    background-color: #555;
    color: #ddd;
}

.chinois {
	color: #ddd;
}

#response-container {
    background-color: #555;
}

.wi input.active {
	color: #fff;
    background-color: #777;
}

.game-finished-points {
	background-color: #333;
}

#game-finished section.game-finished-scoreboard {
	background-color: #555;
}

#user-detail {
	background-color: #555;
    color: #ddd;
}

#user-friends {
	background-color: #555;
    color: #ddd;
}

.bg-highlight {
	background-color: #333;
}

.bg-white {
 background:#333!important;
}

.bg-grey-highlight {
	background-color: #555;
}

#leaderboard-focus, .word-list .word-list-entry:nth-child(2n) {
	background-color: #555;
}

#stats, #challenge-cards {
	background-color: #555;
}

.stage-highlight .stage-preview {
	background-color: #777;
}

.stage-list .stage {
	background-color: #777;
}

.game-dialogue-listening #response-container {
	background-color: #555;
}

#response-container, #response-container>div {
	background-color: #555;
}

#footer {
    background-color: #555;
}

.main {
    background-color: #000;
}

.wrapper {
    background-color: #000;
}

.no-js {
    background-color: #000;
}
}
