style.css:
@-moz-document domain('cesar.org.uk'), url-prefix('http://cesar.org.uk/cesar2') {

body, table, td, p, li {
    background-color: #333 !important;
    font-size: 1em;
    color: #aaa !important;
}
h1 {
    color: #d9cab5;
}
a {
    color: #c0ac92 !important;
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

/* Our img for the title */
img[width="400"] {
    display: block;
    box-sizing: border-box;
    background: url(http://cethefi.org/img/cesar_titre.png) no-repeat;
    width: 431px;
    height: 113px;
    padding-left: 431px;
    margin: 20px;
}

}
