style.css:
@-moz-document regexp("https?://(www)?.?youtube.com.*") {

    :root {
        --yt-spec-brand-button-background: /*[[color]]*/
        !important;
        --yt-main-app-background-tmp: /*[[bg]]*/
        !important;
        --yt-spec-general-background-a: /*[[bg]]*/
        !important;
        --yt-spec-general-background-b: /*[[bg]]*/
        !important;
        --yt-subscribe-button-text-color: /*[[bg]]*/
        !important;
        --yt-spec-icon-active-button-link: /*[[color]]*/
        !important;
        --yt-guide-entry-hover-background-color: /*[[color]]*/
        !important;
        --yt-notification-button-bubble_-_color: /*[[bg]]*/
        !important;
        --yt-spec-themed-blue: /*[[color]]*/
        !important;
        --yt-spec-call-to-action: /*[[color]]*/
        !important;
        /*
        --ytd-searchbox-legacy-border-color: /*[[color]] !important;
		*/
        --ytd-searchbox-legacy-button-hover-color: /*[[color]]*/
        !important;
        --paper-spinner-color: /*[[color]]*/;
        --paper-input-container-focus-color: /*[[color]]*/
        !important;
        --yt-spec-icon-active-other: /*[[color]]*/
        !important;
        --yt-spec-text-primary-inverse: /*[[bg]]*/
        !important;
        --yt-spec-badge-chip-background: /*[[bg]]*/
        !important;
        /*
        --yt-spec-text-primary: /*[[color]] !important;
        --yt-spec-10-percent-layer: /*[[color]] !important;
        */
        --yt-spec-brand-background-solid: /*[[bg]]*/
        !important;
        --ytd-author-comment-badge-icon-color: /*[[bg]]*/
        !important;
        --ytd-author-comment-badge-background-color: /*[[color]]*/
        !important;
        --ytd-author-comment-badge-name-color: /*[[bg]]*/
        !important;
        /* Live chat settings */
        --yt-live-chat-header-background-color: /*[[bg]]*/
        !important;
        --yt-live-chat-background-color: /*[[bg]]*/
        !important;
        --yt-live-chat-vem-background-color: /*[[bg]]*/
        !important;
        --yt-live-chat-action-panel-background-color: /*[[bg]]*/
        !important;
        --yt-spec-suggested-action: transparent !important;
        --paper-tabs-selection-bar-color: /*[[color]]*/
        !important;
    }

    html,
    body {
        background: /*[[bg]]*/
        !important;
    }

    ytd-app {
        background: /*[[bg]]*/;
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
        color: /*[[color]]*/
        !important;
    }

    h4.macro-markers.style-scope.ytd-macro-markers-list-item-renderer {
        color: var(--yt-spec-text-primary) !important;
    }

    #header.ytd-engagement-panel-title-header-renderer {
        border-bottom: solid 1px/*[[color]]*/;
    }

    a {
        color: /*[[color]]*/
        !important;
    }

    /*[[transparency-toggle]]*/
    /* YouTube main logo */
    yt-icon#logo-icon svg g g:nth-child(1) path:nth-child(1),
    yt-icon#logo-icon svg g:nth-child(1) path:nth-child(1) {
        fill: /*[[color]]*/;
    }

    yt-icon#logo-icon svg g g:nth-child(1) path:nth-child(2),
    yt-icon#logo-icon svg g:nth-child(1) path:nth-child(2) {
        fill: /*[[bg]]*/;
    }

    yt-icon#logo-icon svg g g#youtube-paths path {
        fill: white;
    }

    yt-icon#logo-icon svg g g#youtube-red-paths path {
        fill: white;
    }

    #voice-search-button yt-icon-shape svg,
    #voice-search-button yt-icon svg {
        color: /*[[color]]*/
        !important;
    }

    #voice-search-button {
        background: none !important;
    }

    #voice-search-button:hover yt-icon-shape svg,
    #voice-search-button:hover yt-icon svg {
        color: /*[[bg]]*/
        !important;
    }

    #voice-search-button:hover {
        background: /*[[color]]*/
        !important;
    }

    #microphone-circle.ytd-voice-search-dialog-renderer,
    #microphone.ytd-voice-search-dialog-renderer[state=try-again] #microphone-circle.ytd-voice-search-dialog-renderer {
        background-color: /*[[color]]*/
        !important;
    }

    #microphone-circle.ytd-voice-search-dialog-renderer yt-icon-shape svg,
    #microphone.ytd-voice-search-dialog-renderer[state=try-again] #microphone-circle.ytd-voice-search-dialog-renderer yt-icon-shape svg {
        color: /*[[bg]]*/
        !important;
    }

    #microphone-circle.ytd-voice-search-dialog-renderer yt-icon svg,
    #microphone.ytd-voice-search-dialog-renderer[state=try-again] #microphone-circle.ytd-voice-search-dialog-renderer yt-icon svg {
        color: /*[[bg]]*/
        !important;
    }

    .ytp-ce-expanding-overlay-background {
        background-color: /*[[bg]]*/;
    }

    .ytp-ce-website-title {
        color: var(--yt-spec-text-primary);
    }

    #video-title,
    .ytd-video-primary-info-renderer {
        color: var(--yt-spec-text-primary) !important;
    }

    #video-title:hover {
        color: /*[[color]]*/
        !important;
    }

    #above-the-fold h1 yt-formatted-string {
        color: var(--yt-spec-text-primary) !important;
    }

    /* Shorts */
    .progress-bar-played.ytd-progress-bar-line {
        background-color: /*[[color]]*/;
    }

    h2.title .ytd-reel-player-header-renderer {
        color: #eee !important;
    }

    .ytd-reel-player-header-renderer yt-button-shape.style-scope.ytd-subscribe-button-renderer button {
        background-color: /*[[bg]]*/;
        color: /*[[color]]*/;
        border: solid 1px/*[[color]]*/;
    }

    /* Feed category suggestions */
    yt-chip-cloud-chip-renderer {
        font-weight: bold;
    }

    ytd-feed-filter-chip-bar-renderer svg,
    .yt-chip-cloud-renderer .yt-spec-button-shape-next svg {
        fill: /*[[color]]*/;
    }

    ytd-feed-filter-chip-bar-renderer[darker-dark-theme] #left-arrow-button.ytd-feed-filter-chip-bar-renderer,
    ytd-feed-filter-chip-bar-renderer[darker-dark-theme] #right-arrow-button.ytd-feed-filter-chip-bar-renderer {
        background-color: /*[[bg]]*/;
    }

    ytd-feed-filter-chip-bar-renderer[darker-dark-theme][is-dark-theme] #right-arrow.ytd-feed-filter-chip-bar-renderer:before {
        background: linear-gradient(to left, /*[[bg]]*/
        20%, rgba(33, 33, 33, 0) 80%) !important;
    }

    ytd-feed-filter-chip-bar-renderer[darker-dark-theme][is-dark-theme] #left-arrow.ytd-feed-filter-chip-bar-renderer:after {
        background: linear-gradient(to right, /*[[bg]]*/
        20%, rgba(33, 33, 33, 0) 80%) !important;
    }

    yt-chip-cloud-renderer[darker-dark-theme] #left-arrow-button.yt-chip-cloud-renderer,
    yt-chip-cloud-renderer[darker-dark-theme] #right-arrow-button.yt-chip-cloud-renderer {
        background-color: /*[[bg]]*/;
    }

    yt-chip-cloud-renderer[darker-dark-theme][is-dark-theme] #right-arrow.yt-chip-cloud-renderer:before {
        background: linear-gradient(to left, /*[[bg]]*/
        10%, rgba(24, 24, 24, 0) 90%) !important;
    }

    yt-chip-cloud-renderer[darker-dark-theme][is-dark-theme] #left-arrow.yt-chip-cloud-renderer:after {
        background: linear-gradient(to right, /*[[bg]]*/
        10%, rgba(24, 24, 24, 0) 90%) !important;
    }

    yt-chip-cloud-chip-renderer.style-scope.iron-selected {
        background-color: /*[[color]]*/;
    }

    yt-chip-cloud-chip-renderer.style-scope.iron-selected:focus {
        background-color: /*[[color]]*/;
    }

    yt-chip-cloud-chip-renderer.style-scope.iron-selected:hover {
        background-color: /*[[bg]]*/;
        color: /*[[color]]*/;
        border: solid 1px/*[[color]]*/;
    }

    #time.ytd-macro-markers-list-item-renderer {
        background-color: /*[[color]]*/
        !important;
        color: /*[[bg]]*/
        !important;
    }

    ytd-transcript-segment-renderer.active .segment.ytd-transcript-segment-renderer,
    ytd-transcript-segment-renderer:hover .segment.ytd-transcript-segment-renderer {
        background-color: /*[[color]]*/;
    }

    ytd-transcript-segment-renderer.active .segment.ytd-transcript-segment-renderer .segment-text.ytd-transcript-segment-renderer,
    ytd-transcript-segment-renderer:hover .segment.ytd-transcript-segment-renderer .segment-text.ytd-transcript-segment-renderer {
        color: /*[[bg]]*/;
    }

    ytd-transcript-segment-renderer.active .segment.ytd-transcript-segment-renderer .segment-text.ytd-transcript-segment-renderer {
        font-weight: bolder;
    }

    ytd-transcript-segment-renderer.active .segment.ytd-transcript-segment-renderer .segment-timestamp,
    ytd-transcript-segment-renderer:hover .segment.ytd-transcript-segment-renderer .segment-timestamp {
        color: /*[[bg]]*/;
    }

    ytd-transcript-segment-renderer.active .segment.ytd-transcript-segment-renderer .segment-timestamp {
        font-weight: bolder;
    }

    canvas.ytd-app,
    caption.ytd-app,
    center.ytd-app,
    cite.ytd-app,
    code.ytd-app,
    dd.ytd-app,
    del.ytd-app,
    dfn.ytd-app,
    div.ytd-app,
    dl.ytd-app,
    dt.ytd-app,
    em.ytd-app,
    embed.ytd-app,
    fieldset.ytd-app,
    font.ytd-app,
    form.ytd-app,
    h1.ytd-app,
    h2.ytd-app,
    h3.ytd-app,
    h4.ytd-app,
    h5.ytd-app,
    h6.ytd-app,
    hr.ytd-app,
    i.ytd-app,
    iframe.ytd-app,
    img.ytd-app,
    ins.ytd-app,
    kbd.ytd-app,
    label.ytd-app,
    legend.ytd-app,
    li.ytd-app,
    menu.ytd-app,
    object.ytd-app,
    ol.ytd-app,
    p.ytd-app,
    pre.ytd-app,
    q.ytd-app,
    s.ytd-app,
    samp.ytd-app,
    small.ytd-app,
    span.ytd-app,
    strike.ytd-app,
    strong.ytd-app,
    sub.ytd-app,
    sup.ytd-app,
    table.ytd-app,
    tbody.ytd-app,
    td.ytd-app,
    tfoot.ytd-app,
    th.ytd-app,
    thead.ytd-app,
    tr.ytd-app,
    tt.ytd-app,
    u.ytd-app,
    ul.ytd-app,
    var.ytd-app {
        background: /*[[bg]]*/
        !important;
    }

    #container.ytd-searchbox {
        caret-color: /*[[color]]*/
        !important;
    }

    ytd-searchbox[mode="legacy"] #container.ytd-searchbox,
    ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox {
        background-color: /*[[bg]]*/;
        border-color: /*[[color]]*/;
    }

    ytd-searchbox[mode="legacy"][has-focus] #container.ytd-searchbox,
    ytd-searchbox[mode="legacy-centered"][has-focus] #container.ytd-searchbox {
        background-color: /*[[bg]]*/;
        border-color: /*[[color]]*/;
    }

    .sbsb_a {
        background: none;
        color: #ccc !important;
    }

    .gsfs {
        color: #ccc;
    }

    .sbdd_b {
        border-color: /*[[bg]]*/;
    }

    .sbfl_a {
        color: /*[[color]]*/;
    }

    .sdfl_a:hover,
    .sdfl_a:active {
        color: /*[[bg]]*/;
    }

    .sbfl_b {
        background: none !important;
    }

    .sbfl_a:hover .sbfl_b,
    .sbfl_a:active .sbfl_b {
        background: /*[[color]]*/;
    }

    .sbsb_c:hover,
    .sbsb_c:active {
        background: /*[[color]]*/;
        color: /*[[bg]]*/;
    }

    .sbpqs_a {
        color: /*[[color]]*/;
    }

    .sbsb_c:hover .sbpqs_a,
    .sbsb_c:active .sbpqs_a,
    .sbsb_c:hover .fr,
    .sbsb_c:active .fr {
        color: /*[[bg]]*/;
    }

    li.sbsb_c.gsfs.sbsb_d {
        background: /*[[color]]*/;
        color: /*[[bg]]*/;
    }

    .ghp-dialog,
    .ghp-header {
        background: /*[[bg]]*/;
    }

    #progress.yt-page-navigation-progress {
        background-color: /*[[color]]*/;
    }

    yt-page-navigation-progress {
        background-color: /*[[bg]]*/;
    }

    #toast {
        background: /*[[color]]*/;
        color: /*[[bg]]*/;
        border: double 1px/*[[bg]]*/;
    }

    tp-yt-paper-toast .yt-notification-action-renderer button {
        background-color: transparent !important;
        color: /*[[bg]]*/
        !important;
    }

    tp-yt-paper-toast .yt-notification-action-renderer button:hover {
        text-decoration: underline;
    }

    yt-chip-cloud-chip-renderer[chip-style="STYLE_DEFAULT"],
    yt-chip-cloud-chip-renderer[chip-style="STYLE_HOME_FILTER"] {
        border: 1px solid/*[[color]]*/;
        color: /*[[color]]*/;
        background: /*[[bg]]*/;
    }

    .masthead-finish {
        border-bottom: 1px solid/*[[color]]*/
        !important;
    }

    .ytd-masthead #logo-icon-container svg g g path {
        fill: /*[[color]]*/
        !important;
    }

    .ytd-masthead #logo-icon-container svg g g polygon {
        fill: /*[[bg]]*/
        !important;
    }

    #guide-content #logo #logo-icon-container svg g g path {
        fill: /*[[color]]*/
        !important;
    }

    #guide-content #logo #logo-icon-container svg g g polygon {
        fill: /*[[bg]]*/
        !important;
    }

    .yt-spec-icon-badge-shape--type-notification .yt-spec-icon-badge-shape__badge {
        background-color: /*[[color]]*/;
        color: /*[[bg]]*/;
    }

    #notification-count.ytd-notification-topbar-button-renderer {
        color: /*[[bg]]*/;
        font-weight: bold;
        font-size: 12px;
    }

    .unseen #new .ytd-notification-renderer,
    #new .ytd-notification-renderer {
        background-color: /*[[color]]*/
        !important;
    }

    #menu .ytd-menu-renderer svg .yt-icon {
        fill: /*[[color]]*/;
    }

    .yt-multi-page-menu-section-renderer:hover #menu .ytd-menu-renderer svg .yt-icon {
        fill: /*[[bg]]*/;
    }

    button.yt-icon-button {
        color: /*[[color]]*/;
    }

    ytd-notification-renderer #new.ytd-notification-renderer {
        background-color: /*[[color]]*/;
    }

    ytd-notification-renderer:hover #new.ytd-notification-renderer {
        background-color: /*[[bg]]*/;
    }

    #items.yt-multi-page-menu-section-renderer > .yt-multi-page-menu-section-renderer:hover .text {
        color: /*[[bg]]*/;
        font-weight: bolder !important;
    }

    #items.yt-multi-page-menu-section-renderer > .yt-multi-page-menu-section-renderer:hover .metadata {
        color: /*[[bg]]*/;
        opacity: 0.7;
    }

    #items.yt-multi-page-menu-section-renderer > .yt-multi-page-menu-section-renderer:hover {
        background: /*[[color]]*/
        !important;
    }

    #dismissable #subscribe-button .ytd-button-renderer #text,
    .ytd-background-promo-renderer .ytd-button-renderer #text {
        color: /*[[bg]]*/;
    }

    #guide-button.ytd-app {
        color: /*[[color]]*/;
    }

    .style-text[is-icon-button],
    .style-default[is-icon-button],
    .style-opacity[is-icon-button] {
        color: /*[[color]]*/;
    }

    ytd-guide-entry-renderer[is-guide-refresh] .guide-icon.ytd-guide-entry-renderer {
        color: /*[[color]]*/;
    }

    ytd-guide-entry-renderer[is-guide-refresh][is-primary] .title.ytd-guide-entry-renderer {
        color: #eee;
    }

    ytd-guide-entry-renderer[is-guide-refresh][is-primary]:hover .title.ytd-guide-entry-renderer {
        color: /*[[bg]]*/;
    }

    .title.ytd-guide-entry-renderer {
        color: #eee;
    }

    .ytd-compact-link-renderer:hover,
    .ytd-compact-link-renderer:hover yt-icon-shape svg {
        color: /*[[bg]]*/;
        background: /*[[color]]*/;
    }

    .ytd-toggle-theme-compact-link-renderer:hover svg .yt-icon {
        color: /*[[bg]]*/;
    }

    .ytd-toggle-theme-compact-link-renderer:hover {
        color: /*[[bg]]*/;
        background: /*[[color]]*/;
    }

    .guide-entry-badge .yt-icon {
        color: /*[[color]]*/
        !important;
    }

    ytd-guide-entry-renderer[active] {
        background: /*[[bg]]*/;
        border-left: solid 4px/*[[color]]*/;
    }

    ytd-guide-entry-renderer[active]:hover .title.ytd-guide-entry-renderer,
    ytd-guide-entry-renderer[is-guide-refresh][active]:hover .title.ytd-guide-entry-renderer {
        color: /*[[bg]]*/;
    }

    ytd-guide-entry-renderer[active] .guide-icon.ytd-guide-entry-renderer,
    ytd-guide-entry-renderer[is-guide-refresh][active] .guide-icon.ytd-guide-entry-renderer {
        color: /*[[color]]*/;
    }

    .ytd-guide-entry-renderer:hover .guide-entry-badge .yt-icon,
    .ytd-guide-entry-renderer:hover .yt-icon,
    .ytd-guide-entry-renderer:hover .guide-entry-count,
    .ytd-guide-entry-renderer:hover #newness-dot.ytd-guide-entry-renderer {
        color: /*[[bg]]*/
        !important;
    }

    ytd-guide-entry-renderer:hover .title.ytd-guide-entry-renderer {
        color: /*[[bg]]*/;
    }

    .ytd-guide-section-renderer #endpoint:hover {
        color: /*[[bg]]*/
        !important;
        background: /*[[color]]*/
        !important;
    }

    #newness-dot.ytd-guide-entry-renderer {
        background: /*[[color]]*/;
    }

    ytd-guide-entry-renderer:hover #newness-dot.ytd-guide-entry-renderer {
        background: /*[[bg]]*/;
    }

    ytd-mini-guide-renderer,
    ytd-mini-guide-entry-renderer {
        background-color: /*[[bg]]*/;
    }

    .guide-icon.ytd-mini-guide-entry-renderer {
        color: /*[[color]]*/;
    }

    .title.ytd-mini-guide-entry-renderer {
        color: #eee;
    }

    ytd-mini-guide-renderer ytd-mini-guide-entry-renderer #endpoint.yt-simple-endpoint:hover,
    ytd-mini-guide-entry-renderer[aria-selected=true] #endpoint.yt-simple-endpoint {
        background: /*[[color]]*/
        !important;
        color: /*[[bg]]*/
        !important;
    }

    ytd-mini-guide-entry-renderer:hover .guide-icon.ytd-mini-guide-entry-renderer,
    ytd-mini-guide-entry-renderer:hover .title.ytd-mini-guide-entry-renderer {
        color: /*[[bg]]*/;
    }

    ytd-mini-guide-entry-renderer[aria-selected=true] #endpoint span.title {
        color: /*[[bg]]*/;
    }

    ytd-mini-guide-entry-renderer[aria-selected=true] yt-icon.guide-icon svg {
        fill: /*[[bg]]*/;
    }

    yt-icon.ytd-badge-supported-renderer {
        color: /*[[color]]*/;
    }

    yt-icon.ytd-badge-supported-renderer svg path {
        fill: /*[[color]]*/;
    }

    .badge-style-type-premium .yt-icon svg path:nth-child(2) {
        fill: /*[[bg]]*/;
    }

    .badge yt-icon svg {
        fill: /*[[bg]]*/;
    }

    .badge.badge-style-type-simple.ytd-badge-supported-renderer {
        background: /*[[color]]*/;
        color: /*[[bg]]*/;
    }

    #tooltip {
        background-color: /*[[bg]]*/;
        color: /*[[color]]*/;
        font-weight: bold;
    }

    .ytp-cards-teaser .ytp-cards-teaser-text {
        background: /*[[bg]]*/;
        color: /*[[color]]*/;
    }

    /* ytp-svg-fill seems to affect the colors in the play pause icons in video. */
    .ytp-cards-button-icon use path.ytp-svg-fill {
        fill: /*[[bg]]*/;
        stroke: /*[[color]]*/;
    }

    .ytp-cued-thumbnail-overlay button.ytp-large-play-button svg path:nth-last-child(1) {
        fill: /*[[color]]*/;
    }

    .ytp-cued-thumbnail-overlay .ytp-large-play-button-bg {
        fill: /*[[bg]]*/;
    }

    .ytp-cued-thumbnail-overlay:hover button.ytp-large-play-button svg path:nth-last-child(1) {
        fill: /*[[bg]]*/;
    }

    .ytp-cued-thumbnail-overlay:hover .ytp-large-play-button-bg {
        fill: /*[[color]]*/;
    }

    .ytp-spinner-circle {
        border-color: transparent/*[[color]]*/
        transparent/*[[bg]]*/
    }

    .ytd-rich-grid-renderer .ytd-compact-promoted-item-renderer #content #button paper-button#button {
        background: /*[[bg]]*/;
        color: /*[[color]]*/;
        border: 1px solid/*[[color]]*/;
    }

    .ytd-rich-grid-renderer .ytd-compact-promoted-item-renderer #content #button paper-button#button:hover {
        background: /*[[color]]*/;
        color: /*[[bg]]*/;
        border: 1px solid/*[[color]]*/;
    }

    #action-button.ytd-primetime-promo-renderer ytd-button-renderer yt-formatted-string.ytd-button-renderer {
        color: /*[[bg]]*/
        !important;
    }

    .ytd-video-masthead-ad-advertiser-info-renderer .ytd-button-renderer,
    .ytd-action-companion-renderer .ytd-button-renderer {
        color: /*[[color]]*/;
        background: /*[[bg]]*/;
    }

    .ytd-video-masthead-ad-v3-renderer-0 #right-container.ytd-video-masthead-ad-v3-renderer {
        background-color: /*[[bg]]*/;
    }

    .ytvmh-embed-0 {
        background-color: /*[[bg]]*/;
    }

    #block.ytd-action-companion-ad-renderer {
        background: /*[[bg]]*/;
    }

    ytd-ypc-offers-overlay-renderer .offer-button.style-primary yt-formatted-string.ytd-button-renderer {
        color: /*[[bg]]*/;
    }

    .ytp-title-text {
        color: #eee !important;
    }

    #byline {
        color: /*[[color]]*/
        !important;
    }

    #country-code {
        color: /*[[color]]*/
        !important;
    }

    #columns {
        background: var(--yt-main-app-background-tmp) !important;
    }

    ytd-toggle-button-renderer #button.ytd-toggle-button-renderer {
        background-color: /*[[bg]]*/;
    }

    .badge-style-type-live-now.ytd-badge-supported-renderer {
        color: /*[[color]]*/;
        border-color: /*[[color]]*/;
    }

