style.css:
@-moz-document url-prefix("https://browser.yandex.ru/blog"), url-prefix("https://webmaster.yandex.ru/blog"), regexp("https://yandex.([a-z]{2})/blog.*") {
.y-header_club {height: 60px;}
.y-header_club__logo {background-position: 40px 16px;}
.y-header_club__service {padding-top: 10px;}
.y-header_club__content {padding: 14px 40px 0;}
.y-header_club__user-wrapper {padding: 14px 40px 0 0;}

.b-head-blog_yablogs {margin: 25px auto 0;}
.b-head-blog_yablogs__description {font-size: 17px;}
.b-head-blog_yablogs__description p {margin: 10px 0;}
.b-head-blog_yablogs__description span[style="font-size:20px"] {font-size: 17px !important;}
.b-head-blog_yablogs__title {font-size: 46px; margin: 40px 0 25px;}

.b-post_yablogs-minor-tumb__title {font-size: 22px;}
.b-post_yablogs-minor-left__title, .b-post_yablogs-minor-right__title {font-size: 24px;}
.b-post_yablogs-major__title {font-size: 28px;}
.b-post_yablogs-minor-tumb__date, .b-post_yablogs-minor-left__date, .b-post_yablogs-minor-right__date, .b-post_yablogs-major__date {font-size: 15px; font-weight: 400; margin: 6px 0 11px;}
.b-post_yablogs-minor-tumb__text, .b-post_yablogs-minor-left__text, .b-post_yablogs-minor-right__text {font-size: 16px; font-weight: 400;}
.b-post_yablogs-major__text {font-size: 17px; font-weight: 400;}

div[style="background:#ffffff; border:3px solid #ffffff; margin-bottom:-19px; margin-left:-33px; margin-right:-33px; margin-top:-18px; padding:15px 30px"] {padding: 0 !important;}
div[style="display:flex; flex-flow:row wrap; font-weight:300; justify-content:space-around; text-align:center"] {font-weight: 400 !important;}
div[style="display:inline-block; min-width:250px; padding-bottom:40px; vertical-align:top"] {padding-bottom: 15px !important; min-width: 125px !important;}
div[style="display:inline-block; min-width:250px; padding-bottom:40px; vertical-align:top"] figure {margin: 0;}

.b-post-list_yablogs {margin: 20px auto 50px;}
.b-post_yablogs-club {margin: 0 9px 30px; padding-top: 10px;}
.b-post_yablogs-club__info {line-height: 38px;}
.b-post_yablogs-club__author, .b-article_yablogs-not-draft__author {margin-right: 15px;}
.b-post_yablogs-club__more {float: left; padding-top: 0; padding-right: 5px;}
.b-post_yablogs-club__tags {font-size: 15px; font-weight: 400; max-width: 400px;}
.b-post_yablogs-club__tag {padding-left: 7px;}
.b-post_yablogs-club__footer {margin-top: 20px;}

.b-back {padding-right: 12px;}
.b-back__link {font-size: 15px;}
.b-back::after {font-size: 17px;}
.b-article_yablogs-not-draft, .b-article_yablogs-draft {font-weight: 400; padding-top: 25px;}
.b-article_yablogs-not-draft__moderate, .b-article_yablogs-draft__moderate {margin: 0;}
.b-article_yablogs-not-draft__title, .b-article_yablogs-draft__title {font-size: 28px; margin: 18px 0 5px;}
.b-article_yablogs-not-draft__date {font-size: 15px;}
.b-article-text_yablogs-club, .b-article-text_yablogs {font-size: 17px; font-weight: 400; line-height: 24px;}
.b-article_yablogs-not-draft__info {margin-bottom: 5px;}
.b-article-text_yablogs {padding-top: 20px;}
.b-article-text_yablogs .yablogs-marked-text {font-size: 17px; line-height: 28px; padding: 10px 20px;}
.b-article-text_yablogs table td, .b-article-text_yablogs table th {padding: 15px;}
.b-article_yablogs-not-draft__footer {margin: 50px auto 0;}

.b-round-avatar {width: 38px; height: 38px;}
.b-user_yablogs__user-data {margin-left: 5px;}
.b-user_comment__avatar {margin: 0 8px 0 -56px;}

.b-comments {margin: 0;}
.b-comments__count {margin-bottom: 20px;}
.b-comment-add_yablogs {margin-bottom: 30px; padding: 20px 25px;}
.b-comment-list > .b-comment-group_yablogs {margin-bottom: 20px; padding: 15px 20px;}
.b-comment-group_yablogs {font-weight: 400; margin: 10px 0;}
.b-comment-group_yablogs__subgroup {margin-left: 56px;}
.b-comment-group_yablogs__comment {padding: 8px 8px 16px;}
.b-comment-group_yablogs__banned, .b-comment-group_yablogs__removed, .b-comment-group_yablogs__removed-ban {padding: 20px 15px;}
.b-comment-group_yablogs__updated {margin-top: 6px;}

.y-textarea_yablogs {font-weight: 400;}

.b-all-blogs_yablogs__sub-title {font-size: 17px; font-weight: 400;}
.b-all-blogs_yablogs__blog-wrap {padding: 0 10px 1em;}
.b-all-blogs_yablogs__blog {font-size: 17px; font-weight: 400; line-height: 18px;}

.b-social-links_yablogs__title {padding: 20px 0 15px;}
.b-social-links_yablogs__links-wrap {padding-bottom: 10px;}
.b-social-links_yablogs__social-wrap {padding: 12px 14px;}
.b-footer_yablogs {padding: 15px;}
}
