style.css:
@-moz-document domain("meiobit.com") {
.banner,
.col-articles-list,
.elm-button,
.post .wp-caption .wp-caption-text,
.single-page,
body,
html {
	background: #232323!important
}
#breadcrumbs,
.post h1 a,
.post h2,
.post li a,
.post p,
.post p a,
.sectionh3,
.sidebar-featured span,
.single-bit-article .caption h2,
.single-bit-article .caption p.details,
.single-bit-article.bit-sidebar .caption span,
body {
	color: #f5f5f5!important
}
.post blockquote p {
	color: #9e9e9e!important
}
}

@-moz-document regexp("https?://disqus\\.com/embed/comments/.*meiobit.com.*") {
.dropdown-menu li > a:hover,
.tab-conversation.active > a,
body {
	color: #f5f5f5!important
}
.dropdown-toggle:hover,
.label:hover,
.nav-tab > a:hover,
a:hover {
	color: #039be5!important
}
.btn.busy:active,
.btn.busy:hover,
.dropdown-menu {
	background: #232323!important
}
.comment__footer .vote-down,
.comment__footer a,
.comment__header .vote-down,
.comment__header a,
.dropdown-toggle,
.nav-tab > a {
	color: #9e9e9e!important
}
}
