style.css:
@-moz-document domain("learningjquery.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

selector
{
	text-transform: capitalize!important;
}

selector,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
      text-shadow: none!important;
}

/*___ Fonts ________________________________________________________________________*/

.post h2 a
{
	font-size: 2.5rem!important;
}

.content-container p, .content-container blockquote, .content-container pre, .content-container a, .content-container abbr, .content-container acronym, .content-container address, .content-container big, .content-container cite, .content-container code, .content-container del, .content-container dfn, .content-container em, .content-container font, .content-container img, .content-container ins, .content-container kbd, .content-container q, .content-container s, .content-container samp, .content-container small, .content-container strike, .content-container strong, .content-container sub, .content-container sup, .content-container tt, .content-container var, .content-container b, .content-container u, .content-container i, .content-container center, .content-container dl, .content-container dt, .content-container dd, .content-container ol, .content-container ul, .content-container li, .content-container fieldset, .content-container form, .content-container label, .content-container legend, .content-container table, .content-container caption, .content-container tbody, .content-container tfoot, .content-container thead, .content-container tr, .content-container th, .content-container td,
.entry
{
	font-size: 1.85rem!important;
}

selector
{
	font-size: 1rem!important;
}

selector
{
	line-height: 28px!important;
}

selector
{
	line-height: 25px!important;
}

/*___ Remove Items _________________________________________________________________*/

.adthrive-ad,
.left-sidebar,
.trc_related_container
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

selector
{
    top: 10px!important;
}

/*___ Margins ______________________________________________________________________*/

.hentry
{
	margin: 1rem!important;
}

selector
{
	margin: 3px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

selector
{
	padding: 1rem!important;
}

selector
{
	padding: 3px 26px!important;
}

/*___ Heights ______________________________________________________________________*/

selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

.home .content-container, .single .content-container
{
	max-width: none!important;
}

[class*='page-item-'] a
{
	width: auto!important;
}

#nav-filter,
#sidebar,

.single .contents {
    width: 100%!important;
}

#header,

.home .content-container,
.single .content-container,
.wrapper
{
	width: 98%!important;
}

.home .contents
{
	width: 75%!important;
}

#sidebar-container
{
	width: 25%!important;
}

}

