style.css:
@-moz-document domain(monday.com) {
    :root {
        --color-bg-darker: #151619;
        --color-bg-dark: #1d2023;
        --color-bg: #202226; /* 32, 34, 38 */
        --color-bg-light: #24262b;
        --color-bg-lighter: #2a2d33; /* 42, 45, 51 */
        --color-border: #2F3238;
        --color-border-lighter: #3B3F46;
        --color-text: #8B99A8;
        --color-text-lighter: #96A6B6;
        --color-text-darker: #525B65;
        --color-text-bright: #B6C2D8;
        --color-accent: #1591E1;
        --color-accent-hover: #127EC4;
        --color-input-bg: var(--color-bg-dark);
        --color-input-text: #e5e5e5;
        --color-green: #00ca72;
        --color-purple: #a358df;
        --color-yellow: #ffcb00;
    }

    /* Primary Styles */
    body {
        color: var(--color-text);
    }
    a {
        color: var(--color-accent);
    }
    body#main,
    body#main .group-header-component div,
    .board-content-component,
    body#main #content,
    body#main #header_top_nav_profile .top-profile-pic-wrapper,
    body#main #header_top_nav_profile .top-profile-arrow-wrapper,
    body#main .ds-menu-dialog,
    body#main .q-list-wrapper,
    body#main .person-select-button,
    body#main .clear-button,
    body#main .save-button,
    body#main #inbox,
    body#main #inbox div,
    body#main #content_wrapper {
        background: var(--color-bg) !important;
    }
    body#main #header {
        background: var(--color-bg-dark) !important;
    }
    body#main #header #top_user_profile #top-profile-pic {
        background: var(--color-border);
    }
    body#main #header .content {
        background: var(--color-bg-dark) !important;
        border-bottom-color: var(--color-border);
    }
    body#main .header_mini_navigation #users a {
        color: var(--color-text);
    }
    body#main .header_mini_navigation > li .separator,
    #header #top_user_profile .separator {
        border-color: var(--color-border);
    }
    body#main #header #header_top_nav_invite_members .icon {
        color: var(--color-accent);
    }
    body#main span,
    body#main p {
        color: var(--color-text);
    }
    a
    body#main input {
        background-color: var(--color-bg-dark) !important;
    }
    body#main .pulse-right-indicator,
    body#main .cell-component,
    body#main .column-header,
    body#main .name-column-header,
    body#main .pulse-component {
        border-color: var(--color-border) !important;
    }
    #header .search_wrapper #top_project_finder .search {
        background: var(--color-bg-lighter) !important;
        border-color: var(--color-bg-lighter);
    }
    .board-collapse-button .triangle {
        fill: var(--color-bg-lighter);
    }
    #header #top_user_profile #top-user-menu {
        background: var(--color-bg-lighter) !important;
        border-color: var(--color-bg-darker);
    }
    #header #header_top_nav_profile .pulse_menu_options li a {
        color: white;
    }
    body#main .leftnav,
    body#main #header .leftnav {
        background: var(--color-bg-dark) !important;
    }
    body#main .leftnav,
    body#main .quick-search-container {
        box-shadow: inset -1px 0 0 var(--color-border);
        background-color: var(--color-bg-darker) !important;
    }
    .pulse_list_bar .nano-content {
        background-color: var(--color-bg-dark);
    }
    .pulse_menu_wrapper .pulse_menu_options li.ui-menu-item a.ui-state-focus,
    .pulse_menu_wrapper .pulse_menu_options li.ui-menu-item a.ui-state-active {
        background-color: var(--color-accent);
    }
    #header #header_top_nav_profile .pulse_menu_options li a {
        border-color: var(--color-accent);
    }
    body#main #header #top_user_profile #user_profile_menu:hover .hover-circle {
        background: var(--color-bg-lighter);
    }
    #header #top_user_profile #user_profile_menu.active .hover-circle-border,
    #header #top_user_profile #user_profile_menu.active .hover-circle,
    #header #top_user_profile #user_profile_menu.active:hover .hover-circle-border,
    #header #top_user_profile #user_profile_menu.active:hover .hover-circle,
    #header #top_user_profile #user_profile_menu.active .top-profile-arrow-wrapper:before {
        background-color: var(--color-bg-lighter);
    }

    /* Leftpane */
    body#main .leftpane-container {
        background: var(--color-bg-dark);
        border-right: solid 3px var(--color-border);
    }
    body#main .leftpane-navigation-tab-component .link-wrapper span.title {
        color: var(--color-text);
    }
    body#main .leftpane-navigation-tab-component .link-wrapper span.count {
        color: white;
    }
    body#main .leftpane-navigation-tab-component.selected .link-wrapper span.title, 
    body#main .leftpane-navigation-tab-component:hover .link-wrapper span.title {
        color: white;
    }
    body#main .leftpane-navigation-tab-component.selected .link-wrapper span.count, 
    body#main .leftpane-navigation-tab-component:hover .link-wrapper span.count {
        color: var(--color-accent);
    }
    .leftpane-navigation-tab-component {
        border-bottom-color: var(--color-border);
    }
    .leftpane-board-kind-separator-component {
        background-color: var(--color-border);
    }
    .board-kind-section-component.share .section-header-title:hover span, 
    .board-kind-section-component.share .section-header-title:hover .board-kind-boards-count {
        color: var(--color-purple) !important;
    }
    body#main .leftpane-board-item-component-wrapper .leftpane-board-item-component.selected.share span {
        color: white;
    }

    /* Scrollbars */
    body#main.embeded_board #content::-webkit-scrollbar-thumb,
    #content_wrapper .content::-webkit-scrollbar-thumb,
    #inbox #latest_news::-webkit-scrollbar-thumb,
    .pulse_container ::-webkit-scrollbar-thumb,
    .pulse_list_wrapper .column_type_color .has_labels::-webkit-scrollbar-thumb {
        background-color: var(--color-border);
    }
    body#main.embeded_board #content::-webkit-scrollbar-thumb:hover,
    #content_wrapper .content::-webkit-scrollbar-thumb:hover,
    #inbox #latest_news::-webkit-scrollbar-thumb:hover,
    .pulse_container ::-webkit-scrollbar-thumb:hover,
    .pulse_list_wrapper .column_type_color .has_labels::-webkit-scrollbar-thumb:hover {
        background-color: var(--color-border-lighter);
    }

    #notifications_popup_preview .popup_preview_body::-webkit-scrollbar,
    .broadcast_list_wrapper::-webkit-scrollbar,
    .personal-assistant-component .personal-assistant-content-view::-webkit-scrollbar,
    .automations-store-page-component .automations-store-page-inner .automations-store-content::-webkit-scrollbar {
        width: 8px;
        height: 10px;
    }
    #notifications_popup_preview .popup_preview_body::-webkit-scrollbar-thumb,
    .broadcast_list_wrapper::-webkit-scrollbar-thumb,
    .personal-assistant-component .personal-assistant-content-view::-webkit-scrollbar-thumb,
    .automations-store-page-component .automations-store-page-inner .automations-store-content::-webkit-scrollbar-thumb {
        background-color: var(--color-border);
    }
    #notifications_popup_preview .popup_preview_body::-webkit-scrollbar-thumb:hover,
    .broadcast_list_wrapper::-webkit-scrollbar-thumb:hover,
    .personal-assistant-component .personal-assistant-content-view::-webkit-scrollbar-thumb:hover,
    .automations-store-page-component .automations-store-page-inner .automations-store-content::-webkit-scrollbar-thumb:hover {
        background-color: var(--color-border-lighter);
    }

    /* Header dropdowns */
    #header .notification_message:hover,
    #header .notification_message.selected,
    #header .notification_message .plaster,
    #notifications_popup_preview,
    #notifications_popup_preview .popup_preview_header,
    #header span#broadcast_component #broadcast_button:hover,
    #header span#broadcast_component #broadcast_button.active {
        background: var(--color-bg-light) !important;
    }
    #header .notification_message:hover,
    #header .notification_message.selected,
    #header span#broadcast_component #broadcast_button:hover,
    #header span#broadcast_component #broadcast_button.active {
        border-color: var(--color-bg-light) !important;
    }
    #notifications_popup_preview,
    #notifications_popup_preview .popup_preview_header {
        border-color: var(--color-bg);
    }
    ul.notification_list li.notification {
        background-color: var(--color-bg-lighter);
        border-color: var(--color-border);
    }
    ul.notification_list li.notification.opened {
        background-color: var(--color-bg);
    }
    ul.notification_list li.notification:hover {
        background-color: var(--color-bg-lighter);
    }
    #broadcast_frame {
        background: var(--color-bg-light) !important;
        border-color: var(--color-border) !important;
    }
    #header span#broadcast_component #broadcast_button.active:before {
        background: var(--color-bg-light) !important;
    }
    .broadcast_list_title {
        border-color: var(--color-border) !important;
    }
    #broadcast_list li.broadcast_notification {
        border-bottom-color: var(--color-border) !important;
    }
    .broadcast_seperator {
        background: var(--color-border) !important;
    }

    /* Inbox */
    .pulse_list_bar .pulse_list_bar_top_menu,
    .personal-assistant-tab-component,
    #pulse_bar .boards-wrapper .boards_inner {
        border-color: var(--color-border);
    }

    #pulse_bar ul li a:not(.on_boarding_dot) span {
        color: var(--color-text);
    }
    #pulse_bar ul li a:not(.on_boarding_dot) span.count {
        color: var(--color-bg);
    }
    #pulse_bar .share_boards_wrapper li.selected span {
        color: white;
    }
    #pulse_bar .counter,
    #pulse_bar .count {
        background-color: var(--color-accent);
    }
    .pulse_list_bar .pulse_list_bar_top_menu .notification_updates:hover .count,
    .pulse_list_bar .pulse_list_bar_top_menu .notification_updates.selected .count {
        color: var(--color-accent) !important;
    }
    .pulse_list_bar .pulse_list_bar_top_menu .my_news_bar.selected,
    .pulse_list_bar .pulse_list_bar_top_menu .my_news_bar:hover,
    .personal-assistant-tab-component.selected,
    .personal-assistant-tab-component:hover {
        color: white;
        background: var(--color-accent);
    }

    .pulse_list_bar .pulse_list_bar_top_menu .my_news_bar.selected a:not(.on_boarding_dot) {
        color: white;
    }

    .my_news_bar.selected a:not(.on_boarding_dot) span,
    .my_news_bar:hover a:not(.on_boarding_dot) span {
        color: white !important;
    }

    .personal-assistant-tab-component.selected .personal-assistant-link span,
    .personal-assistant-tab-component:hover .personal-assistant-link span {
        color: white !important;
    }

    body#main #inbox .expander_toggle:before {
        background-image: none;
    }

    body#main #inbox .posts_list .post {
        border-left-color: var(--color-border);
        border-right-color: var(--color-border);
        border-bottom-color: var(--color-border);
    }
    body#main #inbox .inbox-title-actions a {
        color: var(--color-text);
    }
    body#main #inbox .inbox-filter,
    body#main #inbox .inbox-filter div,
    body#main #inbox .inbox-filter ul.inbox-list li.board-filter-item {
        background: var(--color-bg-light) !important;
    }
    body#main #inbox .inbox-filter .other_sections a {
        color: var(--color-text);
    }
    body#main #inbox .inbox-filter .other_sections a .icon {
        color: var(--color-text-lighter);
    }
    body#main #inbox .inbox-filter ul.inbox-list li.board-filter-item .post_count {
        background-color: var(--color-accent);
    }
    body#main #inbox .inbox-filter ul.inbox-list li.board-filter-item:hover {
        background-color: var(--color-bg-darker) !important;
    }
    body#main #inbox .posts_list .post_box .toggle-update-button .v-icon {
        background-color: var(--color-accent);
    }
    body#main #inbox .posts_list .post_box .toggle-update-button:hover .v-icon {
        background-color: var(--color-accent-hover);
    }

    .post .post_body {
        color: var(--color-text);
    }
    .pulse_container .posts_list .post .title a.user_name,
    .new_pulse .posts_list .post .title a.user_name,
    .post .post_body a,
    .replay_area .replay .user_name,
    #pulse_bar .left_nav_footer_padding .guest-create-account .join-account-link a {
        color: var(--color-accent);
    }

    .post_tools_area {
        border-top-color: var(--color-border);
    }
    .post .replay_area .top_arrow {
        border-bottom-color: var(--color-border-lighter);
    }
    .post .post_menu_container .post_menu_button {
        background: var(--color-bg);
        border-color: var(--color-border-lighter);
    }
    .post .post_menu_container .post_menu_button:hover {
        color: var(--color-border);
    }
    .post .post_menu_container.active .post_menu_button {
        background: var(--color-bg);
    }
    .ui-widget-content,
    .pulse_menu_wrapper .pulse_menu_options .email_preferences {
        background: var(--color-bg) !important;
        border-color: var(--color-border-lighter);
    }
    .ui-widget-content a {
        color: white;
    }
    .post .post_menu_container .post_menu .seperator {
        border-color: var(--color-border-lighter);
    }
    .post .post_menu_container .post_menu li a {
        color: white;
    }
    .post .post_menu_container .post_menu a.ui-state-hover,
    .post .post_menu_container .post_menu a.ui-state-focus {
        background: var(--color-accent);
    }
    .post_tools_area a.user_like .img-circle {
        border-color: var(--color-bg);
    }
    .post_tools_area a.likebtn,
    .post_tools_area a.reply_btn,
    .post_tools_area span.who_watched,
    .post_tools_area a.who_pinned,
    .post_tools_area .like_button_wrapper {
        color: var(--color-text);
        border-color: var(--color-border-lighter);
    }

    .replay_area .replay,
    .replay_area .post_reply_tools .write_a_reply,
    .replay_area .replay time.humanize,
    .replay_area .replay .who_watched,
    .replay_area .replay .likebtn {
        border-color: var(--color-border-lighter);
    }
    .replay_area .replay time.humanize,
    .replay_area .replay .who_watched,
    .replay_area .replay .likebtn,
    .replay_area .replay .details .icon {
        color: var(--color-text);
    }

    .pulse_container .posts_list .post_board_link a,
    .new_pulse .posts_list .post_board_link a {
        color: var(--color-text-darker);
    }

    #support_list .support_item_wrapper .support_item {
        border-color: var(--color-border);
    }

    .search_content,
    .search_menu {
        background: var(--color-bg) !important;
    }
    .search_content .search_menu .search_menu_inner {
        border-bottom-color: var(--color-border);
    }
    .search_page.home_screen .homescreen_wrapper .searches_section h2,
    .search_page.home_screen .homescreen_wrapper .user_searches h2 {
        color: var(--color-text-lighter);
        border-bottom-color: var(--color-border);
    }
    .search_page.home_screen .homescreen_wrapper .searches_section .empty_message {
        color: var(--color-text-darker);
    }
    .search_page.home_screen .homescreen_wrapper .searches_section .search_item .search_link {
        color: var(--color-text);
    }
    .search_page.home_screen .homescreen_wrapper .user_searches {
        border-color: var(--color-border);
    }

    /* Boards */
    body#main .pulse-component div:not(.pulse-left-indicator) {
        background-color: transparent;
    }
    body#main .pulse-right-indicator,
    body#main .cell-component,
    body#main .column-header,
    body#main .name-column-header,
    body#main .pulse-component {
        border-bottom-color: var(--color-bg) !important;
        border-right-color: var(--color-bg) !important;
    }
    .pulse-component-wrapper {
        background: var(--color-bg-lighter);
    }
    body#main #content .name-cell,
    #content .name-cell {
        background-color: rgba(42,45,51,.933333) !important;
    }
    .pulse-menu-component {
        background: var(--color-bg) !important;
    }
    .color-cell,
    .color-cell span {
        color: white !important;
    }
    .group-floating-header {
        background-color: var(--color-bg);
        border-bottom-color: var(--color-border);
    }
    .group-header-component .group-menu .group-menu-button:not(:hover) span {
        color: white !important;
    }
    .group-header-component .group-menu .group-menu-button:hover {
        background-color: var(--color-bg-dark) !important;
        border-color: var(--color-border) !important;
    }
    .group-header-component .group-menu.open .group-menu-button {
        background-color: var(--color-bg-dark) !important;
        border-color: var(--color-border) !important;
        border-bottom-color: var(--color-bg-dark) !important;
    }
    .group-menu-dialog .group-menu-patch {
        background-color: var(--color-bg-dark);
    }
    .board-wrapper .board-description.can-edit .ds-text-component.description-content {
        border-color: transparent;
    }
    .board-wrapper .board-actions-v2 .board-actions-section {
        border-color: var(--color-border-lighter) !important;
    }
    .board-views-list-component .board-views-list-inner-wrapper .board-views-list-inner .list-button:not(:last-child):before,
    .board-wrapper .board-actions-v2 .separator {
        background: var(--color-border) !important;
    }
    .board-views-list-component .board-views-list-inner-wrapper .board-views-list-inner .list-button,
    .board-wrapper a {
        color: var(--color-text) !important;
    }
    .automations-button-inner {
        color: var(--color-text);
    }
    .automations-button-inner:hover {
        color: var(--color-accent);
    }
    body#main .automations-button-component .new-tip {
        color: white;
    }
    .collapse-group-toggle-component {
        background-color: var(--color-border);
        border-color: var(--color-border);
    }
    .collapse-group-toggle-component:hover {
        color: white;
        background-color: var(--color-accent);
        border-color: var(--color-accent);
    }
    .collapse-all-groups {
        border-right-color: var(--color-border) !important;
    }
    .board-filter .board-filter-input-wrapper_v2 input {
        background: var(--color-bg-lighter) !important;
        border-color: var(--color-bg-lighter) !important;
    }
    .board-filter .board-filter-input-wrapper_v2 input::placeholder {
        color: var(--color-text);
    }
    .board-filter .advanced-filter-button_v2 .advanced-filters-button {
        color: var(--color-text) !important;
    }
    .board-filter .advanced-filter-button_v2 .advanced-filters-button:hover,
    .board-filter .person-select .person-select-button_v2:hover span {
        color: var(--color-accent) !important;
    }
    .board-filter .advanced-filter .advanced-filter-dialog_v2,
    .board-filter .person-select-dialog_v2 {
        background-color: var(--color-bg-dark) !important;
        border-color: var(--color-border) !important;
    }
    .board-filter .advanced-filter .advanced-filter-dialog_v2 {
        background-color: var(--color-bg) !important;
    }
    .advanced-board-filters-upsale-component .advanced-filter-title, 
    .board-filter .advanced-filter-title {
        color: var(--color-text-lighter) !important;
        background-color: var(--color-bg-dark) !important;
    }
    .board-filter .advanced-filter .advanced-filter-dialog_v2 .advanced-filter-dialog-inner:before {
        border-bottom-color: var(--color-border) !important;
    }
    .board-filter .advanced-filter .advanced-filter-dialog_v2 .advanced-filter-dialog-inner:after {
        border-bottom-color: var(--color-bg) !important;
    }
    .board-filter .person-select-dialog_v2:before {
        border-right-color: var(--color-border) !important;
    }
    .board-filter .person-select-dialog_v2:after {
        border-right-color: var(--color-bg) !important;
    }
    .advanced-board-filters-upsale-component .advanced-filter-title span, 
    .board-filter .advanced-filter-title span {
        color: var(--color-text-lighter) !important;
    }
    .board-filter .advanced-filter-item .advanced-filter-item-title {
        color: var(--color-text) !important;
    }
    .board-filter .advanced-filter-item .advanced-filter-item-options .advanced-filter-option {
        background-color: var(--color-bg-lighter) !important;
        border-bottom-color: rgba(255,255,255,0.1) !important;
    }
    .board-filter .advanced-filter-item .advanced-filter-item-options .advanced-filter-option:hover {
        background-color: #e1e1e1 !important;
        border-bottom-color: rgba(255,255,255,0.1) !important;
    }
    .board-filter .person-select-item:hover {
        background-color: var(--color-bg-lighter) !important;
    }
    .posts-indicator-component .conversation-indicator-icon .updates-count,
    .posts-indicator-component .conversation-indicator-icon.recently-updated .updates-count {
        color: var(--color-bg) !important;
        background-color: var(--color-text);
    }
    .posts-indicator-component .conversation-indicator-icon.recently-updated .updates-count {
        background-color: var(--color-accent);
        font-weight: normal;
    }
    .posts-indicator-component .conversation-indicator-icon .icon-v2-chat-without-update:hover,
    .posts-indicator-component .conversation-indicator-icon.recently-updated .icon-v2-chat-with-update {
        color: var(--color-accent) !important;
    }
    .board-actions-v2 .activity-log-button svg g g:nth-child(1) rect {
        fill: var(--color-accent);
    }
    .board-actions-v2 .activity-log-button svg g g:nth-child(2) rect {
        fill: var(--color-bg);
    }
    .board-wrapper .board-actions-v2 .board-menu.open .board-menu-button,
    .board-wrapper .board-actions-v2 .board-menu .board-menu-button:hover {
        border-color: var(--color-border-lighter);
    }
    .ds-menu-dialog,
    .ds-menu-dialog .ds-seconday-menu-wrapper-left,
    .ds-menu-dialog .ds-seconday-menu-wrapper-right  {
        background: var(--color-bg-dark);
        border-color: var(--color-border);
    }
    .ds-menu-item:not(.disabled).active,
    .ds-menu-item:not(.disabled):hover,
    .ds-menu-item:not(.disabled) .ds-title {
        color: white !important;
    }
    .ds-menu-dialog .ds-seconday-menu-wrapper-left .ds-menu-inner,
    .ds-menu-dialog .ds-seconday-menu-wrapper-right .ds-menu-inner {
        border-color: var(--color-border);
    }
    .ds-menu-section:not(:last-child) {
        border-bottom-color: var(--color-border-lighter);
    }
