style.css:
@-moz-document domain("muylinux.com"){
body {background: #222 !important;color: #bbb !important;}
body.dark {background: #222 !important;color: #bbb !important;}
img {background-color: #111 !important;filter: grayscale(30%) brightness(70%) !important;}
#logo {background-color: #111 !important;}
#logo a img {background-color: #111 !important;padding:0 !important;}
#bgon-cookie-bar {background-color: #111 !important;border: 1px solid #333 !important;}
#subheader-inner {background: #222 !important;color: #bbb !important;border: 0 !important;}
#header-inner {background-color: #111 !important;border: 1px solid #333 !important;}
#header-nav, #nav a {background: #111 !important;color: #bbb !important;border: 0 !important;}
#logo img {filter: grayscale(30%) !important;}
#nav li {border-right: 1px solid #333!important;}
#nav li a {color: #bbb !important}
#nav li a:hover {color: #fff !important}
.redesSociales, #s {background-color: #363636 !important;color: #bbb !important;border: 0 !important;}
.redesSociales .News a, .redesSociales a {color: #bbb !important;}
.redesSociales a:hover {color: #fff !important;}
#header-line {background: #222 !important; border: 0 !important;}
.miniheading{background: #222 !important; border: 0 !important;}
.entry-part a {color: #bbb !important;}
.single .entry-category, .single .entry-meta, .single .entry-title { text-shadow: 0 1px 0 #333 !important; color: #bbb !important;}
#page-inner, #footer-inner {background: #111 !important; color: #bbb !important; border: 0 !important;}
.main.sidebar-right{background: #111 !important;}
.wp-pagenavi {border: 0 !important;}
.wp-pagenavi .pages,.wp-pagenavi .current {background: #333 !important; color: #bbb !important;}
.wp-pagenavi a {background: #333 !important; color: #bbb !important;}
.wp-caption {background: #333 !important;border: 0 !important;color: #bbb !important;}
pre {background: #000 !important; color: #bbb !important;border: 0 !important;}
.textarea-wrappe {background: #000 !important;color: #bbb !important;}
.blog .entry, .single .entry, .archive .entry, .search .entry { border-bottom: 1px solid #666 !important;}
.bigspot, .megaspot { border-bottom: 1px solid #666 !important;}
.previous a, .next a{background: #171717 !important; color: #bbb !important;border: 0 !important;}
.previous strong, .next strong {color: #ccc !important;}
.previous a:hover, .next a:hover{background: #333 !important;}
#wpb-tabs-2 a {color: #ccc !important;}
blockquote {color: #aaa !important;}
h1, h2, h3, h4, h5, h6 {color: #ccc !important;}
.alert.notice {background: #222 !important;border-color: #ff7900 !important;}
.alert a {color: #ddd !important;}
.alert .alert-close {filter: invert(100%) !important;}
.text table tr.alt {background: #222 !important;}
.select-nav {background: #111 !important;}
.select-nav select {background: #222 !important;color: #ccc !important;border-radius:2px !important;border: none !important;padding:5px !important;}
}
