style.css:
@-moz-document domain("php.net") {
html
    {background-color: #131313; color: #ccc}

.refsect1 .example-contents > [class$="code"], .refsect1 .example-contents.screen, .docs .classsynopsis, .refsect1 .fieldsynopsis, .refsect1 .dc-description, .docs .sect1 .dc-description,

#usernotes .note .text, .example-contents > [class$="code"], .example-contents.screen, .informalexample .literallayout, .phpcode span.html, 
.info, div.tip, blockquote.note

    {background-color: #171717;  color: #c7c7c7}

span[style*="color: #0000BB"]
    {color: #a299c9 !important}

span[style*="color: #DD0000"]
    {color: #cd92a2 !important}

span[style*="color: #007700"]
    {color: #9bb391 !important}

span[style*="color: #FF8000"]
    {color: #dea66c !important}


.methodname b, .methodname strong, .methodname a, .classsynopsis .classname, .parameter,
#layout-content a:link, #layout-content a:visited
    {   color: #91afcd;}

.initializer, .initializer code 
    {color: #ae7893}


h1.refname,
.title a, .title{
    color: #bf89ac;
}

#layout-content {
    background: #222;
    color: #ddd;
}

.refsect1 blockquote.note
    {background: #171717}

div.phpcode span.default {
    color: #8da5ce;}

div.phpcode span.keyword {
    color: #93b867;}

div.phpcode span.string {
    color: #d67373;}


#usernotes .note .votes .tally,
#usernotes .note .name em
{color: #bbb}

}
