style.css:
@-moz-document domain("utip.io") {
    /**                                                              **/
    /** UTIP Dark Mode V2.0.10 by VooDoo **/
    /**                                                              **/
    :root {
        --custom-color: /*[[custom-color]]*/ /*voodoo: #2a63d5    utip: #0076f4*/;
        --text-normal: /*[[text-normal]]*/ /*#fff*/;
        --text-muted: /*[[text-muted]]*/ /*#ccc*/;
        --text-link: var(--custom-color);
        --text-custom: var(--custom-color);
        --background-primary: /*[[background-primary]]*/ /*#232434*/;
        --background-secondary: /*[[background-secondary]]*/ /*#2e2f3e*/;
        --background-floating: /*[[background-floating]]*/ /*#1e1c2a*/;
        --background-light: /*[[background-light]]*/ /*#404255*/;

        --danger: #ff5a2a;
        --transparant: transparant;
    }

    /* Global */
html,
body {
    background-color: var(--background-floating);
}

/*lien*/
a {
    color: var(--text-link);
}
a:hover {
    color: var(--text-link);
}

.feed-wrapper[data-v-1c5e266f],
.ufeed-container[data-v-550491a4] {
    background-color: var(--background-floating);
}

/* Alerte */
.flash__message.success {
    color: #7fdb81!important;
    background-color: #1b5304!important;
    border-color: #3c7b06!important;
}

.flash__message.warning {
    color: #eea75a!important;
    background-color: #8e3e00!important;
    border-color: #995803!important;
}

.flash__message.error {
    color: #ff5151!important;
    background-color: #570000!important;
    border-color: #dd0909!important;
}
/*background*/
/* Navbar */
.ufeed-menu {
    background-color: var(--background-primary);
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.3);
}

#responsive-menu {
    background: var(--background-primary);
    border-bottom: 1px solid var(--background-light);
}

#responsive-menu .active a.nav-link,
.active a.nav-link:visited {
    color: var(--text-normal);
}

.navbar-back-link {
    border-right: 1px solid var(--background-light);
}

.navbar-back-bloc {
    border-bottom: 1px solid var(--background-light);
}

.ncs-container[data-v-381e09c4] {
    border: solid 1px var(--background-light);
    color: var(--text-muted);
}

/*couleur du texte*/
.nav-item.active:after {
    border-bottom: 3px dotted var(--text-normal);
}
/*couleur du dot*/
#responsive-menu a.nav-link {
    color: var(--text-muted);
}
/*couleur du texte visitÃ©*/
input[data-v-381e09c4] {
    color: var(--text-normal);
    background: var(--background-primary);
}

input[data-v-381e09c4]::placeholder {
    color: var(--text-normal);
}

.mc-icon-toggler[data-v-575128cd] > img {
    filter: hue-rotate(671deg) invert(1);
}

.notification-quick-action[data-v-1be52e5d] > img {
    filter: hue-rotate(671deg) invert(1);
}

.mc-container.mc-dark > .mc-icon-toggler[data-v-575128cd] > img {
    filter: none
}

.navbar-quick-actions-container.nv-dark > .notification-quick-action[data-v-1be52e5d] > img {
    filter: none
}

.search-bar-creators-list-choice[data-v-41d9a73c] {
    color: var(--text-normal);
}

.search-bar-creators-list[data-v-05bdf43e] {
    background: var(--background-primary);
    box-shadow: 0 3px 10px 0 rgba(0, 0, 0, 0.3);
}

.search-bar-creators-list-choice[data-v-41d9a73c]:hover {
    background-color: rgba(48, 56, 76, 0.3);
}

.search-bar-no-result[data-v-05bdf43e] {
    color: var(--text-normal);
}


/* sidebar */
.sic-container[data-v-f25d7976] {
    box-shadow: -3px 0 5px 0 rgba(0, 0, 0, 0.3);
    background-color: var(--background-primary);
    color: var(--text-normal);
    border-right: 1px solid var(--background-light);
}

.sic-header[data-v-f25d7976] {
    box-shadow: 0 3px 5px 0 rgba(0, 0, 0, 0.3);
}

img[data-v-f25d7976] {
