}

div.flex-block[data-v-03971458] > div.flex-block[data-v-03971458] > div[data-v-03971458] > img[data-v-03971458] {
    content: url(https://cdn.utip.eu/build/images/Ico_Menu_Abo.22003e6d.svg)
}

.ndm-wallet > div[data-v-03971458] > img[data-v-03971458] {
    content: url(https://cdn.utip.eu/build/images/Ico_Menu_Wallet.586ba808.svg)
}

.dropdown-item[data-v-03971458]:first-child > .flex-block > img[data-v-03971458] {
    content: url(https://cdn.utip.eu/build/images/Ico_Menu_Activity.5c68e9ac.svg)
}

.dropdown-item[data-v-03971458]:nth-child(4n+2) > .flex-block > img[data-v-03971458] {
    content: url(https://cdn.utip.eu/build/images/Ico_Menu_Status.52464fa7.svg)
}
.dropdown-item[data-v-03971458]:nth-child(4n+3) > .flex-block > img[data-v-03971458] {
    content: url(https://cdn.utip.eu/build/images/Ico_Menu_Compte.e1693aff.svg)
}
.dropdown-item[data-v-03971458]:nth-child(4n+4) > .flex-block > img[data-v-03971458] {
    content: url(https://cdn.utip.eu/build/images/Ico_Menu_Notif.48dc7c2a.svg)
}

/* Feed Post */
.post {
    background: var(--background-secondary);
}
.post.with-border, div.post-content div.EmbedInsta, .post-body.with-border, .p-thumbnail {
    border: 1px solid rgba(0, 0, 0, .098);
}


/*background du post*/
.author-name {
    color: var(--text-normal);
}

.edit-button[data-v-a96ec664]:focus {
    background-color: var(--background-primary);
}
/*Post bouton ficus color*/
.dropdown-menu {
    box-shadow: 0 2px 8px 0 rgba(0, 0, 0, .03);
    border: solid 1px var(--background-secondary);
    background-color: var(--background-secondary);
}

.dropdown-item {
    border-bottom: solid 1px var(--background-secondary);
