}
/*couleur du lien voir plus*/
.EmbeddedTweet {
    background-color: var(--background-secondary);
}
/*couleur de fond des tweets*/
.EmbeddedTweet a,
.EmbeddedTweet a:visited {
    color: var(--text-link);
}
/*couleur des liens*/
.EmbeddedTweet .Tweet-text[dir=ltr],
.EmbeddedTweet .QuoteTweet-link,
.EmbeddedTweet .QuoteTweet-link:active,
.EmbeddedTweet .QuoteTweet-link:focus,
.EmbeddedTweet .QuoteTweet-link:hover,
.EmbeddedTweet .QuoteTweet-link:visited,
.EmbeddedTweet .Identity-screenName,
.EmbeddedTweet .with-icn,
.EmbeddedTweet .TweetAuthor-name {
    color: var(--text-normal);
}
/*couleur du text des tweets*/
.EmbeddedTweet .QuoteTweet,
.EmbeddedTweet .MediaCard-borderOverlay {
    border: solid 1px var(--background-light);
}
/*couleur de la box des RT avec commentaire*/
.EmbeddedTweet .QuoteTweet:active,
.EmbeddedTweet .QuoteTweet:hover {
    border-color: var(--background-floating);
}
/*et avec le hover*/
.EmbeddedTweet .Icon--retweetEdge {
    filter: invert(1) brightness(200%);
}
div.EmbeddedTweet .MediaCard-mediaContainer {
    background-color: var(--background-light);
}

/* Feed color filter */
/* Utilise la couleur par dÃ©faut (du SVG) mais lui applique un brightness pour la rendre plus lisible */

.verified {
