style.css:
@-moz-document url-prefix("http://www.pniao.com") {
#container {
	background: url( https://i.loli.net/2018/10/04/5bb6039a16ed4.jpg) no-repeat fixed center / cover !important;
}
.columnMenu a, .columnMenu a:visited {
    display: block;
    padding: 2px 9px;
    border-radius: 2px;
    color: #c2e4fb;
}
.siderSortNav a, .siderSortNav a:visited {
    color: #86ebff;
}
#container > DIV.mainContainer:first-child > DIV.mainBox:nth-child(2) > DIV._line:nth-child(n){
    width: 740px;
    border-bottom: solid 2px #faa0a0;
}

.movies .sawBtn a, .movies .sawBtn a:visited {
    padding: 0 6px;
    border: solid 1px #44414100;
    border-radius: 2px;
    color: #555;
    background-color: #F27887;
}
.indexSignBox a, .indexSignBox a:visited {
    display: block;
    padding: 8px 0;
    padding-left: 25px;
  
    background-size: 25px 25px;
    background-color: #f59b9b;
    border: solid 2px #c1e4fe;
    border-radius: 2px;
}
.siderNavTitle {
    margin-bottom: 10px;
    padding: 0 5px;
    height: 30px;
    line-height: 30px;
    border-left: solid 3px #B6CFDE;
    font-weight: bold;
    color: #131212;
    background-color: #35343400;
}

a, a:visited {
    color: #6feab4;
    text-decoration: none;
    outline: none;
}
.indexSignBox:first-child > A,
.siderNavTitle.appDown >a
{
    color: #252625;
    text-decoration: none;
    outline: none;
}
.movies .eachOne {
    width: auto;
    margin: 20px 0;
    padding: 10px;
    border: solid 2px #f4a2a2cc;
	border-radius: 0px ;
    background-color: #18171754;
}
.movies .title a, .movies .title a:visited {
    font-size: 15px;
    font-weight: bold;
    color: #ff9f9f;
}
.movies .right {
    width: 100%;
    height: 100%;
    color: #c6c5c5;
}
.movies .score a, .movies .score a:visited, .movies .top250Flag,
.movieOne .score a, .movieOne .score a:visited, .movieOne .top250Flag {
    color: #4bd04b;
    font-family: Georgia;
}
.movieOne .awards a, .movieOne .awards a:visited {
    color: #ee54ff;
}

._line {
	width:740px;
    border-bottom: solid 3px   #ff9f9f;
   
}
.movies .info .brief {
    color: #e9e9e9;
    margin-bottom: 5px;
    font-size: 13px;
}

.siderInner.siderFloat{background-color: #45424200;}
.siderMovies .score {
    width: 25px;
    height: 25px;
    line-height: 20px;
    color: #2fcd08;
    text-align: center;
    font-family: Georgia;
}
.siderMovies .onlyTitle {
    padding: 7px 0;
    height: 25px;
    line-height: 25px;
    border-top: solid 2px #f7a8a8;
}
.headerContainer .headerInner {
    width: 100%;
    background-color: #474646;
}
#loadDownUrl > DIV.downUrlContainer.downUrlContainer_hide.seekDownContainer:nth-child(3) > DIV.alertNotice:first-child {color:#323131}

body {
    margin: 0;
    padding: 0;
    color: #d5d5d5;
    font-size: 14px;
    background-color: #f99;
    font-family: "Microsoft YaHei",Helvetica,Arial,sans-serif;
    -webkit-text-size-adjust: none;
}
.headerContainer .shadowBg {
    border-top: solid 3px #41424300;
    width: 100%;
    height: 8px;
    background-size: 1px 8px;
}
.topics .each {
    margin-bottom: 20px;
    padding: 5px 0;
    border-bottom: solid 2px #fab9b903;
	border: solid 2px #f4a2a2cc;
}
.topicFlag.each{ background-color: #3d3b3bbf;}
.topics .right a, .topics .right a:visited {
    float: left;
    display: block;
    margin: 2px;
    height: 26px;
    line-height: 26px;
    padding: 0 10px;
    background-color: #5c5f61;
}
.mainPage a, .mainPage a:visited {
    display: block;
    padding: 3px 10px;
    border: solid 2px #fc9393;
    border-radius: 2px;
    color: #d6d2d2;
	background-color: #414243;
}
.topics .topicTitle a, .topics .topicTitle a:visited {
    font-weight: bold;
    font-size: 18px;
    color: #f59999;
}
.movies .movPre {
    font-weight: bold;
    color: #e66cfff7;
}

.douList .eachOne:nth-child(even),
.douListFlag.eachOne{
    border: solid 0px #ffa2a2;
    background-color: #3c3b3b;

}
.douList .info_act .userName a, .douList .info_act .userName a:visited {
    color: #b4dbfd;
}

.discuss .eachOne {
    margin-bottom: 20px;
    border: solid 2px #ff979700;
    padding: 5px;
    box-shadow: 1px 1px 2px 1px #eee0;
    background-color: #0000;
}
.discuss .mainMsg .topBar {
    padding: 0 5px;
    height: 35px;
    line-height: 35px;
    background-color: #9d9a9ad6;
	 color: #1d1d1e;
}
.siderMovies .orderNum {
    float: left;
    margin-right: 10px;
    width: 25px;
    height: 25px;
    line-height: 25px;
    color: #ffbfbf;
    background-color: #06060661;
    text-align: center;
}

.discuss .replies .topBar {
    padding: 0 5px;
    height: 30px;
    line-height: 30px;
    background-color: #f1dec1;
		 color: #1d1d1e;
}
.discuss .replies {
    margin-left: 30px;
    margin-top: 5px;
    font-size: 13px;
    color: #f9e0ba;
}
.acts a, .acts a:visited {
    color: #1b1b1b;
}
.userHomeHeader {
    width: 100%;
    margin-bottom: 10px;
    box-shadow: 1px 1px 1px 1px #3c3030;
    background-color: #7aabab;
}



.mainBox_border {
    padding: 20px;
    background-color: #15151575;
    border: solid 2px #ffa2a2;
}
.downUrlContainer {
    
    background-color: #0006;
    border: solid 2px #f9b6b6;
}
.movieOne .info li:first-child {
    color: #e2d9d9;
}
.movieOne .briefOuter {
    color: #c2c2c2;
}
.movieOne .movTitle h1 a, .movieOne .movTitle h1 a:visited {
    font-weight: bold;
    font-size: 16px;
    color: #f9a4a4;
}
.downUrlList:nth-child(odd),.downHeader,.downUrlList {
	background-color: #6d6c6c00;
	border: solid 0px #ffa2a2;

}
.ajax_urls {
    margin-bottom: 10px;
    border-radius: 2px;
    background-color: #fff0;
}
.downUrlNav_line {
    margin-right: 2px;
    margin-top: -1px;
    border-bottom: solid 1px #abccef00;
}
.downSubNav {
   
    border-bottom: solid 0px #fbb0b0;
} 
.downUrlNav a, .downUrlNav a:visited {
    display: block;
    padding: 2px 15px;
    color: #93e1fc;
    border: solid 1px transparent;
}
.downUrlNav a:hover, .downUrlNav .selected a, .downUrlNav .selected a:visited {
    border: solid 1px #abccef00;
    border-bottom: solid 1px transparent;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    background-color: #ffacac;
    color: #666;
}
.downSubNav .seek_downShare a, .downSubNav .seek_downShare a:visited {
    display: block;
    padding: 0 5px;
    border: solid 1px #f9f9f905;
    border-radius: 2px;
    color: #afdaef;
}

.seekers .each {
    
    border: solid 1px #fc959500;
    border-radius: 3px;
    box-shadow: 1px 1px 2px 1px #eee0;
    color: #dfa7f9;
    line-height: 25px;
	background-color: #313030;
}
.alertNotice {
    margin-top: 5px;
    margin-bottom: 10px;
    padding: 5px;
    background-color: #F9EEDF;
   color:#131212
}
.mcommentForm .mcommentTip {
    padding: 5px;
    line-height: 25px;
    border: solid 1px #ccc0;
    border-bottom: 0;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    background-color: #444343;
}
.mcommentArea,input, textarea,
.mcomments .mainMsg .topBar,
.mcomments .replies .topBar{
   
    border: solid 1px #ccc0;
    border-bottom-left-radius: 3px;
    border-bottom-right-radius: 3px;
    cursor: text;
    box-shadow: 2px 2px 1px 1px #fafafa00 inset;
   background-color: #f7ead8;
	color: #444343;
}
.mcomments .replies {
    margin-left: 30px;
    margin-top: 15px;
    font-size: 13px;
    color: #dbd9d9;
}

.userHomeHeader .infoOuter {
    width: 100%;
    height: 100%;
    color: #383535;
    font-weight: bold;
    text-shadow: 1px 1px 1px #bcb1b100;
    text-align: right;
}
.userHomeHeader .userName {
    height: 20px;
    line-height: 20px;
    margin-top: -8px;
    text-align: center;
    font-weight: bold;
    text-shadow: 1px 1px 1px #fff0;
	color: #3a3737;
}

.movieOne .score_1,.movieOne .score_2  {
    color: #66ca73;
    font-size: 18px;
    font-family: Georgia;
}
.movieOne .score a:hover {
    background-color: transparent;
    color: #66ca73;
    text-decoration: underline;
}
DIV.info > UL:last-child > LI:first-child,DIV.info > UL:last-child > LI:nth-child(2)>a{color:#ffaa41}

.mainSoForm input {
    width: 100%;
    border: 0;
    text-align: center;
 
   background-color: #F9EEDF;
    color: #030303;
}

.mainSoForm {
    margin-bottom: 10px;
        width: 100%;
    border: solid 0px #f88b8b00;
    border-radius: 0px;
  background-color: #F9EEDF;
    box-shadow: 1px 1px 2px 1px #fafafa03 inset;
	color: #030303;

}
.indexMovies{ background-color:#6a686503;
  border:solid 2px #ff9f9f00;}

.mainSoForm .rightFilter a, .mainSoForm .rightFilter a:visited {
    display: block;
    width: 100%;
    height: 100%;
    background-color: #0000;
	border: solid 0px #ff9b9b00;
    text-align: center;
    padding-top: 0px;
    line-height: 19px;
    color: #585252;
}
.mainSoForm .subBtn a, .mainSoForm .subBtn a:visited{
    
    color: #585252;
 border: solid 0px #ff9b9b00;
    background-color: #49444400;
}
.mainSoForm .subBtn a, .mainSoForm .subBtn a:visited {
    
   
   
    background-color: #d6d6d600;
	color: #585252;
}
#movieSoBtn > A,#container > DIV.mainContainer:first-child > DIV.mainBox:nth-child(2) > DIV.mainSoForm:first-child > DIV.rightFilter:last-child > A{border: solid 0px #ff9b9b00;}

#container > DIV.mainContainer:first-child > DIV.discuss:nth-child(3){ background-color: #19191900;}
#container > DIV.mainContainer:first-child{ background-color: #31303005;}

.indexSignBox:first-child > A, .siderNavTitle.appDown >a,
.siderNavTitle{
    color: #2d2e2e;
    text-decoration: none;
    outline: none;
}
A.copyMore:nth-child(n){color: #fcc0c0;}



#container > DIV.mainContainer:first-child > DIV.mainBox:last-child{background-color: #27262691;}

#container > DIV.mainContainer:first-child > DIV.mainBox:last-child {
    background-color: #f5f0f000;
}
.pc_multiSo {
    margin: 0 5px 3px 0;
    padding: 10px;
    border: solid 1px #cccccc05;
    border-radius: 2px;
    background-color: #464444;
    box-shadow: inset 2px 2px 20px 5px #226b58;
}

a:hover {
  
    color: #fcc0c0;
    
}
}

@-moz-document url-prefix("http://www.pniao.com/Mtopic"), url-prefix("http://www.pniao.com/Discuss/handBook"), url-prefix("http://www.pniao.com/Discuss") {
#container > DIV.mainContainer.marginTop:first-child > DIV.mainBox:first-child > DIV._line:nth-child(2){display:none;}
#container > DIV.mainContainer:first-child > DIV._line:nth-child(2){display:none;}
}

@-moz-document url-prefix("http://www.pniao.com/Mov/latest/") {
.indexMovies_2 ul:nth-child(even) ,
.indexMovies_2 ul{
    background-color: #0000;
    border-top: solid 0px #ebebeb00;
	
}
}
