style.css:
/* i really want this to be global */
.task-list>.task-setting-modal>.task-setting {
    top: 5%;
    height: 87%;
}
.sub-task-area li, .sub-task-area ul {
    height: 25px;
}
.button-set {
    display: flex;
    flex-flow: row wrap;
    width:100px;
    margin-right:20px;
}
.button-set .btn {
    margin-bottom:10px;
}
.button-set .btn-primary {
    order: 1;
}
.button-set .btn-success {
    order: 2;
}
.button-set .btn-danger {
    order: 3;
}

#vue > div.button-set > a.btn.btn-primary{
    margin-bottom:20px;
    margin-left: auto;
    margin-right:auto;
    width:100px;
    height:100px;
    transition: all 300ms 0s ease;
    border-radius: 50%;
    text-align:center;
word-wrap: break-word;
padding-top:35%;
padding-left:3px;
}

#vue > div.button-set > a.btn.btn-primary i{
display:none
}


#vue > div.button-set > a.btn.btn-primary:hover {
width: 100px;
height:100px;
}

.button-set>input[type=button].addtask {
    margin-bottom:20px;
    margin-left: auto;
    margin-right:auto;
    width:80px;
    height:80px;
transition: all 300ms 0s ease;
}

.button-set>input[type=button].addtask:hover {
width: 100px;
height:100px;
}
