style.css:
@-moz-document domain("doc.rust-lang.org") {
    code {
        font: 14px/1.5em "Fira Code";
    }

    body {
        font: 16px/1.7em "FiraGO";
    }

    span.caption {
        font-size: 14px;
        font-weight: 500;
    }

    h3 {
        font-size: 20px;
        letter-spacing: .5px;
    }

    h2 {
        font-size: 24px;
        letter-spacing: .5px;
    }
}
