style.css:
@-moz-document domain("melpa.org") {
body,
.nav > li > a:focus,
.nav > li > a:hover,
a,
a:hover,
a:focus,
h1,
input[type=search],
.pagination > li > a:focus,
.pagination > li > a:hover,
.pagination > li > span:focus,
.pagination > li > span:hover {
	background-color: #34495e;
	color: #ecf0f1;
}

small {
	color: #bdc3c7!important;
}

pre,
.btn,
.well,
.well a,
.well a:hover,
.well a:focus,
header.navbar,
.navbar,
.navbar .brand,
.navbar a,
.navbar .nav > li > a,
.muted,
.jumbotron,
.timeline-Widget,
.timeline-Header-title,
.table-hover > tbody > tr:hover,
.table-hover > tbody > tr:hover a,
.TweetAuthor-nameScreenNameContainer,
.customisable-highlight,
.u-floatLeft,
.u-floatRight,
.Icon,
.dt-updated,
.Avatar,
.timeline-Tweet:hover,
.timeline-TweetList-tweet a,
.pagination > .disabled > a,
.pagination > .disabled > a:focus,
.pagination > .disabled > a:hover,
.pagination > .disabled > span,
.pagination > .disabled > span:focus,
.pagination > .disabled > span:hover,
.pagination > li > a,
.pagination > li > span,
.TwitterCard-container--clickable:hover {
	background-color: #2c3e50;
	color: #ecf0f1;
	border: none;
}

.btn-default:hover {
	background-color: #304457;
	color: #ecf0f1;
}

.timeline-TweetList-tweet a {
	color: #2b7bb9;
}

.TwitterCard-container--clickable:hover,
.TwitterCard-container--clickable {
	border: 1px solid #ecf0f1 !important;
}

input[type=search],
.form-control:focus {
	display: block;
	border: none;
	border-radius: 0;
	box-shadow: none;
	border-bottom: 1px solid #ecf0f1 !important;
}

.SandboxRoot.env-bp-660 .timeline-Tweet-text {
	font-size: 1.4em;
	line-height: 1.4em;
}


/* Scrollbar */
::-webkit-scrollbar {
	width: 10px;
	height: 10px;
}

::-webkit-scrollbar-thumb,
.well ::-webkit-scrollbar-thumb {
	background: #ecf0f1;
	border-radius: 10px;
}

::-webkit-scrollbar-track,
.well ::-webkit-scrollbar-track {
	background-color: #304457;
}
}
