.list-group-item, .list-group-item:focus, .list-group-item:hover, .page-link, .page-link:focus, .page-link:hover {
    text-decoration : none;
}
.list-group-item:not(.active):not(.list-group-item-info):not(.list-group-item-danger):not(.list-group-item-success):hover, .btn-secondary:hover, .list-group-item:not(.active):not(.list-group-item-info):not(.list-group-item-danger):not(.list-group-item-success):focus, .btn-secondary:focus {
    /* list+page-link show,hover,active:hover */
    background-color: #eceeef;
}
.list-group-item:not(.active):not(.list-group-item-info):not(.list-group-item-danger):not(.list-group-item-success):hover:active, .page-link:hover:active {
    color : #014c8c;
    background-color : #d4d4d4;
    border-color : #ddd;
}
/* INFO/DANGER/SUCCESS Features, not done yet.*/
.btn-secondary {
    /* btn color show,hover,active:hover */
    color : #0275d8;
    border : 1px solid #ddd;
}
.btn-secondary.active, .btn-secondary.active:hover, .btn-secondary.active:focus {
    color: #fff !important;
    background-color: #0275d8 !important;
    border : 1px solid #ddd !important;
    cursor : default;
}
.btn-secondary:hover, .btn-secondary:focus {
    color : #014c8c;
    border : 1px solid #ddd;
}
.btn-secondary.active.focus,.btn-secondary.active:focus,.btn-secondary.active:hover,.btn-secondary:active.focus,.btn-secondary:active:focus,.btn-secondary:active:hover,.open>.btn-secondary.dropdown-toggle.focus,.open>.btn-secondary.dropdown-toggle:focus,.open>.btn-secondary.dropdown-toggle:hover {
    color : #014c8c;
    background-color : #d4d4d4;
    border-color : #ddd;
}

.fa-star-o, .prob-fav {
    cursor : pointer ;
}

