style.css:
@-moz-document regexp('http(s?)://2018\\.stateofjs\\.com/.*') {
.Sidebar__Inner {
    overflow-y: auto;
    direction: rtl;
}
.Sidebar__Inner > .nav {
    direction: ltr;
}
.Sidebar__Inner::-webkit-scrollbar {
    width: 8px;
    background: black;
}
.Sidebar__Inner::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px black;
}
.Sidebar__Inner::-webkit-scrollbar-thumb {
    background: rgb(65, 199, 199);
    outline: 1px solid slategrey;
}
}
