/* ==UserStyle==
@name           amps
@namespace      USO Archive
@author         Artemy Khisamiev
@description    `Альтернативная тема для сайта Monopoly One`
@version        20220415.18.47
@license        CC-BY-NC-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("monopoly-one.com") {
    /*[[color]]*/  

    .processing,
    [v-cloak=processing] {
        filter: hue-rotate(280deg); }

    body[theme=dark] .HeaderUser-menu-one:hover {
    background-color: var(--color9);
    color: var(--color2); }

    body[theme=dark] label[for="settings_beta_darktheme"]:after {
        content: ' / версия: amps_dark_100323';
        color: var(--color12); }

    label[for="settings_beta_darktheme"]:after {
        content: ' / версия: amps_light_100323';
        color: var(--color12); }

    body[theme=dark] label[for="table-opt-oldcolors"]:after {
        content: '(!) Требуется плагин m1pro';
        color: var(--color2); }

    body:not([theme]) label[for="table-opt-oldcolors"]:after {
        content: '(!) Требуется плагин m1pro';
        color: var(--color2); }

    body:not([theme]) {
        --color-border-focus: var(--color1);
        --color-this: var(--color1);
        --color-this-hover: var(--color2);
        --color-link: var(--color1);
        --color-nottheme: var(--color1);
        --color-nottheme1: var(--color7);
        --color-nottheme2: var(--color2);
        --color-scroll: var(--color9);
        --color-link-border: var(--color2); }

    body[theme=dark] {
        --color-nottheme: var(--color-block-bg);
        --color-nottheme1: var(--color4);
        --color-nottheme2: var(--color-bg);
        --color-scroll: var(--color5);
        --color-bg: var(--color4);
        --color-block-bg: var(--color3);
        --color-border-1: var(--color-gray-700);
        --color-border-focus: var(--color-aqua-700);
        --color-font: var(--color-gray-300);
        --color-font-2: var(--color-gray-500);
        --color-link: var(--color2);
        --color-link-border: var(--color1);
        --color-link-font: var(--color-gray-300);
        --color-link-font-border: var(--color-gray-600);
        --color-link-font-2: var(--color-gray-500);
        --color-link-font-2-border: var(--color-gray-800); }

    /* header */
    .header {
        background: none;
        border-bottom: none;
        box-shadow: none !important; }

    .header .widther {
        width: 1105px; }

    .header .header-logo > div {
        display: none; }

    .header .header-menu .header-menu-one {
        margin-right: 10px;
        box-shadow: 0 0 1px rgba(0, 0, 0, 0.3); }

    body:not([theme]) .header .header-menu .header-menu-one {
        background-color: var(--color-light); }

    .header:not([mnpl-style]) .header-logo > div,
    .header:not([mnpl-style]) .header-menu-one {
        color: var(--color-font);
        background-color: var(--color-block-bg); }

    body > .widther {
        margin-top: 5px; }

    /* body */
    .block {
        box-shadow: 0 0 1px rgba(0, 0, 0, 0.3);
        border-bottom: none !important; }

.VueGamesRoomsOne-settings .form .form-row._disabled {
    display: none !important; }

    .VueHeaderSearch-block {
        display: block !important; }

    div.widther.main-section.main-section-love > .container,
    .grid:not(.grid-of-1) {
        display: flex !important }

    .m1tvLive-streams,
    .marketBanner {
        margin-bottom: 20px; }

    .header .header-menu .header-menu-one .badge {
        color: var(--color-bg); }

    .auth .auth-separator {
        border-left: 1px solid var(--color-bg); }

    .auth .auth-separator:before {
        background-color: var(--color-bg); }

    .auth .auth-separator:after {
        content: 'или';
        color: var(--color-font); }

    .profile-top-info-main-mods ._vip {
        color: var(--color3); }

    .profile-top-stat-actions-dropdown-list {
        background: var(--color-block-bg);
        color: var(--color-font); }

    .profile._nocover .profile-top-info-main,
    .profile._nocover .profile-top-stat,
    .profile._nocover .profile-body-level {
        color: var(--color-font); }

    body[theme=dark] .VueGamesMissions ._progressbar {
        background: var(--color5); }

    .profile-top-info-avatar._online {
        border: 4px solid var(--color2); }

    .GamesNewroom-modes-one._selected {
        color: var(--color5); }

    /*MMR*/
    .GamesNewroom-modes-one._competitive._selected {
        background: var(--color-sunflower-500); }

    .GamesNewroom-block .alert.alert-_mediumgray {
        background-color: var(--color-grapefruit); }

    body[theme=dark] .GamesNewroom-block .alert.alert-_mediumgray {
        background-color: var(--color5); }

    body[theme=dark] .GamesNewroom-block .alert.alert-_vip {
        background: var(--color5); }

    body[theme=dark] .GamesNewroom-block .alert.alert-_vip > div > .button {
        background: var(--color3); }

.table-body-board-chat-message-users ._message:not(._hack) {
    border: none;
    color: var(--color4); }

    /*Ретро*/
    .GamesNewroom-modes-one._retro._selected {
        background: linear-gradient(45deg, var(--color-font), var(--color-font-2)); }

    .games-disconnect-message:before {
        content: none; }

    .games-disconnect-button > .button.button-grass {
        color: var(--color2);
        background-color: var(--color-grapefruit-600); }

    /* body_dark */
    body[theme=dark] .dropdown-list,
    body[theme=dark] .selecter-options {
        box-shadow: 0 0 1px rgba(0, 0, 0, 0.3); }

    body[theme=dark] .HeaderUser-menu,
    body[theme=dark] .footer {
        box-shadow: 0 0 1px rgba(0, 0, 0, .3); }

    body[theme=dark] .footer,
    .m1tvLive-streams,
    .marketBanner {
        background-image: none;
        background-color: var(--color-nottheme); }

    .block .block-hr {
        background-color: var(--color-bg); }

    body[theme=dark] .HeaderUser-menu,
    body[theme=dark] .VueHeaderSearch-block {
        background-color: var(--color-block-bg);
        box-shadow: 0 0 1px rgba(0, 0, 0, 0.8); }

    body[theme=dark] .VueGamesRoomsOne._game_mode_4 {
        background-image: var(--color1); }

    body[theme=dark] .VueGamesRoomsOne._game_mode_4 ._time span {
        background: var(--color4);
        color: var(--color2); }

    /* buttons */
    body[theme=dark] .VueGamesRooms #create-room.button-competitive, 
    body[theme=dark] div.title.title-3 > input#create-room.button.title- 
    button.button-competitive:hover {
        --color-this-font: var(--color2);
        color: var(--color2); }

    body[theme=dark] .VueGame-stat {
        background-color: var(--color3); }

    body[theme=dark] .GamesMissions ._progressbar > div {
        background-color: var(--color2); }

    .profile-body-mfp, .VueGamesRoomsOne-body-members-one-stats span.mfp {
        display: none }
    body:not([theme]) .button.button-grass,
    .button:not([disabled]):not(.disabled):hover,
    body:not([theme]) .button.button-aqua {
        box-shadow: 0 0 1px rgba(0, 0, 0, 0.3); }

    .VueGamesRooms #create-room.button-competitive {
        --color-this: var(--color7);
        --color-this-hover: var(--color-gamemode-competitive-light);
        --color-this-font: var(--color-gamemode-competitive);
        border: solid 1px #ececed !important; }

    .profile-body-inventory .title .title-button:hover {
        background: var(--color1);
        color: var(--color4); }

    body:not([theme]) .button.button-grass {
        color: var(--color2);
        --color-this: var(--color-light);
        --color-this-hover: var(--color2); }

    body:not([theme]) .button:not([disabled]):not(.disabled):hover {
        color: var(--color-font); }

    body:not([theme]) .button.button-aqua {
        background-color: var(--color7);
        color: var(--color2); }

    body:not([theme]) input[type=checkbox].switcher:checked + div.switcher:not(.hack) {
        background-color: var(--color2); }

    .profile._nocover .profile-top-stat-actions-button {
        border-color: var(--color6);
        background: var(--color2);
        color: var(--color3); }

 /*[[sqr]]*/ 
 /*[[invx2]]*/ 

    .menu-item._selected {
        border-left: 3px solid var(--color2); }

    body:not([theme]) .menu-item._selected {
        background-color: var(--color9) }

    .header[mnpl-style=transparent-light] .header-menu-one {
        background-color: #ffffff00 !important; }

    .header[mnpl-style=transparent-white] .header-menu-one {
        background-color: var(--color10) !important; }

    .marketSearchPad .marketSearchPad-row .marketSearchPad-row-add .marketSearchPad-row-add-list .marketSearchPad-row-add-list-body {
        background-color: var(--color-block-bg);
        color: var(--color-font); }

    body:not([theme]) .HeaderUser-menu-one:hover {
        background-color: var(--color9);
        color: var(--color-font); }

    .profile-top-stat-actions-button {
        background-color: var(--color-bg);
        color: var(--color-font); }

    body:not([theme]) .profile-top-stat-actions-button:hover {
        background: var(--color8);
        color: var(--color2); }

    .profile-body-posts-list-one-bottom > div > a._like_liked:before,
    .profile-body-posts-list-one-bottom > div > div._like_liked:before {
        color: #ef0000; }

    .profile-body-posts-list-one-bottom > div > a._like._like_liked,
    .profile-body-posts-list-one-bottom > div > div._like._like_liked {
        background: #f6f7fb;
        color: #656f95 }

    .profile-body-posts-list-one-bottom > div > a._like._like_liked:hover,
    .profile-body-posts-list-one-bottom > div > div._like._like_liked:hover {
        background: #e6e9ed; }

    /* buttons_dark */
    body[theme=dark] .button.button-grass {
        --color-this: var(--color3);
        --color-this-hover: var(--color8);
        --color-this-font: var(--color2); }

    body[theme=dark] input[type=checkbox].switcher:checked + div.switcher:not(.hack) {
        background-color: var(--color1); }

    body[theme=dark] input[type=checkbox].switcher:checked + div.switcher:not(.hack):before,
    .header[mnpl-style=transparent-white] .header-findgame > a,
    .header[mnpl-style=transparent-white] .header-menu-one .badge {
        background-color: var(--color-nottheme1) !important; }

    .header[mnpl-style=transparent-white] .header-menu-one:hover {
        background-color: var(--color-nottheme2) !important; }

    body[theme=dark] .menu-item:not(._selected):hover {
        background-color: var(--color8); }

    body[theme=dark] .menu-item._selected {
        background-color: var(--color9); }

    body[theme=dark] div.VueSettingsProfile-profile-upload-wrapper > .button {
        background-color: #f58e781a;
        color: var(--color1); }

    body[theme=dark] .selecter-options-value:not(._selected):hover {
        background-color: var(--color5); }

    .marketSearchPad .marketSearchPad-row .marketSearchPad-row-add {
        background-color: var(--color-bg); }

    .marketSearchPad .marketSearchPad-row .marketSearchPad-row-add body[theme=dark].marketSearchPad-row-add-list .marketSearchPad-row-add-list-body {
        background-color: var(--color-block-bg);
        box-shadow: 0 0 1.6px rgba(0, 0, 0, 0.4); }

    body[theme=dark] .profile._nocover .profile-top-stat-actions-button {
        border-color: var(--color3);
        background: var(--color3);
        color: var(--color1); }

    .marketSearchPad .marketSearchPad-row .marketSearchPad-row-add .marketSearchPad-row-add-list .marketSearchPad-row-add-list-arrow {
        height: 20px; }

    .marketSearchPad .marketSearchPad-row .marketSearchPad-row-add .marketSearchPad-row-add-list .marketSearchPad-row-add-list-arrow:before {
        border-bottom-color: var(--color2); }

    .marketSearchPad .marketSearchPad-row .marketSearchPad-row-add .marketSearchPad-row-add-list .marketSearchPad-row-add-list-body .marketSearchPad-row-add-list-body-one:hover {
        background-color: var(--color9); }

    body[theme=dark] .profile-top-stat-actions-button:hover {
        background: var(--color9) !important;
        color: var(--color2) !important; }

    body[theme=dark] .VueGamesRooms #create-room.button-competitive {
        --color-this-font: var(--color-gamemode-competitive-light);
        --color-this: var(--color4);
        --color-this-hover: var(--color5);
        border: solid 1px var(--color5) !important; }

    body[theme=dark] div.title.title-3 > input#create-room.button.title-button.button-competitive:hover {
        color: var(--color-gamemode-competitive-light);
        background-color: var(--color5); }

    .GamesNewroomMaxplayers > div > div._selected {
        background: var(--color2); }

    body[theme=dark] .GamesNewroomMaxplayers > div > div._selected {
        background: var(--color1);
        color: var(--color5); }

    .games-disconnect-button > .button.button-grass {
        color: var(--color2);
        background-color: var(--color-grapefruit-600); }

    body[theme=dark] .profile-body-posts-list-one-bottom > div > a,
    body[theme=dark] .profile-body-posts-list-one-bottom > div > div {
        background: #2b3034;
        color: #ffffff; }

    body[theme=dark] .profile-body-posts-list-one-bottom > div > a._like._like_liked,
    body[theme=dark] .profile-body-posts-list-one-bottom > div > div._like._like_liked {
        background: #2b3034;
        color: #ffffff; }

    /* market_block */
    body[theme=dark] div.games-market-list-one {
        background: var(--color-block-bg) !important; }

    .games-market-list-one-more-button button {
        background: var(--color-bg); }

    /* inversion */
    .col-8 {
        display: grid;
        grid-column: span 8;
        min-width: 0;
        grid-column-start: 1;
        grid-column-end: 9;
        grid-row-start: 1; }

    div.block.games-market {
        grid-row-start: 6; }

    .col-4 {
        grid-column: span 4;
        grid-column-start: 9;
        grid-column-end: 14; }

    div.col-4.footer-left {
        grid-column: span 4;
        grid-column-start: 1;
        grid-column-end: 5; }

    div.Gchat.block.block-notrimcontent,
    div.VueGamesRooms {
        width: 645px; }

    /* scrollbar */
    ::-webkit-scrollbar {
        width: 12px; }

    ::-webkit-scrollbar-track {
        border: none;
        background: none;
        border-radius: 10px; }

    ::-webkit-scrollbar-thumb {
        background-color: var(--color-scroll);
        border-radius: 10px; }

    /* extras */
    div.GamesNewroom-modes-one._gmshuffle > .GamesNewroom-modes-one-title:after {
        visibility: visible !important;
        content: ' игрушка дьявола во первых'; }

    div.GamesNewroom-modes-one._gmshuffle > .GamesNewroom-modes-one-subtitle {
        display: none; }

    /* old time yet */
    div.table-body-players-card-body {
        display: grid;
        grid-template-rows: 4fr 1fr; }

    div.table-body-players-card-body-money {
        grid-row-start: 1;
        grid-column-start: 1;
        grid-column-end: 3;
        margin-top: 12px;
        font-size: 26px;
        font-weight: 300; }

    div.table-body-players-card-body-nick,
    div.table-body-players-card-body-avatar {
        grid-row-start: 2; }

    .table-body-players-card-body-avatar > div {
        width: 40px;
        height: 40px; }

    .table-body-players-card-body-avatar > div > div {
        border: 1px solid #161a1b; }

    .table-body-players-card-body-avatar > div {
        padding: 2px; }

    .table-body-players-card-body-nick {
        z-index: 2;
        justify-content: left;
        margin-top: 14px;
        height: 20px; }

    .table-body-players-card-body-timer {
        grid-row-start: -1;
        grid-row-end: -2;
        grid-column-start: 1;
        position: absolute;
        top: -18px;
        left: -55px; }

    .table-body-players-card-body-timer div {
        background: none;
        color: var(--color6);
        font-size: 20px; }

    .table-body-board-fields-one-label,
    .TableFieldcard-top {
        color: var(--color5); }

    .TableAction-buttons > div:nth-child(1):hover {
        background: var(--color2); }

    .TableAction-buttons > div:nth-child(1) {
        background: var(--color1);
        color: var(--color5); }

    .table-body-players-card[mnpl-action_player] {
        color: #212121; }

    .table-body-players-card-body-nick ._nick {
        overflow: auto;
        max-width: 130px; }
    .table-body-board-chatbottom>div._channel[mnpl-ch=all], .table-body-board-chatbottom>div._channel[mnpl-ch=team] {
        color: var(--color4); }

/*[[m1Pro]]*/  
/*[[0to4]]*/ 

.ion-close-circled:before {
    color: #ff4343bf; }
div.avail.ion-plus-circled {
    color: #a7ff5cbf; }

/*[[font]]*/  
/*[[pts]]*/  

}

options.value:
/* 
@name           amps
@namespace      USO Archive
@author         Artemy Khisamiev
@description    `Дополнение к плагину M1Pro`
@version        20220415.18.47
@license        CC-BY-NC-4.0 
*/
body[theme=dark],
element.style,
html {
    background: var(--color-bg) !important }
.Info-pages > div.selected {
    background-color: var(--color1); }
* {
    font-weight: 400 !important; }
.blocksbox {
    margin-left: 14px; }
.profile-body-ingame {
    position: absolute;
    left: -1200px;
    top: 335px;
    display: flex;
    align-items: center; }
.profile-body-ingame-text {
    margin-top: 8px; }

.profile-top-cover {
    height: 110%; }
div.block.profile-body-inventory {
    margin-top: 25px; }

.games-market-list-one._m1pro .games-market-list-one-more-image {
    background-image: none; }
.VueGamesRoomsOne-body-members-one-stats span:before {
    display: none; }
.Info-pages .Info-btn-pro {
    position: relative;
    background-image: url(https://3.downloader.disk.yandex.ru/preview/3de13086170affd952cfd862689c238d5af812eadc24497b1cdd589c1413b263/inf/8AHjPLFugmUsDxYc7GKudH-n6wYZ7eKbGoMob9nJKzXvLvIoeTvJksHCc7wyApruLfaB0c0MXfPUI427GGgbGQ%3D%3D?uid=403766280&filename=rr69tzCKV7o.jpg&disposition=inline&hash=&limit=0&content_type=image%2Fjpeg&owner_uid=403766280&tknv=v2&size=1349x652); }

.header[mnpl-style="transparent-white"],
.header[mnpl-style="transparent-light"] {
    background: none !important; }

.header .widther {
    top: 13px;
    left: 15px;
    background: var(--color-block-bg);
    border: 1px solid var(--color10);
    border-radius: var(--border-radius);
    box-shadow: 0 0 1px rgb(0 0 0 / 30%);
    height: 96% !important; }

.col-9, .grid-9, .grid-medium-9 {
    grid-column-end: span 9;
    min-width: 0;
    width: 1500px; }

div.Gchat.block.block-notrimcontent,
div.VueGamesRooms {
    width: auto !important; }

.Inventory2-items {
    display: grid;
    grid-template-columns: repeat(9, 1fr) !important;
    grid-gap: 10px; }

div.Inventory2.grid.widther {
        margin-left: 3%;
        margin-top: 1% !important; }

div.block._nomarginbottom.profile-body-note {
        background-color: #00000017; 
        box-shadow: none !important;
        display: block;
        margin-top: 20px;}

.profile-body-level {
    flex-direction: row-reverse; }
.profile-body-level-info {
    padding-right: 15px; }
.profile-body-level-info-left {
    right: 188px; }
.profile-body-level-info-rank {
    display: flex;
    flex-direction: row-reverse; }
.profile-body-level-info-level {
    display: flex;
    flex-direction: row-reverse;}

body[theme=dark] .GamesMissions ._progressbar {
    background: #2b3d3d; }

div.table-body-players-card-body-timer {
    color: var(--color7);
    margin-left: -20px !important;
    margin-top: -28px !important; }

.table-body-players-card[mnpl-leaved] .table-body-players-card-body-money {
    text-align: right; }

.table-body-players-vs .table-body-team span {
    font-size: 16px; }

body:not([mnpl-newcolors]) .table-body-players-card-body-avatar > div > div {
    filter: brightness(0.5);
    border-radius: 0px;
    width: 171px !important;
    height: 130px !important;
    background-position: center;
    background-size: cover; }
.table-body-board-chat-message-users ._arrow {
    background: none !important }

.table-body-board-chat-message-users ._arrow:after {
    content: "\f215";
    font-size: 16px;
    color: var(--color7);
    padding-top: 1px;
    margin-left: 5px; }

.table-body-board-chat-message-users {
    color: var(--color5); }

.table-body-board-chat-message-users ._message._to {
    margin-right: 0px;    
    margin-left: 5px;
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
    padding-left: 6px;
    transform: none; }

.table-body-board-chat-message-users ._message._from {
    margin-left: 2px;
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
    padding-right:6px;
    margin-right: 8px; }

div.widther.container.profile-body {
        width: 90% !important;
        top: 15px; }

.nc {
        display: none; }

.col-8 {
    margin-left: 10px; }

.widther {
    top: 8px;
    left: 12px; }

.block:not(.nomargin) {
    margin-bottom: 14px; }

/* big profile pic on game board*/
body[theme=dark] label[for="table-opt-oldcolors"]:after {
    content: 'Плагин m1pro установлен';
    color: #add884; }

body:not([theme]) label[for="table-opt-oldcolors"]:after {
    content: 'Плагин m1pro установлен';
    color: #add884; }

body:not([mnpl-newcolors]) .profile-top-stat-badge {
    bottom: -45px !important;
    top: auto;
    left: 130% !important; }

.profile-top-info-avatar-image, .profile-top-info-avatar {
    width: 250px;
    height: 250px; }

.profile-top-info-avatar._online {
    width: 265px;
    height: 265px; }

.profile-top-stat {
    margin-top: 80px;
    margin-left: 30px;}

.profile-top-stat-list {
