style.css:
@-moz-document url-prefix("https://snahp.it") {
/*
	Snahp.it DARK MODE

	Author : jiraph
	CC     : CC BY-NC-SA
	Version: 1.0.5
	Date   : 2021-05-06
*/


/*BACKGROUND*/
body {background-color: #26272b;}
div[class="container group"] {display:none}
#header {background:#00000000; background-color:#00000000;}


/*SIDEBARS*/
.sidebar .sidebar-content, .sidebar .sidebar-toggle {background: #34373c;}
.container-inner>.main::after, .container-inner>.main::before {background-color: #34373c;}
.widget, .widget a,.alx-posts .post-item-date, .alx-tab .tab-item-date, .alx-tab .tab-item-name, .widget_recent_comments ul li, .widget_recent_entries ul li, .page-title a {color: #ffffffb3;}
.alx-posts .post-item-title a, .alx-tab .tab-item-comment a, .alx-tab .tab-item-title a, h1, h2, h3, h4, h5, h6 {color:#ffffff;}
.alx-posts li, .alx-tab li, .widget_archive ul li, .widget_categories ul li, .widget_links ul li, .widget_meta ul li, .widget_nav_menu ul li a, .widget_pages ul li a, .widget_recent_comments ul li, .widget_recent_entries ul li, .widget_rss ul li {border-bottom: 1px solid #3e4147;}
.widget a:hover, .widget>h3 a, .widget>h3 a:hover {color:#ffffff;}
div[id="text-3"] {display:none;}


/*SEARCH*/
.themeform input[type=text], .themeform input[type=password], .themeform input[type=email], .themeform input[type=url], .themeform input[type=tel], .themeform input[type=number], .themeform select, .themeform textarea {background: #3e4147; border:0px;}
.search-expand {background:#26272b; box-shadow: 0px 4px 7px 0px #2b2d31}
.toggle-search.active, .toggle-search:hover, .search-expand-inner, #nav-topbar .nav>li:hover>a, #nav-topbar .nav>li>a:hover {background: #34373c}
.themeform input[type=text], .themeform input[type=password], .themeform input[type=email], .themeform input[type=url], .themeform input[type=tel], .themeform input[type=number], .themeform select, .themeform textarea  {color:#ffffffb3}
.themeform input[type=text]:focus, .themeform input[type=password]:focus, .themeform input[type=email]:focus, .themeform input[type=url]:focus, .themeform input[type=tel]:focus, .themeform input[type=number]:focus, .themeform select:focus, .themeform textarea:focus {color:#ffffff}
.notebox {background:#34373c; color:#ffffffb3}
.notebox:before {border-bottom: 8px solid #34373c}
.notebox:after {border-bottom: 6px solid #34373c}

.search-form input[type="search"] {border:2px #26272b solid !important; background: #26272b; border-radius: 4px; color: #ffffffb3;}
.search-form input[type="search"]:focus {color: #fff}


/*BODY*/
.page-title {background:#f7a840; border-bottom: 0px; padding: 18px 30px;}
div[class="pad group"] {background:#3e4147;}
.page-title h1, .page-title h2, .page-title h4 {color:#ffffff;}
.page-title span {color:rgba(255,255,255,.8);}
.featured, .featured.flexslider {border-bottom: 1px solid #34373c;}
.post-list .post-row {border-bottom: 1px solid #34373c;}
.entry.excerpt {color:#ffffffb3;}
.wp-pagenavi a:active, .wp-pagenavi a:hover, .wp-pagenavi span.current {color:#ffffffb3}
/*SHOWCASED*/
.post-title a {color:inherit;}
.post-title {color:#ffffff;}
#flexslider-featured .flex-direction-nav a {background: inherit; font-size: 16px; height: 34px !important; height: 30px !important; left: 12px;}
ul[class="flex-direction-nav"]{background:#00000000; top: -35px !important; margin-top: 5px}
#flexslider-featured .flex-direction-nav a::before {top: 3px;}
#flexslider-featured .flex-direction-nav li {margin-left: 0px;}
#flexslider-featured .flex-control-nav {top: -30px;}
#flexslider-featured .flex-control-nav li {margin-right: 5px; height: 30px  !important;}
#flexslider-featured .flex-control-nav li a {width: 50px; height: 10px; border-radius: 1px; margin-top: 10px}


/*FOOTER*/
section[id="footer-full-width-widget"], nav[id="nav-footer"] {display:none;}
#page {padding-bottom:0;}
#footer-bottom {background:#26272b;}
#footer-bottom #back-to-top:hover {color:#c5c6c8}


/*MEDIA PAGE FIXES*/
.post-nav li a span {color:#ffffffb3}
body {color:#ffffff;}
a:hover {color:#ffffffb3;}
.main {background-color:#3e4147 !important;}
.themeform input[type=text], .themeform input[type=password], .themeform input[type=email], .themeform input[type=url], .themeform input[type=tel], .themeform input[type=number], .themeform select, .themeform textarea {background:#575a60;}
#commentform {background: #34373c;}
.themeform label {color:#ffffffb3;}
.commentlist .comment-body p, .commentlist .fn, .pinglist .ping-link {color:#ffffff;}
.post-tags a {background:#34373c;}
.post-tags a, .post-tags span {color:#ffffffb3}
.sidebar .post-nav li a, .sidebar .post-nav li.next a, .sidebar .post-nav li.previous a {border-bottom: 1px solid #3e4147 }
.sidebar .post-nav li a {border-top: 0px}
.post-nav li a strong {color: #ffffff}
.page-title a:hover, .post-nav li a:hover span {color: #ffffff}
.entry blockquote {color: #ffffffb3}
.comment-tabs li a {color: #ffffffb3}
.comment-tabs li a:hover {color: #ffffff}
.comment-tabs li span {background-color: #34373c}
.comment pre, .entry pre {background:#34373c; border:none !important}
.comment pre:not(.wp-block-code), .entry pre:not(.wp-block-code) {background:#34373c; border:none !important}


}
