style.css:
@-moz-document domain("forum.snahp.it") {
/*
	forum.snahp.it DARK MODE - BASIC THEME

	Author : jiraph
	CC     : CC BY-NC-SA
	Version: 1.0.2
	Date   : 2020-01-10
*/



/*GLOBAL*/
body {
    background-color: #26272b
}
html,
body {
    color: #ffffff !important
}



/*HEADER*/
.navbar.tabbed.not-static + .navbar.secondary {
    background-color: #34373c;
    background: #34373c;
    border: 1px solid #34373c
}
.navbar.tabbed.not-static .nav-tabs li.tab {
    background-color: #34373c;
    border: 1px solid #34373c
}
.navbar.tabbed.not-static .nav-tabs li.tab.selected:after {
    display: none
}



/*SEARCH*/
.inputbox,
select,
.inputbox:hover,
select:hover {
    background-color: #575a60;
    border: 1px solid #575a60
}
::placeholder {
    color: #fff !important;
    opacity: 1
}
.search-header,
.search-header:hover {
    border: 1px solid #575a60
}
.search-box .inputbox,
.search-box .inputbox:hover,
.search-box .inputbox:focus {
    border: 0px
}
.inputbox,
select,
.inputbox:focus,
select:focus {
    color: #fff
}



/*BOARD INDEX*/
.navbar {
    background: #34373c;
    border: 1px solid #34373c
}



/*THREADS*/
.forabg ul.topiclist li.header,
.forumbg ul.topiclist li.header,
.forabg {
    background: #34373c;
    border: 1px solid #34373c
}
ul.topiclist {
    background: #3e4147;
    border: 0px solid #3e4147;
    border-radius: 2px
}
li.row {
    border-bottom: 0px;
    border-top: 1px solid #34373c
}
.forum-statistics,
li.row .forum-lastpost strong,
li.row .subforums-list strong {
    color: #ffffffb3
}
.forum-statistics .value,
ul.topiclist li {
    color: #ffffff
}
a,
.navbar.tabbed.not-static + .navbar.secondary,
.navbar .nav-tabs .nav-link {
    color: #25a0f8
}
.forumbg {
    background: #34373c;
    border: 1px solid #34373c
}



/*FOOTER*/
h3 {
    color: #25a0f8
}
.copyright {
    color: #ffffffb3
}



/*POPUP*/
.phpbb_alert {
    background-color: #26272b;
    border-color: #26272b;
    border-radius: 2px
}



/*-------PAGES-------*/
/*BODY*/
.bg1 {
    background-color: #3e4147;
    border: 1px solid #3e4147
}
.bg2 {
    background-color: #34373c;
    border: 1px solid #34373c
}
.content,
.post:target .content {
    color: #fff
}
.postbody {
    color: #ffffffb3
}
blockquote {
    background-color: #26272b
}

/*TEXT COLOR CONTRAST FIXES*/
h3 a,
.content h2,
.panel h2,
.post:target h3 a {
    color: #25a0f8
}
.postlink:visited {
    color: #1960ff
}
.postlink {
    color: #1ebeff
}

/*ACTION BAR*/
.button,
.buttons > .button.locked-icon {
    background-color: #4f535b;
    background-image: none;
    color: #25a0f8;
    border-color: #4f535b
}

/*LEFT PROFILE SIDEBAR*/
.postprofile strong {
    color: #ffffff
}
.postprofile {
    color: #ffffffb3;
    border-color: #676a76
}

/*QUICK REPLY*/
.panel,
.panel-container > form > .submit-buttons {
    background-color: #34373c;
    border: 1px solid #34373c
}
label {
    color: #ffffffb3
}
.inputbox:focus,
select:focus {
    background-color: #3e4147
}
.inputbox,
select,
.inputbox:hover,
select:hover {
    background-color: #3e4147
}
.content h2,
.panel h2 {
    border-bottom-color: #676a76
}
fieldset dl:hover dt label {
    color: #ffffff
}
.inputbox:hover,
select:hover {
    color: #25a0f8
}
a.button1,
input.button1,
input.button3,
a.button2,
input.button2 {
    color: #25a0f8;
    background-color: #4f535b;
    border-color: #4f535b;
    background-image: none
}

/*FULL REPLY*/
.bg3.panel {
    background-color: #34373c;
    border: 1px solid #34373c;
    background-image: none
}
#tabs .activetab > a,
#tabs .activetab > a:hover {
    background: #34373c !important;
    border-color: #34373c !important
}

/*POLL*/
fieldset.polls dl {
    color: #ffffff;
    border-color: #676a76
}

/*CARDS*/
.twbs .card {
    background-color: #26272b;
}

/*MEDIAINFO*/
.mediainfo {
    background-color: #26272b!important;
}
.mediainfo .title {
    background-color: #34373c
}
.mediainfo .general,
.mediainfo .video,
.mediainfo .audio {
    border-color: #676a76
}

/*CODEBOX*/
.codebox,
.codebox p,
blockquote,
blockquote cite {
    background-color: #26272b;
    border-color: #676a76;
}
.codebox code {
    color: lime;
}

/*HIDEBOX*/
dl.hidebox,
.hidebox.unhide {
    background-color: #26272b;
    border-color: #676a76;
}
dl.hi > dd {
    color: #fff
}

/*IMDB CARD*/
.imdb-template11,
.imdb-template180 .caption {
    background-color: #26272b
}
.MuiRating-iconEmpty {
    color: #000
}



/*USER CONTROL PANEL*/
.panel-container .panel {
    background: #3e4147
}
.panel,
.panel-container > form > .submit-buttons,
.panel-container .panel li.header dd,
.panel-container .panel li.header dt {
    color: #ffffff
}
dd label {
    color: #ffffff
}
#navigation a {
    background: #3e4147;
    color: #25a0f8
}
dl.details dd {
    color: #ffffffb3
}
dl.details dt {
    color: #ffffff
}
#tabs .tab > a {
    background: #3e4147;
    border-color: #3e4147;
    color: #25a0f8
}
#tabs .tab > a:hover {
    background: #34373c;
    border-color: #34373c
}



/*FAQ*/
dl.faq dt,
.content dl.faq dt {
    color: #ffffff
}
h2 {
    color: #25a0f8
}



/*SHOP*/
.twbs .card {
    background-color: #3e4147;
    border: none;
}
.twbs .table-striped tbody tr:nth-of-type(2n+1) {
    background-color: #34373c;
}
.twbs .table {
    color: #fff;
}



}
