style.css:
@-moz-document domain("monstercat.com") {
	:root {
		--accent-color: /*[[accent]]*/ ;
		--font-color: /*[[fontColor]]*/ ;
		--container-background: /*[[containerBackground]]*/ ;
		--site-background: /*[[siteBackground]]*/ ;
	}

	img.logo-black,
	.client > div,
	.side-banner.reporting,
	.navbar-brand img,
	.symbol {
		filter: invert(1);
	}

	body,
	.controls,
	.controls.playing,
	.playing.controls.fixed,
	.tile,
	input,
	textarea,
	select,
	table.form select,
	.tool-tip--artist,
	.tag,
	.navbarFUV {
		background-color: var(--site-background);
		color: var(--font-color);
	}

	.bg-white,
	.bg-black,
	.publishing-articles .client,
	.empty td,
	.highlight-box,
	.global-search,
	.license-legend tr:nth-child(2n + 1) > td,
	tr:nth-child(2n) > td {
		background-color: var(--site-background);
	}

	.panel,
	.one-line,
	.events-no-featured-message,
	.publishing-articles .article-banner,
	.podcast-form input[type="text"],
	.podcast-form input[type="email"],
	.podcast-form input[type="number"],
	.podcast-form textarea,
	.podcast-form input[type="radio"] ~ label::before,
	.podcast-form input[type="checkbox"] ~ label::before {
		background-color: var(--container-background);
	}

	.scrub,
	.side-banner,
	.bg-black,
	.dropdown-menu,
	#front-form {
		background-color: var(--container-background);
	}

	.side-banner.reporting {
		background-color: #ccc;
	}

	.top-banner-gold .banner {
		filter: brightness(0.135);
	}

	#early-streaming > div:nth-child(1) > div,
	#content-creator-licenses > div:nth-child(1) > div,
	#support-the-artists > div:nth-child(1) > div,
	#shop-discounts > div:nth-child(1) > div,
	#discord > div:nth-child(1) > div,
	#reddit > div:nth-child(1) > div {
		filter: brightness(0.2);
	}

	#download-access > div:nth-child(1) > div:nth-child(1) {
		filter: invert(0.835);
	}

	.banner .gradient {
		background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, black 90%);
	}

	.gradient {
		background: linear-gradient(to right, rgba(255, 255, 255, 0) 0%, var(--site-background) 100%);
	}

	/* Buttons */
	button,
	.button {
		border-color: var(--accent-color);
		color: var(--font-color);
	}

	.button--cta {
		background: var(--accent-color);
	}

	/* Hover */
	button:active,
	button:hover,
	.button:active,
	.button:hover,
	.button--cta:active,
	.button--cta:hover {
		color: var(--font-color);
	}

	.cl-white a.button:hover,
	.cl-white a.button:active {
		color: var(--font-color);
	}

	button:active,
	button:hover,
	.button:active,
	.button:hover,
	.panel .button-list .selected-year {
		background: var(--accent-color);
	}

	.button--cta:active,
	.button--cta:hover {
		background: var(--site-background);
		border-color: var(--site-background);
	}

	/* Special Buttons  */
	.cl-white .button--home-view,
	.cl-white .button--cta:active,
	.cl-white .button--cta:hover,
	.cl-white .button.button--cta:hover {
		background: var(--site-background);
		border-color: var(--site-background);
		color: var(--font-color);
	}

	.cl-white .button--home-play:hover span {
		color: var(--font-color);
	}

	/* White Buttons */
	.button--white,
	.cl-white .button--white {
		background: var(--site-background);
		color: var(--font-color);
		border-color: var(--site-background);
	}

	.button--white:active,
	.button--white:hover,
	.cl-white .button--white:active,
	.cl-white .button--white:hover {
		background: var(--accent-color);
		border-color: var(--accent-color);
		color: var(--font-color);
	}

	/* Dark Buttons */
	.btn.btn-default {
		background: var(--container-background);
		color: var(--font-color);
		border-color: var(--container-background);
	}

	.btn.btn-default:active,
	.btn.btn-default:hover,
	.open > .dropdown-toggle.btn-default:focus,
	.open > .dropdown-toggle.btn-default:hover {
		background: var(--accent-color);
		color: var(--font-color);
		border-color: var(--accent-color);
	}

	/* Black Buttons */
	.button--black {
		color: var(--font-color);
		border-color: var(--container-background);
		background: transparent;
	}

	.button--black:active,
	.button--black:hover {
		color: var(--font-color);
		border-color: var(--font-color);
		background: transparent;
	}

	/* Transparent Buttons */
	.button--transparent,
	.button--transparent:active,
	.button--transparent:hover {
		background: transparent;
		border-color: transparent;
	}

	/* Gold Buttons */
	.button--mcatgold-dark {
		color: var(--site-background);
		background: #f1d383;
		border-color: #f1d383;
	}

	.button--mcatgold-dark:active,
	.button--mcatgold-dark:hover {
		color: goldenrod;
		border-color: goldenrod;
		background: var(--site-background);
	}

	.button--mcatgold {
		color: var(--font-color);
		border-color: #f1d383;
	}

	.button--mcatgold:active,
	.button--mcatgold:hover {
		color: var(--site-background);
		background: #f1d383;
	}

	/* Podcast Button */
	.button-form {
		background: var(--container-background);
		border-color: var(--container-background);
		color: var(--font-color);
	}

	.button-form:hover,
	.button-form:focus {
		background: var(--accent-color);
		color: var(--container-background);
		border-color: var(--accent-color);
	}

	/* Filter Buttons */
	.filter-list li button {
		background: var(--container-background);
		color: var(--font-color);
	}

	.filter-list li button:focus,
	.filter-list li button:hover,
	.filter-list li button:active,
	.filter-list li button.check {
		background: var(--accent-color);
		color: var(--font-color);
	}

	/* Text */
	.dropdown-menu li a,
	.sVotes .sLabel,
	.navbarFUV .navbar-text,
	h1,
	h2,
	h3,
	h4,
	h5,
	h6,
	.cl-white h1,
	.cl-white h2,
	.cl-white h3,
	.cl-white h4,
	.cl-white h5,
	.cl-white a,
	.white,
	.podcast-form input[type="radio"] ~ label,
	.podcast-form input[type="checkbox"] ~ label,
	.previous-podcast .title,
	.social-grid .social-grid-wrapper,
	.cl-white p.date,
	.event-list label.city,
	.mcat-mast .mcat-mobile-nav > ul li > a {
		color: var(--font-color);
	}

	/* Inputs */
	.global-search input[type="text"],
	.podcast-form input[type="text"],
	.podcast-form input[type="email"],
	.podcast-form input[type="number"],
	.podcast-form textarea,
	.podcast-form input[type="checkbox"] ~ label:hover::after,
	.podcast-form input[type="checkbox"]:checked ~ label::after,
	.podcast-form input[type="checkbox"]:checked ~ label:hover::after {
		color: var(--font-color);
	}

	input {
		border-color: var(--font-color);
	}

	input:focus,
	textarea:focus {
		border-color: var(--accent-color);
	}

	#search-input,
	#search-input:focus {
		background-color: var(--container-background) !important;
	}

	#search-input:focus {
		border-color: var(--accent-color) !important;
	}

	/* Lines */
	hr,
	.line-top::before,
	.mcat-mast .mcat-nav ul li a::after,
	.nav-item::before,
	.global-search::before,
	.cl-white .line-bottom::after,
	.cl-white .line-top::before,
	footer .footer-items li a::before,
	footer .footer-items li button::before,
	.mcat-mast .mcat-mobile-nav .hamburger-icon .line,
	.mcat-mast .mcat-mobile-nav .hamburger-icon.active .line {
		background: var(--font-color);
	}

	.loader {
		border-color: #666;
		border-top-color: var(--font-color);
	}

	/* Links */
	/* Normal */
	a,
	table.events a.buy-tickets,
	.mcat-mast .mcat-nav ul li a,
	a.silent,
	.link-bottom:focus,
	.link-bottom:hover,
	.drop-box a:focus,
	.drop-box a:hover,
	.link-bottom,
	.drop-box a,
	ul.gold-perks li,
	.cl-white .art-list li a label,
	.panel .button-list .selected-year,
	.sInfo .sTitle a {
		color: var(--font-color);
	}

	a svg {
		fill: var(--font-color);
	}

	/* Hover */
	/* Change color */
	a:hover,
	a:active,
	h1 a,
	h2 a,
	h3 a,
	h4 a,
	button.faux:active,
	button.faux:hover,
	.nav-item:active,
	.nav-item:hover,
	table.events a.external,
	table.events a.buy-tickets:hover,
	table.events a.buy-tickets:active,
	ul.gold-perks li:hover,
	.cl-white a:active,
	.cl-white a:hover,
	.cl-white button.faux:active,
	.cl-white button.faux:hover,
	.cl-white .art-list li a:hover label,
	.sInfo .sTitle a:hover {
		color: var(--accent-color);
	}

	a svg:hover {
		fill: var(--accent-color);
	}

	/* Keep color */
	.mcat-mast .mcat-nav ul li a:focus,
	.mcat-mast .mcat-nav ul li a:hover,
	button.faux {
		color: var(--font-color);
	}

	header:hover .scrub > .progress,
	.scrub-container--mobile .progress,
	.volume-slider-inner {
		background: var(--accent-color);
	}

	.toggle > .check:checked ~ .track {
		box-shadow: inset 0 0 0 20px var(--accent-color);
	}

	.flex.license-type .container-right.clearfix,
	.flex.license-type .container-right.clearfix p {
		color: var(--site-background);
	}

	.flex.license-type .container-right.clearfix .line-top::before {
		background: var(--site-background);
	}

	.creator-wrapper {
		background-image: linear-gradient(rgba(51, 51, 51, 0.6), rgba(51, 51, 51, 0.6)),
			url("https://www.monstercat.com/img/banner-2.jpg");
	}

	.cl-white p.date {
		opacity: 0.7;
	}

	.event-list label.city {
		opacity: 0.85;
	}

	.hover-wrapper {
		background: #111;
		color: var(--font-color);
	}

	.panel .button-list > li:last-child > a {
		border-right-color: var(--accent-color);
	}

	/* Mobile searchbar */
	.mcat-mobile-nav .global-search {
		padding: 10px;
	}

	/* Dropdown */
	.dropdown-menu > li > a:focus,
	.dropdown-menu > li > a:hover {
		background: var(--accent-color);
		color: var(--font-color);
	}

	/* Full width searchbar */
	body > header {
		display: flex;
		flex-direction: column;
	}

	.player {
		display: flex;
		flex-wrap: wrap;
	}

	#navigation {
		display: flex;
		flex-grow: 0;
	}

	.navigation > .nav-item {
		display: flex;
		align-items: flex-end;
		white-space: nowrap;
	}

	.controls,
	.controls.playing {
		display: flex;
		margin: 0;
		margin-left: auto;
		flex-wrap: nowrap;
		align-items: flex-end;
	}

	.controls > button,
	.controls.playing > button {
		flex: auto;
		padding-right: 2.5px;
		padding-left: 2.5px;
	}

	.global-search {
		display: flex;
		flex-grow: 1;
		align-items: flex-end;
	}

	.global-search form {
		display: flex;
		flex-grow: 1;
		align-items: flex-end;
	}

	.global-search input[type="text"] {
		max-width: none;
	}

	@media screen and (max-width: 992px) {
		.controls,
		#navigation,
		.col-xs-hidden {
			display: none;
		}

		.controls.playing {
			flex-wrap: wrap;
		}
	}

	@media screen and (min-width: 992px) {
		.global-search::after {
			content: "";
			display: inline-block;
			margin: 0 0.75rem 0 0;
			width: 2px;
			vertical-align: middle;
			height: 18px;
			background: #eee;
		}

		.global-search input[type="text"] {
			border-bottom: 1px solid var(--container-background);
			margin: 0 0.75rem;
		}

		.global-search input[type="text"]:focus {
			border-bottom: 1px solid var(--font-color);
		}
	}
}

@-moz-document domain("shop.monstercat.com") {
	ul#nav,
	header,
	.searchbox form #q,
	.footer-template,
	#product-description form,
	#product-description,
	#looked-at,
	#footer,
	input[type="text"],
	input[type="password"],
	input[type="email"],
	textarea,
	select,
	.input-wrapper {
		background-color: var(--site-background);
	}

	#nav-border {
		background-color: var(--font-color);
	}

	#product-description form,
	#product-description,
	#looked-at,
	#footer,
	input[type="text"],
	input[type="password"],
	input[type="email"],
	textarea,
	select {
		border-color: var(--font-color);
	}

	#footer p,
	#footer a,
	#footer h4,
	ul#footer-icons li a,
	.prod-title,
	#breadcrumb a,
	ul#nav li a,
	.backto a,
	#product-description form,
	#product-description,
	#looked-at,
	#footer,
	input[type="text"],
	input[type="password"],
	input[type="email"],
	textarea,
	select,
	input[type="text"]:focus,
	input[type="password"]:focus,
	input[type="email"]:focus,
	textarea:focus {
		color: var(--font-color);
	}

	h2 {
		color: var(--site-background);
	}

	#logo,
	.site-logo img {
		filter: invert(1);
	}

	.backto a:hover {
		color: var(--accent-color);
	}

	.na-box,
	.button,
	button,
	input[type="submit"],
	input[type="reset"],
	input[type="button"],
	.button:hover,
	button:hover,
	input[type="submit"]:hover,
	input[type="reset"]:hover,
	input[type="button"]:hover {
		background: var(--font-color);
		color: var(--site-background);
	}
}

@-moz-document url-prefix("https://www.monstercat.com/catalog")
{
	input,
	select {
		background-color: var(--container-background);
	}

	.button--cta:active,
	.button--cta:hover {
		background: var(--container-background);
		border-color: var(--container-background);
	}
}

@-moz-document url-prefix("https://www.monstercat.com/browse")
{
	.filter-list li button {
		background: var(--site-background);
		border-color: var(--site-background);
	}
}
