}
.poll__link {
	color: var(--orange);
}
.poll__footer {
	color: var(--orange);
}
.account__header__tabs__name h1 small {
	color: var(--green);
}

.account__header__extra__links a {
	color: var(--orange);
}
.account__header__extra__links a strong {
	color: var(--orange);
}
.account__header__bio .account__header__fields a {
	color: var(--green);
}
.fa-ellipsis-h::before {
	color: var(--orange);
}
