.mute-modal,
.report-modal {
	background-color: var(--darkest);
	color: var(--lightgrey);
	border: 1px solid var(--orangelight);
}
.button:active,
.button:focus,
.button:hover {
	background-color: var(--orange);
	opacity: .8;
}
.media-modal .extended-video-player video,
