style.css:
@-moz-document url-prefix("https://vk.com") {
/* Шрифт Google Sans */
  @font-face
    {
      font-family: 'Google Sans';
      font-style: normal;
      font-weight: 400;
      src: local('Google Sans Regular'), local('GoogleSans-Regular'), url(https://fonts.gstatic.com/s/googlesans/v5/4UaGrENHsxJlGDuGo1OIlL3Kwps.ttf) format('truetype');
    } 
  @font-face
    {
      font-family: 'Google Sans';
      font-style: normal;
      font-weight: 500;
      src: local('Google Sans Medium'), local('GoogleSans-Medium'), url(https://fonts.gstatic.com/s/googlesans/v5/4UabrENHsxJlGDuGo1OIlLU94Yt3CwM.ttf) format('truetype');
    }
  /* Шрифт Google Sans */

    :root
    {
      --main-color: #3970B8;
      --main-color-hover-opacity: #3970B8;
      --main-background-color: #fcfcfc;
      --main-section-color: #fff;
      --main-wide-mode-width: 1110px;
      --main-offset-top: 56px;
      --main-menu-indicator-x: calc(100% - 20px);
      --main-menu-indicator-y: calc(50% - 2px);
      --main-compact-mode-columns: 3;
    }
  .top_home_link.top_home_ny_logo .top_home_logo {
    margin: 15px 0px 0px 55px !important;
  }
  /* Кнопка назад / вверх */
    #layer_stl
    {
      display: flex;
      justify-content: center;
      align-items: center;
    }

    #layer_stl_bg, #stl_bg
    {
      width: 90px;
    }

    #layer_stl_text, #stl_text
    {
      position: fixed;
      height: 50px;
      margin: 0;
      padding: 0;
      width: 90px;
      top: calc(50% - 25px);
      background: url("data:image/svg+xml;utf8,<svg fill='rgba(255, 255, 255, 1)' height='50' x='0' y='0' viewBox='0 0 24 24' width='50' xmlns='http://www.w3.org/2000/svg'><path d='M8.12,14.71L12,10.83l3.88,3.88c0.39,0.39,1.02,0.39,1.41,0l0,0c0.39-0.39,0.39-1.02,0-1.41l-4.59-4.59c-0.39-0.39-1.02-0.39-1.41,0l-4.59,4.59c-0.39,0.39-0.39,1.02,0,1.41l0,0C7.09,15.09,7.73,15.1,8.12,14.71z'/></svg>") center center no-repeat;
      color: transparent;
    }
	.top_home_link.top_home_ny_logo .top_home_logo {
		margin: 15px 0px 0px 55px !important;
    }
    #layer_stl_text.down, #stl_text.down {
      background: url("data:image/svg+xml;utf8,<svg fill='rgba(255, 255, 255, 1)' height='50' x='0' y='0' viewBox='0 0 24 24' width='50' xmlns='http://www.w3.org/2000/svg'><path d='M8.12,9.29L12,13.17l3.88-3.88c0.39-0.39,1.02-0.39,1.41,0l0,0c0.39,0.39,0.39,1.02,0,1.41l-4.59,4.59c-0.39,0.39-1.02,0.39-1.41,0l-4.59-4.59c-0.39-0.39-0.39-1.02,0-1.41l0,0C7.09,8.91,7.73,8.9,8.12,9.29z'/></svg>") center center no-repeat;
    }

    .stl_active.over_fast #stl_bg
    {
      background-color: #eee;
    }
  /* Кнопка назад / вверх */

  /* Левое меню */
  /*[[leftmenu]]*/
  /* Левое меню */

    .left_count_wrap, #side_bar ol li .left_row:hover .left_count_wrap, .font_medium .left_count_wrap, .font_medium #side_bar ol li .left_row:hover .left_count_wrap
    {
      text-align: center;
      background-color: var(--main-color);
      padding: 0 !important;
    }

  /* Анимация лого */
    .top_home_link
    {
      position: relative;
      overflow: hidden;
    }

    .top_home_link:after
    {
      z-index: 0;
      position: absolute;
      width: 10px;
      height: 10px;
      top: calc(50% - 5px);
      left: calc(50% - 5px);
      content: '';
      visibility: hidden;
      border-radius: 50% !important;
      background: rgba(0, 0, 0, .5);
      opacity: 1;
    }

    .top_home_link .top_home_logo
    {
      margin: 13px auto 0;
    }

  /*[[logoanim]]*/

    .top_home_link:active:after
    {
      transform: scale(17) !important;
    }

  @-moz-keyframes logo
    {
      0% {
          transform: scale(1);
      opacity: 1;
    }

    100%
    {
      transform: scale(20);
      opacity: 0;
    }
  }

  @-webkit-keyframes logo
  {
    0%
    {
      transform: scale(1);
      opacity: 1;
    }

    100%
    {
      transform: scale(20);
      opacity: 0;
    }
  }

  @-moz-keyframes logocircle
  {
    0%
    {
      transform: scale(0);
      opacity: 1;
    }

    100%
    {
      width: 10px;
      height: 10px;
      transform: scale(10);
      opacity: .2;
    }
  }

  @-webkit-keyframes logocircle
  {
    0%
    {
      transform: scale(0);
      opacity: 1;
    }

    100%
    {
      width: 10px;
      height: 10px;
      border-radius: 50%;
      transform: scale(10);
      opacity: .2;
    }
  }

  .audio_page_player_fixed_shown {
      padding-top: 20px
  }

  /* Анимация лого */

  /* Анимация музыки в шапке */
  .top_audio_player .top_audio_player_play > div
  {
    margin: 0 auto 0;
  }

  .top_audio_player.top_audio_player_playing .top_audio_player_play > div
  {
    background-position: -15px -78px;
  }

  .top_audio_player .top_audio_player_play
  {
    width: 30px;
    height: 30px;
    padding: 0 !important;
    text-align: center;
    margin-top: 6px;
    border-radius: 50%;
  }

  .top_audio_player.top_audio_player_playing .top_audio_player_play
  {
    animation-name: playbtnaudio;
    animation-duration: 2.5s;
    animation-iteration-count: infinite;
    animation-timing-function: linear;
    overflow: visible;
  }

  @-moz-keyframes playbtnaudio
  {
    0%
    {
      box-shadow: none;
    }

    50%
    {
      box-shadow: 0 0 0 15px rgba(0, 0, 0, .2);
      background-color: rgba(0, 0, 0, .2);
    }

    100%
    {
      box-shadow: 0 0 0 30px rgba(0, 0, 0, 0);
      background-color: rgba(0, 0, 0, 0);
    }
  }

  @-webkit-keyframes playbtnaudio
  {
    0%
    {
      box-shadow: none;
    }

    50%
    {
      box-shadow: 0 0 0 15px rgba(0, 0, 0, .2);
      background-color: rgba(0, 0, 0, .2);
    }

    100%
    {
      box-shadow: 0 0 0 30px rgba(0, 0, 0, 0);
      background-color: rgba(0, 0, 0, 0);
    }
  }
  /* Анимация музыки в шапке */

  .page_block
  {
    box-shadow: 0 1px 1px 0 rgba(60,64,67,.05), 0 1px 3px 1px rgba(60,64,67,.08);
    border-radius: 6px;
  }

  .wall_module > .page_block:first-child
  {
    border-bottom: 0;
    border-radius: 3px 3px 0 0;
  }

  .page_list_module .line_cell .desc_info, .page_list_module .line_cell .extra_info, .page_list_module .line_cell .info
  {
    width: 146px;
  }

  /* Опросы */
  .page_media_poll_wrap
  {
    border-radius: 6px;
    border: 1px solid #e8eaed;
  }

  .page_media_poll .page_poll_percent, .page_media_poll .page_poll_row, .media_voting_option, .media_voting_option_bar
  {
    border-radius: 20px;
  }

  .page_media_poll .page_poll_text
  {
    color: #222;
  }
  /* Опросы */

  /* Широкий ВК */
  body:not(.static_header) #page_layout, body:not(.static_header) #page_header
  {
    width: var(--main-wide-mode-width, 1080px) !important;
  }

  body:not(.static_header) #page_body
  {
    width: 915px !important;
  }

  .dev_main_page #dev_page_wrap2, .dev_main_page #page_body, .dev_main_page #page_layout, .dev_main_page .scroll_fix
  {
    width: 100%!important;
    padding: 0px!important;
    margin: 0px!important;
    float: none!important;
  }

  .blog_page #page_body
  {
    width: var(--main-wide-mode-width, 1080px) !important;
  }

  #ads_hero_unit, #ads_page_wrap2, #ads_page_wrap1
  {
    width: 672px !important;
  }

  .videocat_row, .videocat_featured, .video_block_layout, .videocat_featured_playlists, .video_subtab_pane
  {
    width: 100%;
    margin: 0;
    padding: 16px;
    box-sizing: border-box;
  }

  .video_subtab_pane
  {
    padding: 0 20px 20px 20px;
  }

  .video_block_layout .videocat_row .video_cat_morebtn_wrap, .video_block_layout .video_cat_morebtn_wrap
  {
    margin: 0;
  }

  .video_item
  {
    width: calc(100% / 3 - 10px);
    margin: 0;
    margin-bottom: 18px;
  }

  .video_item .video_item_thumb, .video_items_list
  {
    width: 100%;
  }

  .reply_text .page_post_sized_thumbs
  {
    display: block;
  }

  .videocat_featured_playlist
  {
    width: 47%;
  }

  .eltt.top_audio_layer
  {
    width: 914px !important;
  }

  .friends_recom_list
  {
    display: flex;
    flex-wrap: wrap;
  }

  .friends_find_user
  {
    width: 22%;
    margin: 0 14px 0 0;
  }

  .apps_featured_slider, .apps_recent_apps_wrap .apps_recent_all
  {
    width: 100%;
  }

  .apps_recent_block
  {
    width: calc(100% - 362px);
  }

  .apps_cat_row
  {
    margin-right: 9px;
  }

  .apps_cat_wrap .apps_cat_row:nth-child(-n+6)
  {
    margin-top: 15px;
  }
  /* Широкий ВК */

  /* Шапка */
  #page_header_cont, #dev_top_nav_wrap
  {
    height: 56px;
  }

  #page_header_cont .back, #page_header_wrap, #page_header, #page_header .content, #top_nav, .head_nav_item, .top_home_link, .top_nav_btn, #dev_top_nav, .dev_home_wrap, #dev_top_nav a.dev_home_link, #dev_top_nav a.dev_top_link
  {
    height: 100%;
  }

  #page_header_cont .back
  {
    box-shadow: 0 2px 5px rgba(0, 0, 0, .05);
    border: none;
  }

  #top_profile_link
  {
    width: 56px;
    height: 100%;
    padding: 0;
    float: right;
  }
  .top_profile_name {
      display: none;
  }

  #top_notify_btn .top_nav_btn_icon
  {
    position: relative;
    height: 50%;
    margin-top: 25%;
  }

  #top_profile_link:after, .top_nav_link:after, .top_nav_btn:after, #top_notify_btn .top_nav_btn_icon:after
  {
    position: absolute;
    content: '';
    width: 0;
    height: 0;
    left: calc(50% - 5px);
    top: calc(50% - 5px);
    border-radius: 50%;
    transition: all .24s ease-in-out;
  }

  input.text.ts_input
  {
    border-radius: 30px;
    width: 100%;
    height: 38px;
    padding: 0 8px 0 36px;
    margin-top: 10px;
    transition-timing-function: ease-in-out;
    transition-duration: .24s;
      border-left: 15px solid transparent;
      background-position: left 12px;
  }

  input.text.ts_input:focus
  {
    background-color: rgba(255, 255, 255, 1) !important;
    color: #444;
    box-shadow: 0 1px 5px rgba(0, 0, 0, .1);
  }

  .top_notify_count
  {
    top: 7px;
    left: 28px;
    background: #f33;
    font-size: 12px;
    height: 18px;
    min-width: 12px;
    padding: 0 3px;
    line-height: 18px;
  }

  .head_nav_item_player
  {
    left: 100%;
    height: 100%;
    width: 56px;
  }

  .top_audio_player
  {
    height: 100%;
    position: absolute;
    left: 83%;
    width: 56px;
    padding-right: 0;
    transition-duration: .24s;
    background: #fff !important;
  }

  .top_audio_player:hover
  {
    width: 100px;
  }

  .top_nav_btn.top_audio_player_enabled
  {
    display: block !important;
    opacity: 0;
    float: right;
  }

  .top_audio_player .top_audio_player_btn
  {
    padding-top: 16px;
  }

  .top_audio_player .top_audio_player_play
  {
    margin-top: 12px;
  }

