  {
    background: url("data:image/svg+xml;utf8,<svg fill='rgba(255, 255, 255, 1)' height='24' x='0' y='0' viewBox='0 0 24 24' width='24' xmlns='http://www.w3.org/2000/svg'><path d='M7,6L7,6c0.55,0,1,0.45,1,1v10c0,0.55-0.45,1-1,1h0c-0.55,0-1-0.45-1-1V7C6,6.45,6.45,6,7,6z M10.66,12.82l5.77,4.07c0.66,0.47,1.58-0.01,1.58-0.82V7.93c0-0.81-0.91-1.28-1.58-0.82l-5.77,4.07C10.09,11.58,10.09,12.42,10.66,12.82z'/></svg>") center center no-repeat;
  }

  .top_audio_player .top_audio_player_next .top_audio_player_btn_icon
  {
    background: url("data:image/svg+xml;utf8,<svg fill='rgba(255, 255, 255, 1)' height='24' x='0' y='0' viewBox='0 0 24 24' width='24' xmlns='http://www.w3.org/2000/svg'><path d='M7.58,16.89l5.77-4.07c0.56-0.4,0.56-1.24,0-1.63L7.58,7.11C6.91,6.65,6,7.12,6,7.93v8.14C6,16.88,6.91,17.35,7.58,16.89zM16,7v10c0,0.55,0.45,1,1,1h0c0.55,0,1-0.45,1-1V7c0-0.55-0.45-1-1-1h0C16.45,6,16,6.45,16,7z'/></svg>") center center no-repeat;
  }

  .top_audio_player .top_audio_player_play .top_audio_player_btn_icon
  {
    background: url("data:image/svg+xml;utf8,<svg fill='rgba(255, 255, 255, 1)' height='28' x='0' y='0' viewBox='0 0 24 24' width='28' xmlns='http://www.w3.org/2000/svg'><path d='M8,6.82v10.36c0,0.79,0.87,1.27,1.54,0.84l8.14-5.18c0.62-0.39,0.62-1.29,0-1.69L9.54,5.98C8.87,5.55,8,6.03,8,6.82z'/></svg>") center center no-repeat;
  }

  .top_audio_player.top_audio_player_playing .top_audio_player_play .top_audio_player_btn_icon
  {
    background: url("data:image/svg+xml;utf8,<svg fill='rgba(255, 255, 255, 1)' height='28' x='0' y='0' viewBox='0 0 24 24' width='28' xmlns='http://www.w3.org/2000/svg'><path d='M8,19L8,19c1.1,0,2-0.9,2-2V7c0-1.1-0.9-2-2-2h0C6.9,5,6,5.9,6,7v10C6,18.1,6.9,19,8,19z M14,7v10c0,1.1,0.9,2,2,2h0c1.1,0,2-0.9,2-2V7c0-1.1-0.9-2-2-2h0C14.9,5,14,5.9,14,7z'/></svg>") center center no-repeat;
  }

  .top_audio_player .top_audio_player_prev
  {
    display: none;
  }

  .top_audio_player .top_audio_player_play
