    display: block;
  }

  .top_audio_player:hover .top_audio_player_play
  {
    margin-left: 0;
  }

  .top_audio_play
  {
    position: absolute;
    height: 56px;
    right: 5px;
  }

  .top_nav_btn.top_audio_player_enabled ~ .top_audio_play
