    display: none;
  }

  .top_audio_play__button
  {
    border-radius: 20px;
    height: 22px;
    background: var(--main-color);
    top: 17px;
  }

  .top_audio_play__button:before
  {
    display: none;
  }

  .top_audio_play__button:after
  {
    top: 6px;
    background: url("data:image/svg+xml;utf8,<svg fill='rgba(255, 255, 255, 1)' height='22' x='0' y='0' viewBox='0 0 24 24' width='22' xmlns='http://www.w3.org/2000/svg'><path d='M8,6.82v10.36c0,0.79,0.87,1.27,1.54,0.84l8.14-5.18c0.62-0.39,0.62-1.29,0-1.69L9.54,5.98C8.87,5.55,8,6.03,8,6.82z'/></svg>") center center no-repeat;
    background-size: 16px;
  }

